--
-- Table structure for table `npl_posts`
--

DROP TABLE IF EXISTS `npl_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `npl_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` text NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=235 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `npl_posts`
--

LOCK TABLES `npl_posts` WRITE;
/*!40000 ALTER TABLE `npl_posts` DISABLE KEYS */;
INSERT INTO `npl_posts` VALUES (3,1,'2010-07-04 19:50:15','0000-00-00 00:00:00','','Automatisk kladd','','auto-draft','open','open','','','','','2010-07-04 19:50:15','0000-00-00 00:00:00','',0,'http://www.aas-sw.no/npl3/?p=3',0,'post','',0),(5,1,'2007-05-15 12:35:45','2007-05-15 10:35:45','Nå er tiden der, 4. juni realiseres min 70-årsgave, ”Norge på langs”. Alle inviteres til å følge meg på nettet. Daglige oppdateringer av ruten på <a href=\"/kart\">Google Map</a> vil til enhver tid å vise hvor jeg er.\n\nDenne ekspedisjonen er blott til lyst og glede og ingen rekorder skal settes. Sammen med min Golden retriever Najax tar jeg tiden til hjelp. En vakker plass, et godt fiskevann, et trivelig selskap; slikt blir det mer en gjerne en ekstra overnatting og en god stund av. Planlagt rute finner du på hjemmesiden og det blir jevnlige oppdateringer med bilder og tekst. Intensjonen er en god reportasje til inspirasjon for andre. Besøk meg på hjemmesiden og <a href=\"/gjestebok/\">si din mening</a>?\n<p style=\"text-align: center\" align=\"right\">&nbsp;</p>\n<p style=\"text-align: center\"><img src=\"http://farm1.static.flickr.com/201/507939398_1e04a3d22b.jpg\" title=\"Gunnar og Najax\" alt=\"Gunnar og Najax\" height=\"281\" width=\"450\" /></p>\n\n<h4>Litt om meg selv</h4>\nJeg, Gunnar Aas er født i Harstad 22. februar 1937. Siden 1963 har jeg bodd og arbeidet i Mo i Rana. Jeg er nå pensjonist og aktivt medlem av Rana Turistforening. Med en god helse og i full forståelse med min kone Sara går en drøm i oppfyllelse, ”Norge på langs”. To av mine sønner vil sammen med min venn Fred Moerman oppdatere og holde hjemmesiden ajour. I månedsskiftet august/september regner jeg med å komme til Umbukta like ved Mo i Rana. Først i juli 2008 starter jeg på siste del at turen til Nordkapp.','Velkommen','','publish','open','open','','velkommen','','','2007-05-15 12:35:45','2007-05-15 10:35:45','',0,'http://www.aas-sw.no/npl3/?page_id=5',0,'page','',0),(6,1,'2007-05-15 12:38:49','2007-05-15 10:38:49','First post to the new blog. How exciting.','Testing','','publish','open','open','','testing','','','2007-05-15 12:38:49','2007-05-15 10:38:49','',0,'http://luxor.domeneshop.no/www.aas-sw.no/wp/?p=3',0,'post','',0),(7,1,'2007-05-15 12:42:20','2007-05-15 10:42:20','Turen er en 70-årsgave fra meg selv til meg selv. Den er å se på som en gledesytring for god helse i et godt samfunn. Jeg vil, sammen med min Golden retriever Najax ta turen over to somrer. Planlagt start er fra Lindesnes fyr 1. juni 2007.\r\n\r\nDe beregnede dagsetapper forhøyes alltid til hele dager og hver søndag tar jeg fri.  Første året avsluttes ved Umbukta ved Mo i Rana i månedsskiftet august/september. Etter planen har jeg derfor 10 – 20 dager til rådighet kun til kos. Det være seg ved et flott fiskevann, bare en lat dag ved teltet eller et spesielt trivelig vertsskap som jeg gjerne blir en dag eller to ekstra hos.\r\n\r\nEtter hvert som turen går vil erfaringer, hendelser, bilder og kommenterer bli lagt til den enkelte rute i avvikende farge. Alt som skrives er mine subjektive meninger og må brukes deretter. Har du egne synspunkter kan du gjerne blogge og legge noen ord inn i gjesteboken. Selv holder jeg kontakt med hjemmesiden og oppdaterer ved hjelp av Nokia N80 IE med tråløst tastatur SU-8W.\r\n\r\n<a href=\"http://falleri.no/ruta/etappe-1/\">Etappe 1:    Lindesnes – Ljosland</a> 4/6 – 8/6\r\nLengde: 121 km + 5 %    =  127 km/22 = 5+1 dager\r\n\r\n<a href=\"etappe-2\">Etappe 2:    Ljosland – Haukeliseter</a> 9/6 – 20/6\r\nLengde: 174 km + 10 %    =  191 km/22 =    10+2 dager\r\n\r\n<a href=\"etappe-3\">Etappe 3:    Haukeliseter – Finse</a> 21/6 – 27/6\r\nLengde: 118 km + 10 %    =  130 km/22 =    6+1 dager\r\n\r\n<a href=\"etappe-4\">Etappe 4:    Finse – Tyinkrysset</a>        28/6 – 3/7\r\nLengde: 97 km + 10 %        =  107 km/22 =    5+1 dager\r\n\r\n<a href=\"etappe-5\">Etappe 5:    Tyinkrysset – Lalm</a> 4/7 – 10/7\r\nLengde: 114 km + 10 %    =  125 km/22 =    6+1 dager\r\n\r\n<a href=\"etappe-6\">Etappe 6:    Lalm – Folldal</a> 11/7 – 14/7\r\nLengde: 63,5 km + 10 %    =  70 km/22 =    4 dager\r\n\r\n<a href=\"etappe-7\">Etappe 7:    Folldal – Røros</a>            16/7 – 21-22/7\r\nLengde: 118 km + 10 %    =  130 km/22 =    6+1 dager\r\n\r\n<a href=\"etappe-8\">Etappe 8:    Røros–Teveltunet</a>        23/7 – 2/8\r\nLengde: 127 km + 10 %    =  140 km/22 =    9+2 dager\r\n\r\n<a href=\"etappe-9\">Etappe 9:     Teveltunet – Sandv./Nordli     </a>3/8 – 15/8\r\nLengde: 197 km + 15 %    =  227 km/22 = 11+2 dager\r\n\r\nI fjor gikk jeg fram til Sveet ved Veressjøen øst om Stiklestad. Kom dit 31. august.\r\n<strong> Fortsetter min ferd videre fra Sveet til Nordkapp den 19. juni 2008.</strong>\r\n\r\n<a href=\"etappe-9b\">Etappe 9b:   Sveet – Sandv./Nordli </a>22/6 – 30/6 - 2008\r\nLengde: 116 km + 15 %                 =  133 km/17 = 8+1 dager\r\n\r\n<a href=\"http://falleri.no/ruta/etappe-10\">Etappe 10:     Sandvika/Nordli – Røyrvik</a> 1/7 – 2/7\r\nLengde: 62 km + 10 %            =  68 km/34 = 2 dager\r\n\r\n<a href=\"etappe-11\">Etappe 11:  Røyrvik – Umbukta</a> 3/7 – 16/7\r\nLengde: 205 km + 15 %        =  237 km/22 = 11+3 dager\r\nBlir to til tre dager hjemme på Ytteren. Starter fra Umbukta 19/7\r\n\r\n<a href=\"etappe-12\">Etappe 12:  Umbukta – Sulitjelma</a><a href=\"etappe-12\"> </a>19/7 – 29/7\r\nLengde: 170 km + 13 %        =  192 km/21 = 9+1 dager\r\nPS!  Det er ikke lov med hund i Padjelanta nasjonalpark, Sverige. Bryter derfor ved Stødi\r\npå Saltfjellet  (Umbukta - Stødi: 92 km) og går E6 til     Megården. <a href=\"etappe-13b\">Se etappe 13b</a>\r\n\r\n<a href=\"etappe-13\">Etappe 13: Sulitjelma – Abisko</a> 31/7 – 14/8\r\nLengde: 216 km + 10 %        =  238 km/20 = 10+1 dager\r\nOpprinnelig planlagt trase. Tar annen vei til Abisko. <a href=\"etappe-13b\">Se etappe 13b</a>\r\n\r\n<a href=\"etappe-13b\">Etappe 13b:     E6 fra Stødi til Kobbvannet – Abisko</a>    25/7 – 10/8\r\nLengde: 364 km + 10 %        =  400 km/26 = 15+2\r\n\r\n<a href=\"etappe-14\">Etappe 14: Abisko – Kilpisjävre </a>11/8 – 21-23/8\r\nLengde: 207 km + 15 %        =  238 km/22 = 10+1+2\r\n\r\n<a href=\"etappe-15\">Etappe 15: Kilpisjävre – Maze – Skaidi</a>    25/8 – 9/9\r\nLengde: 268 km + 15 %        =  308 km/20 = 15+2\r\n\r\n<a href=\"etappe-16\">Etappe 16: Skaidi – Nordkapp</a> 11/9 – 16/9\r\nLengde: 140 km + 7,5 %        =  151 km/25 = 6 dager\r\n\r\nAvstand:  SVEET - NORDKAPP     =  1627 km\r\nAvstand:  LINDESNES FYR - NORDKAPP     =  2758 km','Ruta','','publish','open','open','','ruta','','','2010-07-04 21:59:09','2010-07-04 19:59:09','',0,'http://luxor.domeneshop.no/www.aas-sw.no/wp/?page_id=4',3,'page','',0),(9,1,'2007-05-18 15:01:08','2007-05-18 13:01:08','Da skulle vi snart være klar med en ny addresse for denne bloggen, <a href=\"http://folk.uio.no/vahansen/victor/vandrer.html\">syng med!</a>','falleri.no','','publish','open','open','','fallerino','','','2007-05-18 15:01:08','2007-05-18 13:01:08','',0,'http://npl.aas-sw.no/2007/05/18/fallerino/',0,'post','',0),(14,2,'2007-05-18 18:43:32','2007-05-18 16:43:32','<strong>Første etappe blir den store utfordringen, ikke minst for hunden Najax med mye vei og asfalt.</strong>\n\n<strong>Lindesnes – Ljosland 4/6 – 8/6</strong>\nLengde: 121 km + 5 %            =  128 km/22=    5+1 søndag\n\nEtappen fra <a href=\"http://www.lindesnesfyr.no/\">Lindesnes fyr</a> og fram til <a href=\"http://www.ljoslandinfo.no/Hoved/index.aspx\">Ljosland</a> i Åseral kommune går for det meste på riks- og fylkesveier. Denne etappen med nærmere 13 mil på asfalt ser jeg som den store utfordringen, både for egne føtter og ikke minst labbene til min hund Najax. Jeg har prøvd ut flere typer sko og har stoppet med et par av merket MBT, høy modell fra <a href=\"http://www.mbt-info.com/\">Swiss Massai</a>. Disse skoene tar imot og absorberer støtene fra asfalten på en bedre måte enn noen andre jeg har prøvd. Det blir spennende å se om de svarer til forventningene. Proviantering gjøres underveis og nettene tilbringes i telt. Nallo 2 GT fra <a href=\"http://www.hilleberg.se/EUROPE.HTM\">Hilleberg</a> blir perfekt både for trivsel og komfort. Orienteringen er enkel og det holder med veikart for Sør-Norge Vest, målestokk 1:400 000. Ruta blir slik:\n<ul>\n	<li>Rv 460  Fra Lindesnes forbi Vigeland og Konsmo til Håland, 81 km</li>\n	<li>Rv   42  fra Håland til Svindal, 5 km</li>\n	<li>Rv 455 fra Svindal til Åseral, 19 km</li>\n	<li>Fv 352  fra Åseral fram til Ljosland, 23 km</li>\n</ul>\nDepot for neste etappe avtales og sendes til <a href=\"http://www.fjellstova.com\">Ljosland Fjellstove</a> Tlf. 382 85 700.\n\n<strong>Note</strong>: NPLeren Ragnar Fjell opplyser på sin <a href=\"http://www.breogfjellsport.no/npl/\">hjemmeside</a>, om butikker på følgende steder langs ruten: Spangereid, Vigeland, Konsmo, Dyremo og Kyrkjebygdi. Det er derfor ikke nødvendig med mye proviant i sekken.','Etappe 1','','publish','open','open','','etappe-1','','','2007-05-18 18:43:32','2007-05-18 16:43:32','',7,'http://falleri.no/ruta/etappe-1/',0,'page','',0),(17,2,'2007-05-19 00:37:41','2007-05-18 22:37:41','Dette er gjesteboka for “Den glade vandrer\". Her kan du legge igjen en liten hilsning eller hva du ellers måtte ha på hjertet. Underveis på turen vil vi med jamne mellomrom lesa gjesteboka og vi setter stor pris på alle som skriv seg inn.\r\n\r\nHilsen fra Gunnar og Najax','Gjestebok','','publish','open','closed','','gjestebok','','','2010-07-19 10:37:16','2010-07-19 08:37:16','',0,'http://falleri.no/gjestebok/',8,'page','',84),(18,1,'2007-05-19 01:25:18','2007-05-18 23:25:18','Ja, <a href=\"http://www.flickr.com/photos/klareralt/364988960/\">slik</a> kan det vel også se ut ved fyret.  Håper du blir heldigere med været når turen starter.','Lindesnes i storm','','publish','open','open','','lindesnes-i-storm','','','2007-05-19 01:25:18','2007-05-18 23:25:18','',0,'http://falleri.no/2007/05/19/lindesnes-i-storm/',0,'post','',0),(19,1,'2007-05-19 01:36:04','2007-05-18 23:36:04','Jeg vil gjerne takke mine sponsorer som har vært så vennlig å utstyre meg for turen.\r\n\r\n<img src=\"http://falleri.no/wp-content/uploads/2007/05/hilleberg-300x54.jpg\" alt=\"\" title=\"Hilleberg\" width=\"270\" height=\"49\" class=\"alignnone size-medium wp-image-36\" />\r\nHvorfor valgte jeg teltet Nallo 2 GT fra <a href=\"http://www.hilleberg.se/EUROPE.HTM\">Hilleberg</a>?  Jeg falt rett å slett for løsningen med forteltet. Når dertil Hilleberg er kjent for gode produkter var valget enkelt. Jeg ser fram til å få brukt teltet på min tur \"Norge på langs\".\r\n\r\n<img src=\"http://falleri.no/wp-content/uploads/2007/05/logo_mbt-300x103.jpg\" alt=\"\" title=\"MTB Logo\" width=\"270\" height=\"93\" class=\"alignnone size-medium wp-image-38\" />\r\n\"Norge på langs\" går til dels på vei og asfalt. Er det noe som røyner på må det være mil etter mil med asfalt. Her var det at MBT-skoen fra <a href=\"http://www.swissmasai.no/\">Masai Norge AS</a> kom som dalende fra himmelen. Aldri har jeg hatt noe bedre på på hardt og slett underlag. Vent på min rapport og vurdering.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/08/klattermusen.png\" alt=\"Klättermusen\" />\r\nPå min vei mot Nordkapp bruker jeg i år en del utstyr fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a> - Blant annet:  Mjölner, en helt nyutvikklede ryggsekk  - Tivar dunsovepose  - Liv dunjakke  - Mithril bukse. Testen av dette utstyret vil bli en interresant erfaring å ta med seg.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/wwwarctic-circleno-150x150.jpg\" alt=\"Arctic Circle Logo\" height=\"115\" width=\"115\" />\r\nVårt lokale turistkontor, <a href=\"http://www.arctic-circle.no/\">Turistkontoret Mo i Rana</a> og Polarsirkelen Reiseliv er unikt interresert i friluftsliv og har skaffet meg alle kartene jeg trenger til en meget gunstig pris.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/rana-jakt-og-villmark.png\" alt=\"Rana Jakt og Villmark Logo\" height=\"104\" width=\"121\" />\r\nDet er mangt som skal til for en vellykket \"Norge på langs\". Listen med utstyr, både av  smått og stort ble lang. Hadde ikke <a href=\"http://www.jaktogvillmark.no/\">Rana Jakt og Villmark AS</a> det jeg trengte, skaffet de det eller fant et godt alternativ. Servicen var topp og i tillegg ga de meg ekstra gode priser.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/stensland.png\" alt=\"Specsavers Stensland\"/>\r\nVed et tilfeldig besøk hos <a href=\"http://old.monet.no/viggo/interoptik/index2.php?link=visjon\">Specsavers Stensland</a> ble jeg anbefalt å bruke månedslinser på min videre ferd mot Nordkapp. Jeg syntes ideen var god og etter en en nitidig og grundig tilpassing er jeg nå på vei med linsene på og brillene i sekken. Ingen problemer videre med nedduggede briller.','Takk til sponsorer','','publish','open','open','','takk','','','2010-07-19 10:24:41','2010-07-19 08:24:41','',0,'http://falleri.no/takk/',99,'page','',0),(218,2,'2010-07-19 10:09:20','2010-07-19 08:09:20','Jeg vil gjerne takke mine sponsorer som har vært så vennlig å utstyre meg for turen.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/hilleberg-150x150.jpg\" alt=\"Hilleberg\" />\r\nHvorfor valgte jeg teltet Nallo 2 GT fra <a href=\"http://www.hilleberg.se/EUROPE.HTM\">Hilleberg</a>?  Jeg falt rett å slett for løsningen med forteltet. Når dertil Hilleberg er kjent for gode produkter var valget enkelt. Jeg ser fram til å få brukt teltet på min tur \"Norge på langs\".\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/logo_mbt-150x150.jpg\" alt=\"MTB Logo\" />\r\n\"Norge på langs\" går til dels på vei og asfalt. Er det noe som røyner på må det være mil etter mil med asfalt. Her var det at MBT-skoen fra <a href=\"http://www.swissmasai.no/\">Masai Norge AS</a> kom som dalende fra himmelen. Aldri har jeg hatt noe bedre på på hardt og slett underlag. Vent på min rapport og vurdering.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/08/klattermusen.png\" alt=\"Klättermusen\" />\r\nPå min vei mot Nordkapp bruker jeg i år en del utstyr fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a> - Blant annet:  Mjölner, en helt nyutvikklede ryggsekk  - Tivar dunsovepose  - Liv dunjakke  - Mithril bukse. Testen av dette utstyret vil bli en interresant erfaring å ta med seg.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/wwwarctic-circleno-150x150.jpg\" alt=\"Arctic Circle Logo\" height=\"115\" width=\"115\" />\r\nVårt lokale turistkontor, <a href=\"http://www.arctic-circle.no/\">Turistkontoret Mo i Rana</a> og Polarsirkelen Reiseliv er unikt interresert i friluftsliv og har skaffet meg alle kartene jeg trenger til en meget gunstig pris.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/rana-jakt-og-villmark-150x150.png\" alt=\"Rana Jakt og Villmark Logo\" height=\"104\" width=\"121\" />\r\nDet er mangt som skal til for en vellykket \"Norge på langs\". Listen med utstyr, både av  smått og stort ble lang. Hadde ikke <a href=\"http://www.jaktogvillmark.no/\">Rana Jakt og Villmark AS</a> det jeg trengte, skaffet de det eller fant et godt alternativ. Servicen var topp og i tillegg ga de meg ekstra gode priser.\r\n\r\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/stensland.png\" alt=\"Specsavers Stensland\" height=\"72\" width=\"130\" />\r\nVed et tilfeldig besøk hos <a href=\"http://old.monet.no/viggo/interoptik/index2.php?link=visjon\">Specsavers Stensland</a> ble jeg anbefalt å bruke månedslinser på min videre ferd mot Nordkapp. Jeg syntes ideen var god og etter en en nitidig og grundig tilpassing er jeg nå på vei med linsene på og brillene i sekken. Ingen problemer videre med nedduggede briller.','Takk til sponsorer','','inherit','open','open','','19-revision-4','','','2010-07-19 10:09:20','2010-07-19 08:09:20','',19,'http://falleri.no/2010/07/19/19-revision-4/',0,'revision','',0),(20,1,'2007-05-19 10:33:59','2007-05-19 08:33:59','<strong>På etappen forlates vei og asfalt og jeg følger DNTs løypenett. I tilfelle snø i høyden vil truger bli brukt.</strong>\n\n<strong>Ljosland – Haukeliseter 9/6 – 20/6</strong>\nLengde: 174 km + 10 %        =  191 km/22 =    10+2 søndager\n\nFra Ljosland går turen inn i Setesdals- og Ryfylkeheiene. Jeg har skiftet fra mine <a href=\"http://www.mbt-info.com/\">MBT-sko</a> til Jörnkängan fjellsko. Sekk og kløv er blitt tyngre, men ingen etapper vil kreve mer enn 18 kg i sekk og 15 kg i kløv. Fra nå av begynner det virkelig å røyne på for Najax. Kløven er tung og terrenget tøffere. <a href=\"http://www.baltoshop.no\">Spesialforet</a> jeg har tatt med, <a href=\"http://www.baltoshop.no/balto/balto_index.php\">Balto Competition</a> skal fra nå av bevise sin verdi i praksis og gi Najax den energien han trenger. Ruta legges innom <a href=\"http://www.kot.no/\">KOT</a> og <a href=\"http://www.turistforeningen.no/stavanger\">STF</a> sitt nett av turisthytter og følger de merkede turstiene. Nødvendigvis blir det ikke overnatting i hus hver natt, men høyt til fjells og ennå tidlig på året kan dårlig vær inntreffe og da er det betryggende å vite at gode hytter ligger innen rimelig nærhet. Den utstukne ruten går forbi disse turisthyttene:  Gaukhei, Øyvusbu, Svartenut, Bossbu, Kringlevatn, Hovatn, Vassdalstjørn, Krossvatn, Mostøl, Bleskestamoen, Holmevasshytta og til <a href=\"http://www.haukeliseter.no/\">Haukeliseter.</a>\n\n<strong>Depot</strong>\nNødvendig proviant og utstyr for etappen sendes etter avtale til <a href=\"http://www.fjellstova.com/\">Ljosland Fjellstove A.s,</a> Ljosland, 4540 Åseral, Tlf. 382 85 700. - Regner med å supplere med noe middagsmat fra de selvbetjente hyttene. I tillegg får jeg se om fisken begynner å bite.\n\n<strong>Kart</strong>\nFra nå av vil topografiske kart i målestokk 1:50 000 fortelle hvor jeg er og hvor jeg skal. Suunto speilkompass sammen med Garmin GPSMAP 60csx sikrer navigeringen.\n\n<strong>Kart for etappen</strong>\n1412 IV     Kvifjorden\n1413 IV    Botsvatn\n1413 III     Rosskreppfjorden\n1313 I            Blåsjø\n1314 II          Suldalsvatnet\n1414 III    Breive\n1414 IV    Haukeliseteter\n\n<strong>Valgte ruter</strong>\nJeg vil i det alt vesentlige følge rutenettet og beskrivelsen i siste utgave av <a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\">”Til fots i Norge”</a> utgitt av Den Norske Turistforening. De nevnte rutenummer refererer til Setesdalsheiane i TFIN.\n<ol>\n	<li> Ljosland 530 moh til <a href=\"http://home.online.no/~hasto/natur/Gaukhei.htm\">Gaukhei th</a> 840 moh. 18-20 km  og rundt 5 timers gange. Rute nr. 1/1. TFIN oppgir godt fiske i Gaukheivatnet og Søtevatnet, Fram til Pytten går jeg uten papirkart.</li>\n	<li> Gaukhei til Øyvassbu th 931 moh. Ca. 17 km og en 7 timers gange. Rute nr. 1/10.</li>\n	<li> Øyvassbu til Bossbu th 1020 moh. En forholdsvis lang etappe på 26 km, men skal kunne gåes på 8-9 timer. Om nødvendig tar jeg en natt i telt eller på Svartenut th. Rute nr. 1/14 og 1/15.</li>\n	<li> Bossbu til Kringlevatn th 940 moh. Beregnet til 19 km og anslås i TFN til en 8 timers tur.  Rute nr. 1/18. Rutebeskrivelsen er som følger: ”Bratt opp Heimre Skammestigen. Passerer det høyeste punktet på KOTs løypenett (ca. 1300 moh). Den gamle Skinnvegen passeres like før vadet ved Ljosåni nord for Såvatn. Elva kan være vanskelig å passere ved stor vannføring.” Dette kan bli en spennende strekning. Eg håper å finne en snebru over elva.</li>\n	<li> Kringlevatn forbi Storsteinen th 915 moh, til Hovatn th 1112 moh. Traseen er ikke beskrevet på rutenettet, men er beregnet til 24 km og 8-10 timers gange. Bortsett fra relativt stor høyde burde ikke strekket by på de helt store problemer.</li>\n	<li> Hovatn til Krossvatn th 1095 moh. 17 km i nokså kuppert terreng. Regner med å tilbakelegge traseen på noe slikt som 6-8 timer.</li>\n	<li> Krossvatn til Mostøl th 620 moh. 19  km, men burde være forholdsvis greit terreng. Ned Kyrkjesteindalen og til Mostøl kan jeg heller ikke se noen problemer. En tur på 8-9 timer.</li>\n	<li> Mostøl til Holmvasshytta 1130 moh.  Dette er det lengste strekket på hele 29 km. Sannsynligvis en dagsetappe på kanskje 10-11 timer. Alt etter værforholdene og eventuelt sne kan den vise seg å kreve en ekstra overnatting underveis.</li>\n	<li> Holmvasshytta til Haukeliseter 980 moh.  19 km og 7 timers gange. Nå skal det bli artigt å se om den planlagte ankomst 18. juni viser seg å holde.</li>\n</ol>','Etappe 2','','publish','open','open','','etappe-2','','','2007-05-19 10:33:59','2007-05-19 08:33:59','',7,'http://falleri.no/etappe-2/',0,'page','',0),(21,1,'2007-05-19 10:39:23','2007-05-19 08:39:23','<strong>Hardangervidda står for tur. Den kan ennå by på utfordringer med mye vær og overraskelser.</strong>\n\n<strong>Haukeliseter – Finse 21/6 – 27/6</strong>\nLengde: 118 km + 10 %        =  130 km/22 =     6+1 søndag\n\nNå går turen innover mot <a href=\"http://www.dirnat.no/hardangervidda/\">Hardangervidda</a><a href=\"http://www.dirnat.no/hardangervidda/\">.</a> Den planlagte ruta følger også her DNTs rutenett og legges i utgangspunktet om disse turisthyttene til <a href=\"http://www.dntoslo.no/\">DNT Oslo og Omegn</a>:  Hellevassbu, Litlos, Sandhaug, Trondsbu, Dyranut, Kjeldebu til Finsehytta. Ruta kan bli en annen dersom forholdene i fjellet tilsier det. De samme hensyn til sikkerhet som etappe 2 krever, vil også gjelde på denne etappen.\n\n<strong>Depot</strong>\nProviant og utstyr for etappen sendes etter avtale til <a href=\"http://www.haukeliseter.no/\">Haukeliseter Fjellstue</a>, 3895 Edland, tlf. 350 62 777\n\n<strong>Kart for etappen</strong>\n1414 IV    Haukeliseter\n1415 III    Hårteigen\n1415 II           Nordmannslågen\n1415 I              Bjoreio\n1416 II         Hardangerjøkulen\n\n<strong>Valgte ruter</strong>\n<a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\"> \"Til fots i Norge”</a> utgitt av Den Norske Turistforening er grunnlaget for denne etappen.\n<ol>\n	<li> Haukeliseter 980 moh til Hellevasbu th. 1170 moh. 20-22 km og 7 timers gange. Rute nr. 11/54. Langs denne traseen beskriver TFIN 4 sommerbroer. Det er for tidlig på året til at disse broene er lagt ut så jeg får se, blir det vading eller er jeg heldig å kan krysse på snøbruer. Bortsett fra dette skulle ruta være grei, men kan ta noe lengre tid enn beskrevet.</li>\n	<li> Hellevassbu til Litlos th. 1180 moh. 16-17 km og 5 t gangtid. Rute 11/53 går, iht. TFIN en del opp og ned. En rimelig dagsetappe skal det uansett være. To, sannsynlig ikke utlagte sommerbruer vil bli til vading.</li>\n	<li> Litlos til Sandhaug th. 1250 moh. 22-24 km og 7 timers gange. Rute 11/52 a+b. En rimelig lang dagsetappe. Om nødvendig kan jeg ta overnatting på Besso th, men for neste dags etappe vil det være en fordel å nå fram til Sandhaug. Såframt ikke sne gir problemer burde dette være en grei etappe, men noe vading blir det nok.</li>\n	<li> Sandhaug til Dyranut 1250 moh. 24-26 km og 8 timers gange. Etappen er lang og det er derfor mulig at det blir en natt på Tronsbu etter bare 15 km.</li>\n	<li> Dyranut til Kjeldebu th. 1100 moh. 9 km og 3 timer en kort dagsetappe. Jeg går sannsynligvis noe lengere og slår opp teltet for overnatting.</li>\n	<li> Siste dag før <a href=\"http://www.rallarmuseet.no/\">Finsehytta</a> 1220 moh. Blir starten fra Kjeldebu er denne dagsetappen på rundt 25 km. Det er vel ikke usannsynlig at en del av turen blir på snø og at dagen kan bli lang. Forrige natt, forhåpentligvis i telt har nok gjort turen kortere enn helt fra Kjeldebu.</li>\n</ol>','Etappe 3','','publish','open','open','','etappe-3','','','2007-05-19 10:39:23','2007-05-19 08:39:23','',7,'http://falleri.no/ruta/etappe-3/',0,'page','',0),(22,1,'2007-05-19 11:00:36','2007-05-19 09:00:36','<strong>Stadig høyt til fjells, men selv om jeg ennå må regne med å gå på noe snø er det full sommer i fjellet</strong>\n\n<strong>Finse – Tyinkrysset        28/6 – 3/7</strong>\nLengde: 97 km + 10 %            =  107 km/22 =     5+1 søndag\n\nSiste del av Hardangervidda og inn i <a href=\"http://www.etojm.com/dir/skarveheimen.html\">Skarvheimen</a>. Først når dette strekket er tilbakelagt regner jeg med å kunne sende trugene hjem. Fortsatt følger jeg DNTs rutenett  og turen går om om <a href=\"http://www.dntoslo.no/\">DNT Oslo og Omengs</a> hytter: Geitrygghytta, Kongshelleren, Iungdalshytta, Bjordalsbu, Breistølen Turisthytte (privat), Sulebu og til Tyinkrysset.\n\n<strong>Depot</strong>\nProviant og utstyr for etappen sendes  til Finse Post i butikk, Finse Jernbanestasjon, 5719 Finse, tlf. 565 27 100.\n\n<strong>Kart for etappe</strong><strong>n</strong>\n1416 II          Hardangerjøkulen\n1416 I             Aurlandsdalen\n1516 IV    Djup\n1517 III    Borgund\n1517 II           Øye\n\n<strong>Valgte ruter</strong>\n<a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\"> ”Til fots i Norge”</a> utgitt av Den Norske Turistforening er grunnlaget for denne etappen.\n<ol>\n	<li> Fra Finsehytta 1222 moh til Geitrygghytta  1229 moh. 15 km og 5 timers gange. Rute nr. 12/3. En rimelig dagsetappe selv om noe av turen må gjøres med trugger på snø.</li>\n	<li> Geitrygghytta til Kongshelleren th. 1450 moh. 13 km og 4 timers gange, Rute nr 12/12. En forholdsvis kort etappe og ikke usansynlig jeg går forbi Kongshelleren og telter på vei mot Iungdalshytta. Rute nr. 12/19.</li>\n	<li>Teltet pakkes og Iungdalshutta 1110 moh. passeres. Går rute nr. 12/22 forbi Bjordalsbu 1580 moh. og fortsetter rute nr. 12/26 til Breistølen Fjellstue 1025 moh. En ambøsiøs dagsetappe med forholdsvis stor stigning. Anslått til 9 timer.</li>\n	<li>Fra Breistølen Turisthytte beregner jeg to dagsetapper fram til <a href=\"http://www.visitfilefjell.no/sommer/spise.htm\">Tyinkrysset</a>. Enten overnatter jeg på Sulebu 1330 moh. eller tar en natt i teltet.</li>\n</ol>','Etappe 4','','publish','open','open','','etappe-4','','','2007-05-19 11:00:36','2007-05-19 09:00:36','',7,'http://falleri.no/ruta/etappe-4/',0,'page','',0),(23,1,'2007-05-19 11:02:18','2007-05-19 09:02:18','<strong>Den tøffeste delen av turen er tilbakelagt. Fra nå går det mot varmere og late dager i Jotunheimen.</strong>\n\n<strong>Tyinkrysset – Lalm 4/7 – 10/7</strong>\nLengde: 114 km + 10 %        =  125 km/22 =    6+1 dager\n\nFare for vinter i <a href=\"http://www.visitjotunheimen.com/\">Jotunheimens</a> fjell er definitivt tilbakelagt. Fra nå av ser jeg for meg varme og late dager. At været kan veksle er en selvfølge, men det er en del av sjarmen. Fortsatt følger jeg DNTs løypenett. Dog vil stadig flere kvelder og netter bli ved og i teltet. Følgende DNT hytter regner jeg med å passere:  Fondsbu, Gjendebu, Memurubu Turisthytte, Gjendesheim og Hindseter. Memurubu og Hindseter er private de øvrige drives av <a href=\"http://www.dntoslo.no/\">DNT Oslo og Omegn.</a>\n\n<strong>Depot</strong>\nProviant og utstyr for etappen sendes etter avtale til Filefjell Resort/Tyinkrysset fjellstove, 2985 Tyinkrysset, tlf. 613 67 800.\n\n<strong>Kart for etappen</strong>\n1517 II        Øye\n1517 I          Tyin\n1617 IV    Gjende\n1618 III    Glittertinden\n1618 II         Sjodalen\n1718 IV    Otta\n\n<strong>Valgte ruter</strong>\n<a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\"> ”Til fots i Norge”</a> utgitt av Den Norske Turistforening er grunnlaget for denne etappen.\n<ol>\n	<li> Fra <a href=\"http://www.visitfilefjell.no/sommer/spise.htm\">Tyinkrysset</a> går turen til Fondsbu 1065 moh. 24 km og en god dagsmasj på riksvei 252.</li>\n	<li> Fondsbu til Gjendebu 990 moh. 15 km og 5 timer, en fin dagsmasj i rimelig lettgått terreng. Rute nr. 16/12.</li>\n	<li> Gjendebu til Gjendesheim 994 moh. 22 km og og en skikkelig lang dagsmars. Fram til Memurubu mye tøft terreng, men deretter en god gammel stølsvei. Rute nr. 16/6 og 16/1.</li>\n	<li> Gjendesheim til Hindseter 880 moh. 23 km og en dagsmasj. Fra Gjendesheim tar jeg veien forbi Bessvatnet og ned til Russvasbuene og deretter til riksvei 51 for å gå denne til Hindseter. Det blir sannsynligvis telting underveis.</li>\n	<li> Fra Hindseter blir det vei og Sjodalen ned til Randsverk hvor jeg går riksvei 257 til Leirdalen. Herfra går det over fjellet til jeg kommer inn på vei igjen ved Rinddalen og går ned til <a href=\"http://www.lalm.no/\">Lalm</a> 400 moh. rett sør for Vågå.  Detter er en lang etappe på hele 40-42 km og krever 2-3 dager. Overnatting vil bli i telt og muligens en seterbu inne på fjellet. En god del av turen går på veier.</li>\n</ol>','Etappe 5','','publish','open','open','','etappe-5','','','2007-05-19 11:02:18','2007-05-19 09:02:18','',7,'http://falleri.no/ruta/etappe-5/',0,'page','',0),(24,1,'2007-05-19 11:03:15','2007-05-19 09:03:15','<strong>Rondane og Alvdal Vestfjell står for tur. Full sommer og min firbente venn kommer til å få bryne seg</strong>\n\n<strong>Lalm – Folldal            11/7 – 14-15/7</strong>\nLengde: 63,5 km + 10 %        =  70 km/22 =    4+1 dager\n\nFra <a href=\"http://www.lalm.no/\">Lalm</a> går det bratt opp fjellet før det flater ut til nedstigningen til <a href=\"http://www.jorundgard.no/\">Sel</a>. Deretter nye stigninger oppover og inn mot Høvringen og <a href=\"http://www.dirnat.no/rondane/\">Rondane Nasjonalpark</a>. En tøff etappe med mye opp og ned i forhåpentligvis god sommervarme. Fra Høvringen er det nå mer eller mindre slette fjellet fram til Folldal.\n\n<strong>Depot</strong>\nProviant og utstyr for etappen sendes etter avtale til: Lalm Post i butikk, Coop Marked Lalm, 2682 Lalm\n\n<strong>Kart for etappen</strong>\n1718 IV    Otta\n1519 III    Hjerkinn\n1519 II            Folldal\n\n<strong>Valgte ruter</strong>\nDen første delen av etappen går i et ”hvit område”.  Fra Høvringen til Folldal kommer nok en gang <a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\">”Til fots i Norge”</a> utgitt av Den Norske Turistforening til sin rett. Turistforeningshyttene her er eid av <a href=\"http://www.dntoslo.no/\">DNT Oslo og Omegn.</a>\n<ol>\n	<li> Fra Lalm til Høvringen er det rundt 20-22 km. Både på grunn av terrenget og sommervarmen regner jeg med en overnatting underveis. Fra Lalm på 360 moh.  går det opp til vel 800 moh. for så å falle til 300 moh. ved Sel. Deretter er det oppover igjen til Høvringen på 940 moh.</li>\n	<li> Høvringen til Grimsdalshytta 1000 moh. En skikkelig daggsmars på rundt 28 km og 8 t etter TFIN. Jeg tar ruta over Sletthøe på 1576 moh. og regner med at de 8 timene kan bli noe knepet.</li>\n	<li><a href=\"http://www.sletner.nu/dovre/grimsdalen.htm\"> Grimsdalshytta</a> til Folldal 720 moh. 23 km og en fin dagsetappe og mulig både med vei og sti. Jeg går stien til Tverrlisætre og deretter om Mesætre og forbi Grimsdalsgruva for lenger ned i lia å følge veien til <a href=\"http://www.folldalturlag.no/\">Folldal.</a></li>\n</ol>','Etappe 6','','publish','open','open','','etappe-6','','','2007-05-19 11:03:15','2007-05-19 09:03:15','',7,'http://falleri.no/etappe-6/',0,'page','',0),(25,1,'2007-05-19 11:04:38','2007-05-19 09:04:38','<strong>Folldal til Røros er en etappe helt utenfor det merkede rutenettet til Den Norske Turistforening</strong>\n\n<strong>Folldal – Røros 16/7 – 21-22/7</strong>\nLengde: 118 km + 10 %        =  130 km/22 =    6+1 dager\n\nFra <a href=\"http://home.online.no/~steivang/folldalno.htm\">Folldal</a> legger jeg veien over fjellet og følger sti mot Lykkjesætre. Derfra går jeg til Stugulisætra og krysser Einunna 500 m lenger ned. Videre går turen til Savalen og om Lonåsmoen til Vingelen. Herfra er det ikke mye igjen av etappen og turen går på vei ned til  Dalsbygda og fortsetter forbi Sætersjøen, Galåa og til <a href=\"http://www.verdensarvenroros.no/\">Røros.</a>\n\n<strong>Depot</strong>\nProviant og utstyr for etappen sendes til:  Post i butikk, Spar Folldal , 2580 Folldal, tlf. 624 90 174.\n\n<strong>Kart for etappe</strong><strong>n</strong>\n1519  II         Folldal\n1619 IV    Kvikneskogen\n1619 I           Tynset\n1620 II         Forollhogna\n1720 III    Røros\n\n<strong>Valgte ruter</strong>\nHele etappen går i et ”hvit område” og ingen merkede stier av DNT. Skal jeg overnatte under tak må dette skje etter avtale med private sætrer eller andre lagshytter. Telting blir nok førstevalget.\n<ol>\n	<li> Folldal 720 moh. til Stugulisætre 880 moh. 22 km er en passende dagsetappe. Turen går for det meste oppe i snaufjellet og på det høyeste opptil 1250 moh. Litt opp og ned, men egentlig et ganske greit terreng.</li>\n	<li> Stugulisætre til Savalen 720 moh. 28 km. En god dagsetappe, men på gode stier og bygdeveier skulle veien være grei nok.</li>\n	<li> Savalen til Skardstjønna 836 moh. er det også 28 km og ennå 49 km igjen til Røros 660 moh. Tar nok turen i tre dagsetapper på omkring 25-26 km. Da alle dagsetappene har vært rimelig lange er det sannsynlig med en stille liggedag ved et godt fiskevann.</li>\n</ol>','Etappe 7','','publish','open','open','','etappe-7','','','2007-05-19 11:04:38','2007-05-19 09:04:38','',7,'http://falleri.no/ruta/etappe-7/',0,'page','',0),(26,1,'2007-05-19 11:07:47','2007-05-19 09:07:47','<strong>Verdensarven Røros og videre innover i <a href=\"http://www.falkberget.no/\">Falkbergets</a> rike med Sylane som desserten</strong>\n\n<strong>Røros–Teveltunet 23/7 – 2/8</strong>\nLengde: 127 km + 10 %        =  140 km/22 = 10+1 dager\n\nNoe nord for <a href=\"http://www.verdensarvenroros.no/\">Røros</a>, fra Jensåsvollan er det tilbake på Turistforeningens rute- og hyttenett. Nå går jeg inn i Sylane og <a href=\"http://www.tt.no/\">Trodhjems Turistforenings</a> område. DNTs <a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\">\"Til fjells i Norge\"</a> er igjen et godt grunnlag. Denne etappen er den første med planlagt følge. Min kjære datter, Camilla kommer fra Oslo og blir med. Det blir mest sansnlig noe kortere dagsetapper enn tidligere og Camilla blir med til Væktarstua og tar buss ned til Stjørdal. Derfra går jeg videre i grensefjellene og inn til Teveltunet.\n\n<strong>Depot</strong>\nProviant og utstyr for etappen sendes til:  Røros Postkontor, Tollef Bredalsv. 7, 7374 Røros.\n\n<strong>Kart for etappen</strong>\n1720 III    Røros\n1720 I   Stuggusjøen\n1721 II          Esandsjøen\n1721 I            Meråker\n\n<strong>Valgte ruter</strong>\nCamilla er nå kommet med og vi to, sammen med Najax satser på noen fine dager med en blanding av telt og TTs hytter. Camilla er, som hun selv sier et storbymenneske og det blir derfor forholdsvis korte dagsetapper. Den opprinnelige tidsplanen blir å forlenge med en par dager.\n<ol>\n	<li> Fra Røros 660 moh. til vestenden av Aursunden 691 moh. er det 17 km. Dette kan være en passende første dag og vi finner oss teltplass ved vannet. Forhåpentligvis ikke for mye nedtappet (6 m reguleringshøyde).</li>\n	<li> Aursunden er lang og det blir å gå 13-14 km på vei til Jamtvollen. Herfra tar vi oss et stykke oppover Jamtbekken før vi tar kveld og telter.</li>\n	<li> Denne dagen går vi forbi Klinkengruva og fortsetter til Fjellsjøtjønnan 960 moh., en kort dagsetappe på 13 km. Her oppe på fjellet slår vi leir og bruker ettermiddagen til fisking og plankegger nok en dag dag ved tjønnan og Fjellsjøen.</li>\n	<li> Det er nå 20 km igjen til <a href=\"http://www.vaektarstua.no/\">Væktarstua Tydal hotell</a> 612 moh. Etter to late dager får Camilla prøve seg på en god dagsetappe. På Væktarstua blir det overnatting med dusj og et annerledes måltid enn dagene hittil.</li>\n	<li> Væktarstua til Nerdalshytta 780 moh. betjent hytte 18 km og 5 timer. Rute 27/3.  Detter er den første av TTs hytter på min vei. Dagsetappen er kort, men jeg regner med overnatting på denne hytta.</li>\n	<li> Nerdalshytta til Storerikvollen th. 769 moh.  betjent hytte 24 km og 6 t. Rute nr. 27/1. Jeg tar det med ro og bruker dagen og prøver fiskebitet underveis.</li>\n	<li> Storerikvollen til Bjørneggen th. 590 moh. Den første av <a href=\"http://www.nt-tur.no/\">NTTs</a> hytter. 23 km 8 timer. Rute nr. 27/10</li>\n	<li> Bjørneggen til Tevettunet th. 446 moh er det bare 11 km anslått til 5 timer Rute nr. 27/8a. Får se hva dette betyr om det blir å ta det med ro på <a href=\"http://www.teveltunet.no/\">Teveltunet</a> eller om jeg tar litt lenger innover mot Storbekkfjellet og heller slår opp teltet. Depoet for neste etappe på Teveltunet.</li>\n</ol>','Etappe 8','','publish','open','open','','etappe-8','','','2007-05-19 11:07:47','2007-05-19 09:07:47','',7,'http://falleri.no/ruta/etappe-8/',0,'page','',0),(27,1,'2007-05-19 11:08:48','2007-05-19 09:08:48','<strong>Nå går det inn i Nord-Trøndelags grenseområder med en blanding av skog, fjell og vann\n</strong>\n\n<strong>Teveltunet – Sandv./Nordli 3/8 – 15/8</strong>\nLengde: 197 km + 15 %        =  227 km/22 = 11+2 dager\n\nEn lang etappe beregnet til 13 dager. I disse grenseområdene er det mye snaufjell, men også fjellgårder, rikt bevokste daler, elver og fiskevann. I det hele en meget vekslende natur.\n\n<strong>Depot</strong>\nProviant og utstyr for etappen deles i to og sendes etter avtale til:  <a href=\"http://www.teveltunet.no/\">Teveltunet Fjellstue.</a> 7530 Meråker, tlf. 748 12 500 og til <a href=\"http://www.nt-tur.no/index.php?fo_id=4355\">Sveet Gjestgiveri,</a> 7660 Vuku, tlf. 740 70 284.\n\n<strong>Kart for etappe</strong><strong>n</strong>\n1721 I        Meråker\n1722 II        Feren\n1822 III    Bellingen\n1822 IV     Vera\n1823 III    Snåsa\n1823 II        Gjevsjøen\n1923 IV    Nordli\n\n<strong>Valgte ruter</strong>\nSiste del med <a href=\"http://www.nt-tur.no/\">NTT</a>-merkede ruter  og hytter i Nord-Trøndelag. Deretter blir det mer blandet til det etter hvert blir dårligere med stier og oppgåtte ruter. Etappen legges om Angeltjønnhytta, Sulåmo/Ferslia, Sandvika, Sveet, opp Ståggådalen til <a href=\"http://www.indre-namdal.no/apps/otiin/internett/otiinbip.nsf/id/DA79BAABE3FC4A70C1257081003F359C!OpenDocument\">Gaundalen</a>, Holden, Gjevsjøen, forbi Seterlifjellet, vestom Finnhuva/Bieelnie, over Hhykkelfjellet, ned til Sørnes og veien fram til Nordli. En lang etappe med mye villmark og mange fine fiskevann.\n<ol>\n	<li> Teveltunet 446 moh. til Angeltjønnhytta 515 moh. 22 km 6 t. Rute nr. 28/1.  Ei grei merket rute og behagelig dagsetappe. Går for det meste i høyfjellet oppe på vel 800 moh.</li>\n	<li> Angeltjønnhytta til Ferslia th 470 moh. 16 km og 5 t. Rute 28/2. Dette blir en lat dag og en kort etappe. Får se om jeg tar meg litt lengere innover mot Sulsjøan. Det skal være en nymerket rute øst for disse.</li>\n	<li> Ferslia til Innstua/Sandvika 420 moh. 24 km 8t. Rute nr. 28/4. En god dagsetappe, men tilbakela sansynligvis noe av den dagen før.</li>\n	<li> Innistua til Sveet 360 moh. 20 km 6 t. Rute nr. 28/6. Jeg har valgt stien øst om Vergåa og opp til Breivatnet. Deretter følger jeg stien vestom Bustadtjønna og ned til Sveet. En grei dag.</li>\n	<li>Fra nå av hører mye av de områdene jeg kommer ti å vandre i til våre store Nasjonalparker. Den første av disse er <a href=\"http://www.dirnat.no/blafjella/\">Blåfjella-Sjækerfjella nasjonalpark</a>. I tillegg passerer jeg rett vest for <a href=\"http://www.dirnat.no/content.ap?thisId=3357\">Lierne nasjonalpark</a> når jeg nærmer meg Nordli. Turen fra Sveet til Gaundalen 476 moh. 36 km.  Her har jeg valgt å forlate hyttenettet og går innover mot Ståggådalen. To dagsetapper regner jeg med på turen. Gode fiskemuligheter underveis og jeg ser ikke bort i fra at jeg kan bli liggende en ekstra dag underveis</li>\n	<li> Fra Gaundalen til Gjevsjøen 468 moh. 25 km 10 t. Rute nr. 28/11 og 12. Detter er umerkede ruter, 10 timer høres litt vel mye ut. Er terrenget tyngre enn kartet tyder på får jeg eventuelt ta en overnatting på Holden.</li>\n	<li> Gjevsjøen til Nordli/Sandvika 410 moh. 66 km og 3 dagsetapper. Jeg legger ruta opp forbi Hestmyrhaugen og går øst om Litlfjellet mot Seterfjellet. Tar fjellet innover til Snaufjellvatnet og videre innover. Inne ved Bielnienjaevrie avslutter jeg dagen, slår opp teltet og fisker. Neste dagsmarsj blir vestom Luruvatnet innover Urddals- og Hykkelfjellene. Deretter får jeg se om jeg finner brukbar overnatting ved Oksvoll tjørna. Tredje dagen blir det siste etappen til Nordli. Turen går ned Nesskardet, Bustadlia og til Sørnes. Herfra går jeg veien fram til Sandvika/Nordli.</li>\n</ol>','Etappe 9','','publish','open','open','','etappe-9','','','2007-05-19 11:08:48','2007-05-19 09:08:48','',7,'http://falleri.no/ruta/etappe-9/',0,'page','',0),(28,1,'2007-05-19 11:12:14','2007-05-19 09:12:14','<strong>Første etappe i år er tilbakelagt. Det gjenstår å komme seg til Røyrvik før det store eventyret tar til.\n</strong>\n\n<strong>Sandvika/Nordli – Røyrvik 1/7 – 2/7</strong>\nLengde: 62 km + 10 %            =  68 km/34 = 2 dager\n\nPå denne etappen blir det å traske landeveien. Etappen er kort og jeg går fram til Røyrvik på to dager, noe som betyr at mine <a href=\"http://www.swissmasai.no/\">MBT-sko</a> får brynet seg igjen.\n\n<strong>Depot</strong>\nDepot med proviant og utstyr for etappen sendes:  Nte Expert Nordli, Post i butikk, Sandvika, 7882 Nordli, tlf. 743 43 052.\n\n<strong>Kart for etappen</strong>\n1923 IV    Nordli\n1924 III    Tunnsjøen\n1924 IV    Røyrvik\n\n<strong>Valgte ruter</strong>\nRuta er ganske kort, 2 dagsetapper og sin helhet på vei. Jeg legger nordover mot Tunnsjøen og krysser så vidt innom Sverige, går forbi Tunnsjø Senter og finner teltplass rett øst om Litl-Tunnsjøen. Herfra går det slakt nordøstover mot Limingen og det er bare å begynne å traske de siste 30 kilometerne langs vatnet til Røyrvik.\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\n\n1/7\n<a href=\"http://www.liernefritid.no/severdigheter.html\"> Sandvika/Nordli </a>415 moh. til oppunder Almannberga ca. 500 moh.  34 km. En plass her like sør eller øst om Litl-Tunnsjøen regner jeg med å finne teltplass\n2/7\nAlmannberga til Røyrvik 425 moh. Nok en dag med 34 km på vei. Jeg kommer mest sansynlig for sent til å få ut min depotpakke på Post i butikk. Da tar jeg heller en overnatting på <a href=\"http://visitborgefjell.no/index.php?side=1012&amp;a=13\">Limingen Gjestegård</a> og får pakken sendt dit.','Etappe 10','','publish','open','open','','etappe-10','','','2007-05-19 11:12:14','2007-05-19 09:12:14','',7,'http://falleri.no/etappe-10/',10,'page','',0),(29,1,'2007-05-19 11:13:31','2007-05-19 09:13:31','<strong>Jeg går inn i Børgefjellet, vår kanskje vakreste nasjonalpark og derfra til hjemlige trakter.</strong>\n\n<strong>Rørvik – Umbukta 3/7 – 16/7</strong>\nLengde: 205 km + 15 %        = 237 km/22 = 11+3 dager\n\nDette blir så langt den lengste, men også vakreste etappen. Det skal bli godt å kunne supplere provianten med egen fanget fisk. Uavhengig av dette må jeg prøve å få avtalt et mulig depot på nordsida av Børgefjell nasjonalpark. Prøver med Harvasstua, beregner 4-5 dager dit.\n\n<strong>Depot </strong>\nDepot med proviant og utstyr for etappen sendes etter avtale:  Nord-trøndelag Everk, Post i butikk, Sentrum, 7898 Limingen - Mellomdepot på Harvasstua?\n\n<strong>Kart for etappen </strong>\n1924 IV    Røyrvik\n1925 I        Susendalen\n1925 II        Børgefjellet\n2025 IV    Skardmodalen\n2026 III    Krutvatnet\n2026 IV    Hjartfjellet\n2027 III    Storakersvatnet\n\n<strong>Valgte ruter</strong>\nEtappen Rørvik – Umbukta er beregnet til å ta 11+3 dager. I Børgefjell Nasjonalpark kommer jeg til å bli værende noen dager, men jeg har ikke akkurat nå bestemt meg hvor. Fra og til vil derfor bli forandret underveis.\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\n\n3/7\nFra <a href=\"http://visitborgefjell.no/index.php\">Røyrvik</a> 440 moh. til Storvika 470 moh. 28 km. En litt lang dagsetappe, men terrenget byr ikke til leirslaging før Storvika. Den ligger ved sørøstenden av Namsvatnet og turen dit går på vei til Skånalia. Derfra tar jeg av østover i dalen mot Storgollomsvatnet. Videre opp gjennom Reinskardet og mot Rohthetjeklumpen og mer eller mindre strake veien til Storvika. Muligens, om jeg ikke er for trøtt tar jeg en ekstra kilometer opp lia til ei av småtjønnene for fiske og leirslaging.\n4/7\nStorvika til Viermavatnet 798 moh. 21 km. Jeg er inne i <a href=\"http://www.dirnat.no/borgefjell\">Børgefjell nasjonalpark </a>og skal vider innover Viermadalen. Krysser elva på bro nede på Viermaneset og går oppover vestsida av dalen til Viermavatnet. Teltslaging og fiske og jeg sannsynligvis en dag ekstra i området. Akkurat slik var min opprinnelige etappeplan, men nå har det skjedd en trivelig endring. Min gode venn Tor B. Håkonsen møter med på Virmaneset og går sammen med meg inn i Børgefjellet nasjonalpark. Det er også mulig vi legger inn en ekstra dag i Nasjonalparken.\n5/7\nViermavatnet til Simleelva 880 moh. 18 km.  Går vestom Cevlarsfjellet og til Simleelva. Dette kan også være en plass der jeg tar det med ro og ligger over en ekstra natt. Tar ingen endelig bestemmelse før jeg er framme.\n8/7\nSimleelva, forbi Storvollen til <a href=\"http://www.susendal.no/Fakta.html\">Harvasstua</a> 620 moh 21 km. Fra Simleelva går jeg i høyden nord om Sæjnoetjahke og vader Gihpejohke og går myrene og liene ned til Storvollen. Herfra og til Harvasstua går turen delvis på vei. På Harvasstua har jeg mellomdepot for etappen. (Mellomdepotet er flyttet til Kroken i Susendalen).\n9/7\nHarvasstua til Finnskartjønna 835 moh 17 km. Fra Harvasstua følger jeg Nordlandsruta, merket DNT-rute helt fram til Umbukta. Jeg går rett nordover til Daningen,  rundt vatnet på østsida, forbi Amirfjellet og oppover til Rotvatnet. Det skal ligge en åpen hytte litt sør om vatnet, men med godt vær går jeg forbi, følger reingjerdet og runder ned til Finnskardtjønna. Telting og forhåpentligvis ørret til kvelds.\n10/7\nFinnskardtjønna til Tverrelvnes 540 moh. 21 km. Fra Finnskardtjønna går jeg ned Finnskardet til Skarmodalselva. Der blir det å ta seg over elva og gå veien fram til Grannes ved Unkervatnet. Herfra skal jeg komme inn på merket DNT-sti og det blir så å ta seg opp Ørneslia for å ta stien over Sæterfjellet til vei ved Stortjørnmoen. Følger veien ned til Tverrelvnes.\n11/7\nTverrelvnes til <a href=\"http://www.krutfjellvegen.net/turstier.shtml\">Krutvatnet/ Fisketjørna</a> 586 moh. 23 km. Statskog skal ha en åpen hytte omentrent her. Fra Tverrelvneset går jeg stien forbi Holmvatnet til Mosvatnet og ned Tjuvjodalen til Brunreinvatnet. Videre Rauvassaksla til Rv. 73. Tar veien langs Krutvatnet fram til hytta ved Fisketjørna.\n12/7\nKrutvatnet/ Fisketjørna til Kilvatnet 681 moh. 23 km. Nå går jeg forbi Ol-Anderstjørna og på østsida av Fiskløysvatnet. Nedover mot Famnvatnet er det en litt merkelig blanding av naturreservat og landskapsvernområde. Jeg fortsetter videre forbi, oppover langs Storbekken og inn til Kilvatnet. I dette området med mange små tjørner og en par større vann i Skinnfelldalen kan det bli aktuelt med en ekstra overnatting. Råd fra lokalkjente vil avgjøre dette,\n14/7\nKilvatnet til <a href=\"http://www.hemnesturistforening.no/archives/category/hytter/gressvasshytta\">Gressvasshytta</a> 600 moh. 35 km. Dette er for langt å ta strekket på en dag. Jeg regnet derfor med enten å ordne med innomhus overnatting i Nordbukta/Røssvatnet eller telte i området. Herfra er det 21 km igjen til Gresvasshytta. Turen fram går fra Kilvatnet og inn i Tiurskardet, deretter krysser jeg Aksla mot Storelva. Ned til Nordbukta/Røssvatnet med overnatting. Dagen etter inn Spjeltfjelldalen, den er vernet som naturreservat på grunnlag av det rike fuglelivet og tildels på grunnlag av plantelivet. Opp fordi Skrikarbekken, øst om Spjelthaugen og nedover til Gressvasshytta.\n16/7\nGressvasshytta til Umbukta Fjellstue 535 moh. 30 km. En lang etappe, men i og med jeg blir hentet på fjellstua for overnatting hjemme er dette greit nok. Turen fram går langs Grasvatnet opp Storskardet, runder Grasfjellura og går østover til Skukken. Herfra rett nordover forbi Grunnvatnet, nordom Rundjellet og øst til Umbukta fjellstue.\nBlir hentet i Umbukta og drar hjem til Mo i Rana/Ytteren for en to/tre dager. Starter opp igjen fra Umbukta senest 19. juli.','Etappe 11','','publish','open','open','','etappe-11','','','2007-05-19 11:13:31','2007-05-19 09:13:31','',7,'http://falleri.no/ruta/etappe-11/',11,'page','',0),(32,1,'2007-05-21 23:53:15','2007-05-21 21:53:15','Idag ble teltet testet og avfotografert sammen med hund og mann.\n\n<img src=\"http://farm1.static.flickr.com/203/507940244_9885dde84b.jpg\" title=\"Gunnar og Najax\" alt=\"Gunnar og Najax\" height=\"363\" width=\"450\" />','Teltet','','publish','open','open','','teltet','','','2007-05-21 23:53:15','2007-05-21 21:53:15','',0,'http://falleri.no/2007/05/21/teltet/',0,'post','',0),(34,1,'2007-05-29 14:19:16','2007-05-29 12:19:16','','Rana Jakt og Villmark Logo','','inherit','open','open','','rana-jakt-og-villmark-logo','','','2007-05-29 14:19:16','2007-05-29 12:19:16','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/rana-jakt-og-villmark.png',0,'attachment','image/png',0),(35,1,'2007-05-29 14:20:39','2007-05-29 12:20:39','','Arctic Circle Logo','','inherit','open','open','','arctic-circle-logo','','','2007-05-29 14:20:39','2007-05-29 12:20:39','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/wwwarctic-circleno.jpg',0,'attachment','image/jpeg',0),(36,1,'2007-05-29 14:21:28','2007-05-29 12:21:28','','Hilleberg','','inherit','open','open','','hilleberg','','','2007-05-29 14:21:28','2007-05-29 12:21:28','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/hilleberg.jpg',0,'attachment','image/jpeg',0),(38,1,'2007-05-29 14:30:39','2007-05-29 12:30:39','','MTB Logo','','inherit','open','open','','mtb-logo','','','2007-05-29 14:30:39','2007-05-29 12:30:39','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/logo_mbt.jpg',0,'attachment','image/jpeg',0),(39,1,'2007-05-30 13:04:09','2007-05-30 11:04:09','Etter Nordkapp er det bare å legge seg i trening, så kanskje du kan bli <a href=\"http://www.aftenposten.no/nyheter/uriks/article1810826.ece\">den første 72-åringen</a>.','71-åring til topps på Mount Everest','','publish','open','open','','71-aring-til-topps-pa-mount-everest','','','2007-05-30 13:04:09','2007-05-30 11:04:09','',0,'http://falleri.no/2007/05/30/71-aring-til-topps-pa-mount-everest/',0,'post','',1),(40,2,'2007-06-01 17:22:40','2007-06-01 15:22:40','Idag fredag 1. juni sitter jeg i Bergen og er ferdig med de siste forberedelsene. Alle små detaljer er på plass. Avtaler er gjort, følgebrev for mine depoter skrevet, mann og hund, \"Fit for fight\". Utpå ettermiddagen starter bilturen til Lindesnes Fyr og mandag 4. juni er det  bare å hive seg ut i det store eventyret.\nHilsen fra Gunnar og Najax','Fit for fight','','publish','open','open','','fit-for-fight','','','2007-06-01 17:22:40','2007-06-01 15:22:40','',0,'http://falleri.no/2007/06/01/fit-for-fight/',0,'post','',0),(41,1,'2007-06-03 23:58:32','2007-06-03 21:58:32','Da er jeg, Flemming og Mikkel akkurat kommet oss hjem til Bergen igjen etter å ha avlevert mann og hund ved Lindesnes fyr.  Så får vi bare håpe at vandreren klarer å få ordnings på det tekniske utstyret slik at han klarer å avlevere rapport etterhvert som turen skrider fram.\n\n<a href=\"http://www.flickr.com/photos/gisle/528588353/\"><img src=\"http://farm1.static.flickr.com/232/528588353_441f1995ec.jpg\" width=\"333\" height=\"500\" alt=\"Gunnar og Najax klar ved Lindesnes fyr\" /></a>','Avlevert','','publish','open','open','','avlevert','','','2007-06-03 23:58:32','2007-06-03 21:58:32','',0,'http://falleri.no/2007/06/03/avlevert/',0,'post','',0),(42,1,'2007-06-05 11:52:25','2007-06-05 09:52:25','','Najax ved teltet','','inherit','open','open','','najax-ved-teltet','','','2007-06-05 11:52:25','2007-06-05 09:52:25','',43,'http://www.aas-sw.no/npl3/wp-content/uploads/2007/06/05062007024-440.jpg',0,'attachment','image/jpeg',0),(43,2,'2007-06-05 11:58:06','2007-06-05 09:58:06','Turen fra Lindesnes gikk greit. Første natten teltet  vi i Susvig ved den gamle Postvei ca. 4 km fra fyret. Neste dag var planen å komme noen kilometer nord om Vigeland, men desverre Najax fikk problem med venstre framfot og begynte å halte. Vi hadde da 1 km igjen til Vigeland. Fikk haik inn og planla telting på plenen ved bensinstassjonen. Der kom jeg i prat med Espen og ble tilbudt telting i hans hage. Her sitter vi da og Najax er fortsatt ikke bra. Det blir nok en tur til Mandal og veterinær. Jeg har alltids med smertestillende til hund, men syns ikke jeg uten videre kan begynne med en slik medikanering. Første etappe til Ljosland blir nok noe forsinket. Selv har jeg det helt utmerket. Det vanket både kaffe og en trivelig prat med en nydelig ung dame og hennes to år gamle sønn på morgenen. Nå er det bare å vente på skyss til Mandal og veterinærbesøk. Jeg håper inderlig det ikke er noe alvorlig feil med foten til Najax. Jeg hilser på det varmeste til alle som besøker meg på min hjemmeside.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/06/05062007024-440.jpg\" alt=\"Najax ved teltet\" />','Vigeland','','publish','open','open','','vigeland','','','2007-06-05 11:58:06','2007-06-05 09:58:06','',0,'http://falleri.no/2007/06/05/vigeland/',0,'post','',1),(44,2,'2007-06-06 11:26:53','2007-06-06 09:26:53','Gårdagens besøk hos veterinær var både trasig, men ga sammtidig håp. Diagnosen for Najax var en brukket klo/klokapsel. Veterinæren ga en tre dagers kur med smertestillende og antibiotika. Videre tar jeg en par korte turen med på ca. en halv time hunden om dagen. Skal tilbake på fredagen og kan da sansynligvis fortsette på etappen til Lojosland. Alternativet trekking av kløa vil bety en 6 ukers rekonvalenstid. I mellomtiden gjør jeg det beste ut av forsinkelse. Blant annet har jeg fått et partytelt over mitt utebord og har det egentlig som plommen i egget, alt tatt i betraktning.','Andre dag på Vigeland','','publish','open','open','','andre-dag-pa-vigeland','','','2007-06-06 11:26:53','2007-06-06 09:26:53','',0,'http://falleri.no/2007/06/06/andre-dag-pa-vigeland/',0,'post','',0),(45,1,'2007-06-07 14:32:13','2007-06-07 12:32:13','<p>Dette kartet viser hvor ruta går.  Den grønne streken viser hvor jeg har gått til nå.  Den blå viser resten ruta for sommeren 2009.</p>\n\n<div id=\"map\" style=\"width: 600px; height: 500px; border: solid gray\"></div>\n\n<script src=\"http://maps.google.com/maps?file=api&amp;v=2&amp;key=ABQIAAAAxWBLvr9Zi09tXKTgWuklohS-ZudBPv_auz4hjKOWqCGBNeADSxTPAt1ntPcLkrn1GbQzcnfu2eBnyQ\" type=\"text/javascript\"></script>\n<script src=\"/gmap.js\" type=\"text/javascript\"></script>\n\n<!-- what a hack -->\n<body onunload=\"GUnload()\"></body>','Kart','','publish','open','open','','kart','','','2007-06-07 14:32:13','2007-06-07 12:32:13','',0,'http://falleri.no/kart/',4,'page','',0),(46,1,'2007-06-08 17:42:12','2007-06-08 15:42:12','<a href=\"http://www.ranablad.no/\">Rana Blad</a> hadde Gunnar og Najax sin gledesytring som midtsideoppslag i helgebilaget sist lørdag.\n\n<a href=\"http://farm1.static.flickr.com/201/536092197_456e37f8e5_o.jpg\"><img src=\"http://farm1.static.flickr.com/201/536092197_e16cfe21af.jpg\" width=\"440\" height=\"302\" alt=\"En 2642 km lang gledesytring\" /></a>','Rana Blad sist lørdag','','publish','open','open','','rana-blad-sist-l%c3%b8rdag','','','2007-06-08 17:42:12','2007-06-08 15:42:12','',0,'http://falleri.no/2007/06/08/rana-blad-sist-l%c3%b8rdag/',0,'post','',0),(47,2,'2007-06-10 14:38:30','2007-06-10 12:38:30','Fredag var det til veterinæren for endelig vurdering av klobruddet til\nNajax. Alt så fint ut og veterinæren ønsket oss god tur videre og var\nmeget optimistisk på våre vegne. Fortsettelse og start ble lagt til\ngodt etter solnedgang samme kveld. Najax la i vei i fint driv, men\ntross alle hensyn, etter 2-3 km begynte dyret å dabbe av og la seg\nforholdsvis fort derretter pladask ned. Han lot seg, med gode ord og\nogså med klar kommando, kun bevege seg noen få skritt. Et slikt tempo\nvil knapt føre oss til Ljosland Fjellstove langt mindre til Nordkapp.\nDet er kun å innse, Najax er kollapset, i hvert fall for i år.\n\nVi kom oss tilbake til hagen hvor fire døgn alt var blitt tilbragt. Heldigvis\nvar vartskapet fortsatt oppe og jeg fikk teltplass for nok en natt.\nDeretter ble min sønn Gisle kontaktet for henting av Najax tilbake til\nBergen enten denne helga eventuelt neste. Parallelt satte Espen\nHolmesland, min utrolig villige og sympatiske vert igang med å skaffe\nmellomtidig plassering av Najax i nærmiljøen.  Fredag morgen kom\nbeskjeden fra Gisle: Vi har drøftet problemet og jeg kjører straks\netter frokost ned til Vigeland for å hente Najax. Dermed kunne også\nEspen gi kontrabeskjed til sine kontakter og stoppe sitt arbeid med å\nhjelpe meg med Najax. Med slike hjelpere er jeg nødt til å komme vel\nfram til Nordkapp. Ikke minst en spesiell takk til Espen Holmesland.\nSist mandag var vi begge helt ukjente for hverandre. Jeg ber tilfeldig\nEspen om råd og uten betenkninger: Bli med hjem til meg der disponerer\nvi en stor hage med plass både  til deg, hund og telt. Deretter yter\nEspen meg all mulig hjelp og bistand.\n\nNajax er nå hentet og alt vel framme i Bergen. Personlig brøt jeg opp\ntidlig i dag morges og fortsatte videre oppover Audnedal. Startet\nlitt før klokken fem og er kommet fram til Konsmo. Her tar jeg det nå\nmed ro i varmen. Spiser en enkel lunch og går etterpå i Audneelva og\nbader. Etter at varmen har gitt seg noe og sola er lavere trasker jeg\nen mils vei videre og finner teltplass for natten.','Videre uten Najax','','publish','open','open','','videre-uten-najax','','','2007-06-10 14:38:30','2007-06-10 12:38:30','',0,'http://falleri.no/2007/06/10/videre-uten-najax/',0,'post','',1),(48,2,'2007-06-12 19:59:40','2007-06-12 17:59:40','I går nådde jeg som sagt Konsmo. Der tok jeg en lang og god siesta på den lokale badeplassen. Plassen ligger ved Ytre Øydavannet. Der var jeg framme litt over kl. 2115. Underveis var bedt inn på litt kaldt å drikke. Du har vel tid til en 5 minutters stopp ble det sagt. Selvsagt, men 5 minutter ble til en halv time og en skikkelig hyggelig prat.\n\nTeltet ble som sagt reist ved Ytre Øydnavannet og der laget jeg et av mine kulinariske måltider, kokt naturris med fenalår og vann til drikke. I og med at jeg hadde lagt bak meg omkring 34 km valgte jeg å sove til jeg voknet av meg selv. Klokken ble faktisk 0800 og alt tydet på nok en varm dag. Jeg valgte derfor å bli til utpå ettermiddag før neste etappe. Du snakker om flaks. Rundt elve fikk jeg selskap på stranden av en meget trivelig ung dame, Tonje Birkeland Rafoss. Hun hadde med seg sin lille datter, Andrine sammen med en liten venn Adrian. Håper å få sendt bilder av disse fra Ljosland Fjellstove.\n\nEtter en flott dag med bading og trivelig samvær la jeg videre på min ferd utpå ettermiddagen.  Tilbakela en 22 kilometer. Passerte Sveindal og teltet  ved bredden av Mandalsåna. Jeg er framme på Åseral og sender disse ordene fra den lokale ambulansestasjonen. Har også hatt en prat med Åserals reiselivssjef. Reiselivssjefen redigerer en lokal nettavis, <a href=\"http://www.moisund.com\">www.moisund.com</a>. Det blir vistnokk et lite innlegg fra min tur der. Alt er vel og mine MBT-sko er en drøm å gå med på hard asfalt.\n\n<img src=\'http://www.aas-sw.no/npl3/wp-content/uploads/2007/06/11062007.jpg\' alt=\'Ytre Audnevann\' />','Åseral','','publish','open','open','','aseral','','','2007-06-12 19:59:40','2007-06-12 17:59:40','',0,'http://falleri.no/2007/06/12/aseral/',0,'post','',0),(49,2,'2007-06-13 14:42:21','2007-06-13 12:42:21','Gårdagen etappe videre fra Åseal ble lang og uten de store begivenhetene. Planen var å telte etter noen kilometer fra Åseral, men jeg valgte å gå helt fram til <a href=\"http://www.fjellstova.com/\">Ljosland Fjellstove</a>. Tilsammen fra starten i går morges ble det omentrent 40 kilometer. Jeg skulle egentlig overnatte underveis. Bra teltplass fant jeg, men derimot ikke godt drikkevann på denne delen av veien.  Valgte derfor å gå helt fram til fjellstova. Var framme tidlig på natta og slo opp teltet rett inn i veggen på huset. To bevere som la på svøm da jeg passerte i nattemørket var alt jeg opplevde underveis. Ikke engang en bil på veien. Etter en god frokost vil jeg tilbringe noen timer i en fantastisk fjellutehage. Denne ligger helt inntil fjellstova og er helt utrolig flott. Ljosland Fjellurtehage er navnet. Her på Ljosland Fjellstove blir jeg en eller to dager før jeg legger innover heiane mot Gaukhei turisthytte. Med meg selv er alt vel. Føtter og kropp fungerer perfekt. Etter alle milene på asfalt er det ikke tvil. Det er mine Massai MBT-sko jeg kan takke for alle problemfrie kilometere.  Jeg ser med fryd og lengsel på å ta fjellet fatt.','Ljosland Fjellstove','','publish','open','open','','ljosland-fjellstove','','','2007-06-13 14:42:21','2007-06-13 12:42:21','',0,'http://falleri.no/2007/06/13/ljosland-fjellstove/',0,'post','',0),(50,1,'2007-06-13 16:17:33','2007-06-13 14:17:33','','Ytre Audnevann','','inherit','open','open','','ytre-audnevann','','','2007-06-13 16:17:33','2007-06-13 14:17:33','',48,'http://www.aas-sw.no/npl3/wp-content/uploads/2007/06/11062007.jpg',0,'attachment','image/jpeg',0),(51,2,'2007-06-14 14:56:51','2007-06-14 12:56:51','Andre dag på Ljosland Fjellstove, men i morgen tidlig går turen innover til Gaukhei turisthytte. Dagene har jeg brukt vel. Late dager uten stress. Revurdert tidsforbruk på etappene og planlagt nye datoer for depoteskene. Uten Najax til hjelp har jeg også skåret noe ned på egen proviant for å lette litt på sekken.\n\n<a href=\"http://www.flickr.com/photos/gisle/547631351/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1306/547631351_32a06bd6a3_m.jpg\" width=\"240\" height=\"180\" alt=\"Anne Karin Bakke Eriksen\" /></a>Her i <a href=\"http://museumsnett.no/alias/HJEMMESIDE/naturmuseum/ljosland/\">Ljosland Fjellhage</a> (ikke Fjellurtehage) hadde jeg i  morges en lengre prat med Anne Karin Bakke Eriksen. Hun er en av de lokale ildsjelene med fjellhagen. Du ser Anne Karin i lukking, et arbeid som må gå mer eller mindre kontinuerlig. All takk til slike mennesker.<br clear=\"all\">\n\n<a href=\"http://www.flickr.com/photos/gisle/547613180/\"><img src=\"http://farm2.static.flickr.com/1006/547613180_e5369501ce.jpg\" width=\"440\" height=\"330\" alt=\"Parti fra Ljosland Fjellhage\" /></a>\n\nNå er det bare å nyte naturen, frisk fjelluft og gjøre seg klar for morgendagen.','Ljosland Fjellhage','','publish','open','open','','ljosland-fjellhage','','','2007-06-14 14:56:51','2007-06-14 12:56:51','',0,'http://falleri.no/2007/06/14/ljosland-fjellhage/',0,'post','',0),(52,2,'2007-06-15 21:42:19','2007-06-15 19:42:19','Her starter turen fra <a href=\"http://www.fjellstova.com\">Ljosland Fjellstue</a> og inn i fjellheimen.  Usikkert med hensyn til mobildekning inntil jeg når <a href=\"http://www.haukeliseter.no/\">Haukeliseter</a>.\n\n<a href=\"http://www.flickr.com/photos/gisle/553128352/\"><img src=\"http://farm2.static.flickr.com/1362/553128352_62465c33d5.jpg\" width=\"375\" height=\"500\" alt=\"Avreise fra Ljosland Fjellstove\" /></a>','Avreise fra Ljosland','','publish','open','open','','avreise-fra-ljosland','','','2007-06-15 21:42:19','2007-06-15 19:42:19','',0,'http://falleri.no/2007/06/15/avreise-fra-ljosland/',0,'post','',0),(53,2,'2007-06-18 11:50:17','2007-06-18 09:50:17','Sitter litt bortom Øyufsbu i nydelig vær og skriver. Voknet først halvti i dag etter en skikkelig god nattesøvn. Nok om det, tilbake til start.\n\n<a href=\"http://www.flickr.com/photos/gisle/693344773/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1030/693344773_074d7f6b30_m.jpg\" width=\"240\" height=\"180\" alt=\"Like før start fra Gaukhei\" /></a>\n\nTuren fra Ljosland til Gaukhei gikk greit nok, men var rimelig lang og strabasiøs. De fem timene oppgitt i <a href=\"http://www.turistforeningen.no/index.php?fo_id=1991\">TFIN</a> er etter å ha tatt båt inn til enden av Langevannet. Jeg gikk helt rundt på en dårlig merket og ryddet sti. På hytta fikk jeg opplyst at bare en av hundre går rundt vannet. På Gaukheihytta lå der forresten et meget trivelig gjeng ungdommer fra 25 til 40 år. Det ble en lang og trivelig kveld med kokt og stekt ørret, god drikke, sang og prat helt til klokken tre om natta.\n\nNeste dag mot Øyufsbu, jeg hadde beregnet 17 km, men avstanden var oppgitt på hytta til 20 km. Legger i vei, men må nok bekjenne en synd. På rimelig godt merket tursti, med kart på brystet og kompasset klart i lomma. Mister merkingen en par ganger, finner den igjen, skyet vær og går trøstig videre. Har rett og slett ikke sjekket retningen og er plutselig tilbake bare 3 til 4 kilometer fra startstedet. Bang, her var det bare å få opp teltet og gremmes. Skit au, dette skjer nok aldri mer. Ble vekket neste morgen av en sauedrift som drar forbi. Der var både flere gjetere med hunder til hjelp. Senere kom de tilbake og både gjeterne og sauene var fra Jæren. Rovdyrplagen opplyste de at de var helt forskånet for og at gjetingen er for å utnytte relativt skrinne beiter maksimalt.\n\nTuren denne dagen til Øyufsbu gitt helt etter boka. Kartet ble jevnt sjekket. Kurs lagt inn på kompasset og alle videre retningsendringer av noe betydning. Var framme i rimelig tid på kvelden. Prøvde fiske i vannet, men hverken jeg eller to andre som lå der fikk noe. Standard middag for så til sengs i elvetida. Sov som sagt i nesten 11 timer, høres ikke det deilig ut. Svartenut turisthytte neste.\n\n<a href=\"http://www.flickr.com/photos/gisle/693298463/\"><img src=\"http://farm2.static.flickr.com/1006/693298463_b6874e88da.jpg\" width=\"440\" height=\"330\" alt=\"Fra Øyufsbu\" /></a>','Øyufsbu','','publish','open','open','','%c3%b8yufsbu','','','2007-06-18 11:50:17','2007-06-18 09:50:17','',0,'http://falleri.no/2007/06/18/%c3%b8yufsbu/',0,'post','',2),(54,2,'2007-06-19 21:48:25','2007-06-19 19:48:25','Jeg sitter på en fjelltopp rett nord om Bossbu turisthytte med noe varierende kvalitet på sambandet. Det er fredelig, men stilt her inne i fjellheimen. På min tur fra Øyufsbu til Svartenut traff jeg kun på to mann, begge hollendere på tur inne i fjellheimen. Ellers er det atskillig mer stilt her enn hjemme hos oss. Det vanlige fuglelivet mangler. Jeg har hverken sett eller hørt en eneste heilo. Bekasiner, heipipelerker eller fjelljo mangler totalt. Det eneste er gjøken. Den kan jeg høre overalt.\n\nEtter hvert som jeg arbeider meg nord og innover fjellene begynner det å bli mere snø. Trugene har jeg derimot ennå ikke hatt bruk for. På en måte er det bra, på den annen side bærer jo dem nyttesløst. At jeg mangler min bærer, Najax merkes. Sekken er rimelig tung og jeg må nok regne med noe kortere etapper enn opprinnelig planlagt. I dag tok jeg tidlig kveld både for bloggen og for å hvile ømme muskler. Dog sitter jeg på en fjelltopp høyt over hytta for å skrive. Det var oppgitt SMS-forbindelse her oppe, og jeg takker for denne opplysningen. I morgen starter jeg på en rimelig lang og ganske strabasiøs etappe mot Kringlevatn turisthytte. Skal opp over den høyeste overgangen på denne del av rutenettet. Blir det i tillegg å bruke truger er det ikke usansynlig at jeg må ta en natt i telt. Bortsett fra en del ømme muskler er alt vel. Akkurat nå litt sent på kvelden er sikkert en ti grader og varmt og godt i sola.','Fjelltopp ved Bossbu','','publish','open','open','','fjelltopp-ved-bossbu','','','2007-06-19 21:48:25','2007-06-19 19:48:25','',0,'http://falleri.no/2007/06/19/fjelltopp-ved-bossbu/',0,'post','',4),(57,2,'2007-06-20 15:52:36','2007-06-20 13:52:36','I går hørtes jeg sikkert noe pesimistisk ut, men turen fra Bossbu gikk greit. 4-5 km med snø hvorav 1,5-2,9 km sammenhengende. Sekken som i går var blytung var noe helt annet i dag. Alt gikk lettere. Det var nok en mening med feilnavigeringen tidligere og den ekstra dagen det tok. En dag tidligere og dette strekket hadde vært utrolig tungt. Nå er kroppen fint restaurert. Tro om det ikke er høyere makter som har stått meg bi.\n\nVadet ved Ljosåni tok jeg med fjellskoa på beina. Barbeint hadde det aldri gått godt. Gode sko,lange gamasjer og turstaver, selv med vann til over knærne går kryssing støtt og problemfritt. På Kringlevatnhytta ble det til hele 9 timers søvn i natt. Herlig å våkne sikkelig utvilt.\n\n<a href=\"http://www.flickr.com/photos/gisle/694211834/\"><img src=\"http://farm2.static.flickr.com/1375/694211834_1e888974f5.jpg\" width=\"440\" height=\"330\" alt=\"Vadet ved Ljosåni\" /></a>','Bossbu - Kringlevatn','','publish','open','open','','bossbu-kringlevatn','','','2007-06-20 15:52:36','2007-06-20 13:52:36','',0,'http://falleri.no/2007/06/20/bossbu-kringlevatn/',0,'post','',0),(58,2,'2007-06-21 15:59:40','2007-06-21 13:59:40','Fra Kringlevatn fram til kryssing av elva i Breidalen var det et lett og fint terreng. Vading over elva gikk greit tross mye vann. Uten skikkelige sko og gamasjer på hadde jeg fått store problemer. Barbeint ville det vært direkte uansvarlig å lagt over elva. Strømmen var sterkere her enn i Ljosåni. Fra nå av og fram til Skorpeskaret gikk det omentrent bare på snø. Snøen bar godt og trugene hang bare på sekken som ekstra ballast. Forbi Bjupatjørn var det en del bratte skavler jeg måtte ta omveier forbi, tildels skikkelig bratte. Gåingen på snøfonner med tildels bratte heng har tatt sin tid og gitt støle muskler. Nå er det like før jeg når Storsteinen turisthytte. Elva over til hytta er ganske stri med rimelig stor vannføring. Gamasjene må på igjen.\n\n<a href=\"http://www.flickr.com/photos/gisle/693306393/\"><img src=\"http://farm2.static.flickr.com/1305/693306393_f42ac39c7a.jpg\" width=\"440\" height=\"330\" alt=\"Vadet over elva i Breidalen\" /></a>','Kringlevatn - Storsteinen turisthytte','','publish','open','open','','kringlevatn-storsteinen-turisthytte','','','2007-06-21 15:59:40','2007-06-21 13:59:40','',0,'http://falleri.no/2007/06/21/kringlevatn-storsteinen-turisthytte/',0,'post','',0),(59,2,'2007-06-22 16:03:31','2007-06-22 14:03:31','Dette ble en distansetest. Kartbladet oppgir avstanden fra Storstein til pos. 0384300 - 6575350 til 5 km. Dette strekket målte jeg med påslått GPS til i overkant 6 km, et påslag på 20%. Heretter vil jeg bruke et tillegg på fra 15-20 % alt etter terrengets beskaffenhet. GPSen brukes ellers kun til posisjonsbestemmelse ved behov.\n\nDenne traseen jeg nå går er ganske tøff med mye opp og ned. I tillegg satt gårdagens vandring på snø godt i beina. Fonnene i oppoverbakke var i dag meget sugende og tunge. Heldigvis, fra høyden av Gyvassfjellet flater terrenget seg ut og blir ganske lettgått. Sammenlignet meg gårdagen, ganske lite snø og det som var ble gått skrått eller eller nedover. Lunchet, min standard på 1 kopp suppe, 3 Bixit, 70 gram sjokolade og en kopp te, ble inntatt på nestet mot Blåsjø.\n\nHar fjellene tidligere vært flotte og spetakulære, hva kan da sies om det som møtte meg fra Reinsgrovtjønnane og inn til Hovatn? Et helt utrolig område. Høysommer her må være et pardis. Dersom det i tillegg er fisk i tjønnane er dette drømmeland. I dag dekket snøen over det meste og isen var ennå ikke gått på vannene. Hovatnhytta har jeg allene. På forhånd hadde jeg bestemt å ta en hviledag her. Det passet bra. Da jeg våkner om morgenen ligger tåka tjukk og det er kun 4 grader ute.\n\n<a href=\"http://www.flickr.com/photos/gisle/694167130/\"><img src=\"http://farm2.static.flickr.com/1355/694167130_9c295ae4aa.jpg\" width=\"440\" height=\"330\" alt=\"Like før Hovatn\" /></a>','Storstein turisthytte - Hovatn turisthytte','','publish','open','open','','storstein-turisthytte-hovatn-turisthytte','','','2007-06-22 16:03:31','2007-06-22 14:03:31','',0,'http://falleri.no/2007/06/22/storstein-turisthytte-hovatn-turisthytte/',0,'post','',0),(60,2,'2007-06-24 16:09:52','2007-06-24 14:09:52','Tåka ligger som en grøt kl. 7 om morgenen. En 1/2  time senere letter den og dagen ser ut til å bli klar og fin. Fra hytta går alt greit fram tll utløpet av Hovatnet. Vannføringa er på grensen til forsvarlig kryssing. Ingen isgang og med turskoa på og gode gåstaver ser jeg ikke det store problemet, bortsett fra våte sko.\n\nVidere går det i et arktiskt og imponerende fjellandskap med snø, islagte vann, grønne bare områder, alt omkranset av fjelltopper og nuter. Sola får insektene til å yre i de grønne flekkene. Både krypende på bakken og flyvende like over. I tillegg er de første fjellblomstene i fult flor. Midt oppi det hele, trøtte bein og skuldre. Herre min skaper, kan en 70 årig kropp ha det bedre? Det er bare å bøye seg og takke. Ved Vaasdalstjørn blir det lunch. Der sikkert at maten har stor betydning å du går slik i fjellene. Ved Pøyleåa blir det vading på nytt. Våt igjen, men nå er det bare 6-7 km igjen til Krossvatnhytta og opptørking.','Hovatn turisthytte - Krossvatn turisthytte','','publish','open','open','','hovatn-turisthytte-krossvatn-turisthytte','','','2007-06-24 16:09:52','2007-06-24 14:09:52','',0,'http://falleri.no/2007/06/24/hovatn-turisthytte-krossvatn-turisthytte/',0,'post','',0),(61,2,'2007-06-25 16:15:21','2007-06-25 14:15:21','En gledelig overraskelse, bru over Storåa og deretter strake vegen innover mot Nøvlenuten. I oppstigningen kom trugene på for første gang. De var siden stort sett på over hele fjellpartiet til langt ned i Kjyrkjesteindalen. Nærmest full, om enn varm vinter i denne høyden på 1000-1250 meter.\n\nKontrasten er stor når jeg kommer nedover dalen mot Mostølen. Full sommer, med grønne bjørker og enorme blåbærlier. En vakker og flomstor elv som plutselig ble til intet. Statkraft, eller er det Agder Energi tok alt vatnet.\n\nVertskapet burde en vel ikke kritisere, men en ting stusser jeg på. Hvordan går det an å kviste en vintertrasse gjennom Kjyrkjesteindalen? En mer jævlig fjellslukt for ras har jeg knapt sett. Trang, bratt, høye steile fjellsider, overhengende skavler, tydelig spor etter store snø- og steinras. På toppen av det hele gikk jeg over et bredt og stort skrefelt som nylig hadde gått tvers over dalen. Det lå flere døde trekkfugler som vindtrykket må ha slått ihjel. For 8 dager siden gikk en annen NPLer, Inger Thommasen ned skaret og hun skriver i hytteboka på Mostøl:\n\n<blockquote><em>\"...jeg følte litt uhygge alene i skaret. Det var vilt der, og det raste stadig snø og stein fra den andre siden. Det føltes befriende å komme ned...\"</em></blockquote>\n\nTakk, jeg vil si det samme. Skulte stadig opp på en par skavler som ennå hang oppe mot toppen av fjellsida. Det var skjønt å komme forbi og vel ned til Mostøl.','Krossvatn turisthytte - Mostøl turisthytte','','publish','open','open','','krossvatn-turisthytte-most%c3%b8l-turisthytte','','','2007-06-25 16:15:21','2007-06-25 14:15:21','',0,'http://falleri.no/2007/06/25/krossvatn-turisthytte-most%c3%b8l-turisthytte/',0,'post','',1),(62,2,'2007-06-26 17:41:00','2007-06-26 15:41:00','En grei tur på rundt 15 km og anslått til 5 timer. Jeg brukte 7 timer inklusiv lunch og diverse fotografering. Traff forresten et par fra Fredrikstad som var der på dagstur.  Et varierende og spetaktulært landskap med flere gamle setergrender langs elva Gaukstølåa. De første kilometrene var også denne elva helt tørrlagt, men etter en inntaksdam rant den i fordums prakt. Grønt og praktfullt hele veien. I vannskillet ned mot Bleskestadmoen blir det mer dramatiskt. Elva ned har skåret seg en djup og spennede canyon, Valajuv. Stien ned slynger seg dels på kanten av stupet og flater etterhvert ut i flotte bjørkelier og sletter.\n\nBleskestadsmoen ligger på en svær flat slette på 730 meter og består av 2 hytter. Den minste av disse er en nyrestaurert perle. Bygget i 1891 og var STFs første turisthytte. Pietetsfullt restaurert i 2001 med støtte fra Thons stiftelse og Kulturdepartementet. Her på  Bleskestadmoen tok jeg fram stanga for å prøve fiskelykken. Det ble ingen fisk, men neste dag lå tåka tjukk som en graut. Ikke snakk om å gå videre med slik en sikt. I stedet brukte jeg dagen til stell og oppshining av hytta. Den var litt smårotede og ikke helt prober. På ettermiddagen fikk jeg besøk av to forskere fra Landbrukshøgskolen på Ås. Jeg ba dem inn på opptørking, varmt hus og kaffe. Til gjengjeld fikk jeg en god innføring i det prosjektet de var involvert i: måling av skogens tilvekst etter at beitetrykket er gått ned og klimaendringens virkning på skoggrensa. Den som vil vite mer får sikkert det ved henvendelse til Landbrukshøgskolen.\n\n<a href=\"http://www.flickr.com/photos/gisle/693304227/\"><img src=\"http://farm2.static.flickr.com/1088/693304227_cc75bd8960.jpg\" width=\"440\" height=\"330\" alt=\"På vei mot Bleskestadmoen\" /></a>','Mosøl turisthytte - Bleskestadmoen turisthytte','','publish','open','open','','mos%c3%b8l-turisthytte-bleskestadmoen-turisthytte','','','2007-06-26 17:41:00','2007-06-26 15:41:00','',0,'http://falleri.no/2007/06/26/mos%c3%b8l-turistbytte-bleskestadmoen-turisthytte/',0,'post','',0),(63,2,'2007-06-28 17:48:54','2007-06-28 15:48:54','Dagen ser bra ut. Grått, småregn, men adskillig mindre tåke enn dagen før. Jeg starter turen mot Holmavatn, en 17-18 km lang tur. Det går rimelig greit opp til demningen ved Sandvatnet. Det tar til å bli kjøligere og blåse noe. Samtidig begynner tåka å sige nedpå. Jeg runder vannet til vestre bredd hvor utbyggeren har en hytte stående. I ly for denne får jeg tatt på noe mere klær. Termometeret på hytta viser 4 grader. Da tåka i tillegg tjukner vurderer jeg seriøst om det beste vil være å få opp teltet og avvente neste dag. 80-100 meter lengre framme ser jeg en liten varde i en snøfond. Kan dette være på stien videre fram? Alt stemmer etter kart og kompass. Jeg går fram uten sekk for å sjekke. Riktig, et DNT merke midt i fonna. Da jeg snur meg kan jeg ikke se hytta jeg nettopp forlot. Men OK, det er bare 100 meter tilbake i nøyaktig motsatt kompassretning.\n\nInntar min lunch og ser at sikten er bedre innimellom. Tar bestemmelsen på å gå videre. Det er tross alt en trasse uten mange kruseduller, mere rett fram. Ullhue, vindvotter og ullhals kommer på og utenpå alt en regnponcho. Finner litt sti og merker dan og vann og tar jevnlig kompasskurs og sjekker med kartet der det går. Der letter tåka noe og jeg kan øke tempoet litt. Over Tverråna er heller ikke brua kommet opp for sommeren. Bra vannføring, men jeg finner en plass hvor elva er ganske så bred med en del stein og grunner på kryss og tvers. Ikke noe dårlig sted for vading. Der kommer jeg meg stort sett tørrskodd over med mine eminete gamsjer på. Fra nå av går det rimelig greit  fram til hytta. Hele turen fra Sandvatnet på fra 1000-1200 meter har vært preget av mange snøfonner og til dels så pass bratte at jeg måtte stemple mens jeg tok meg fram. Godt å være i hus. Fått fyr i ovnen og hentet to bøtter vatn. Bare mat, skriving og avslapping gjenstår.','Bleskestadmoen turisthytte - Holmavatn turisthytte','','publish','open','open','','bleskestadmoen-turisthytte-holmavatn-turisthytte','','','2007-06-28 17:48:54','2007-06-28 15:48:54','',0,'http://falleri.no/2007/06/28/bleskestadmoen-turisthytte-holmavatn-turisthytte/',0,'post','',0),(64,2,'2007-06-29 17:56:42','2007-06-29 15:56:42','Kontrastene er store, i går tåke, vind og surt, i dag strålende sol, skyfritt og varmt. Turen fram mot Haukeliseter går i lettgått terreng fram mot Turistskaret. Her må trugene på og med mange bratte snefonner får jeg konstatert at det går å gå fiskebein med truger. Stemple går i hvert fall ikke. Det er storslått og vakkert i sol med fantastiske formasjoner på fonnene i dette området. Trugene har jeg på til forbi Fjellvåknuten.\n\nTid for lunch i en solvarm skråning dekket med varm og tørr lyng. Jeg kan se <a href=\"http://www.haukeliseter.no/\">Haukeliseter</a> i det fjerne, et <a href=\"http://en.wikipedia.org/wiki/Soria_Moria_Castle\">Soria Moria</a>. Der venter en  halv pils, en 10-pakning Malboro, en dusj, klesvask, en bedre middag, hvite lakene og en god seng. Sivilisasjonen har også sine fordeler.','Holmavatn turisthytte - Haukeliseter','','publish','open','open','','holmavatn-turisthytte-haukeliseter','','','2007-06-29 17:56:42','2007-06-29 15:56:42','',0,'http://falleri.no/2007/06/29/holmavatn-turisthytte-haukeliseter/',0,'post','',0),(65,2,'2007-07-01 10:50:18','2007-07-01 08:50:18','I et strålende vær etter duglig frokost med egen kokt havregrøt legger jeg i vei fjellsida opp fra Haukeliseter. Lia opp er sydvent og uten snø. Tydelige spor i terrenget  viser at her er trafikken mye større enn i Sirdals- og Ryfylkeheiene. Til sine steder er det direkte tråkket vei innover den første delen av vidda. Tråkket gir seg noe etter noen kilometer inn fra Haukeliseter, men hele veien inn mot Hellevassbu der det er snøfritt er stien tydelig merket av stor trafikk.\n\nPå veien innover tar jeg igjen og prater med en tysk familie, mor og far med en gutt og en jente. Begge nærmer seg sikkert tenåra. På dagstur, men foreldrene hadde vært her en uke allene for tre år siden. Nå ville de prøve å få ungene til å like og elske land og landskap og få med seg turgleden. Mens vi prater tar to damer, Berit fra Søgne og Ruth fra Kristiansand meg igjen. Begge skal til Hellevassbu og Litlos og vi slår oss på lag.\n\nNå begynner det å bli mye snø og trugene kommer  på. Mine to turkammerater er usikre på om de skal gå videre og setter seg ned oppe i en solskråning. Ved Mannevatnet treffer jeg nok en gang på 2 hollendere. De har snudd på grunn av mye snø. Mens jeg prater med hollenderne, hvem andre enn mine to damer kommer gående. Fra nå av og videre innover er alle merker dekket av snø og en noenlunde forståelse og bruk av kart og kompass må til. Begge damene sier at hadde det ikke vært for meg måtte de snudd. Ved vadet ved Holmasjøen tar jeg min lunch. Det er blitt noe mer snøbart og vi skiller lag. Fra nå av og fram går det greit etter merket sti, bortsett fra at trugene mine må på etter brua ved Simleøyra.\n\nPå Hellevassbu har mine to venninner installert seg i ei lita sikringshytte. De var uten DNT-nøkkel, hovedhytta låst og begge våte og kalde og valgte derfor ikke å vente på meg. Arrangementet fører til at jeg utpå kvelden får nabobesøk. Tro det eller ei, naboene har med en Jagermeister. Til denne spanderer jeg Arctic Rein og Vilt sitt eminente tørkede reinkjøtt. Kvelden tar vi i elvetida. Hellevassbu, en flott hytte med alt på stell og meget ryddig. Ei selvbetjent hytte det var en fryd å komme til.','Avgang Haukeliseter','','publish','open','open','','avgang-haukeliseter','','','2007-07-01 10:50:18','2007-07-01 08:50:18','',0,'http://falleri.no/2007/07/01/avgang-haukeliseter/',0,'post','',0),(66,2,'2007-07-02 11:23:24','2007-07-02 09:23:24','Etter en god natts søvn tar jeg dagen med ro og starter først i tolvtida mot Litlos. Berit og Ruth gikk en time tidligere i et ennå rimelig klart vær. På vei opp mot Buadalen kommer tåka sigende, riktig tjukk og fra nå av er det bare kart og kompass, assistert av GPS-peilinger for posisjon som gjelder.\n\nJeg tenker på de to som er forran meg. Navigerer litt feil, kommer til høyre for en liten nut. Bratt snøfonn ned mot et lite åpent vann. Må snu og gå rundt på rett side av nuten. Heldigvis finner jeg sporene til de to andre, og bare i en retning. Vi har tydeligvis ikke omgåttes. Ved Tangasjøen møtes vi. Da har Berit og Ruth snudd for nå  tok de ikke sjansen på å gå videre alene. Snø og tåke gjorde sikten lik null. Enig om at vi var sånn omtrent midtveis og like gjerne kunne fortsette mot Litlos. Sikten og terrenget var ikke noe bedre tilbake enn framover. Ble det for ille fikk vi heller ta oss inn i et av husa på Søre Belebotn. Tåka var så tjukk at vi aner ikke hvordan terrenget har sett ut. På sine plasser måtte jeg sende en av jentene fram for å ha noe å peile etter. Sikten var da ikke mer enn 50/60 meter. Uten DNTs ruter hadde jeg bare måttet få opp teltet. Det hadde alltids blitt en kald fornøyelse med bare et liggeunderlag og en sovepose. Uansett, sikkerheten for liv og helse har vi hele tiden hatt i fokus.\n\nI fjellsida rundt Velurnuten klarner det opp.  Hvilken utrolig deilig følelse og få se langt utover og hvor vi skal gå. Vi kommer oss ned til Søre Belebotn og finner ly for vinden i et gammelt nedlagt sauefjøs. Der får vi mat i oss og med maten og sikten kommer motet tilbake. Vi sparer setereieren for et innbrudd og legger i vei på de siste 7 kilometrene til Litlos, nå i god sikt.  Befriende å komme fram til Litlos fjellstue, til dusj, rikelig og god, varm  middag med en deilig rødvin.  En tøff tur i tåka. Jentene ga meg all ære og sa at denne turen hadde vært deres livs opplevele.','Hellevassbu - Litlos','','publish','open','open','','hellevassbu-litlos','','','2007-07-02 11:23:24','2007-07-02 09:23:24','',0,'http://falleri.no/2007/07/02/hellevassbu-litlos/',0,'post','',0),(67,2,'2007-07-04 12:27:40','2007-07-04 10:27:40','En dag med skikkelig regn og vinden rett imot. Alt i alt ikke så verst her inne på Hardangervidda når jeg får høre at det har vært storflom med stengte veier og ødelagte avlinger lengere øst og sør i landet. Her inne i fjellet har regnet de siste dagene tæret hardt på snøen. Forleden gikk jeg sammen med Berit og Ruth på forholdet 80% snø og 20% barmark. I dag var det motsatt. Vel nok gikk turen i dag i et mye lettere landskap med mye mindre snø totalt enn forrige dagers etapper. I dag alene fra Litlos, vinden imot fra nordøst og et kraftig regn. Etter kort tid er jeg våt som ei <a href=\"http://no.wikipedia.org/wiki/Skj%C3%A6re\">skjur</a> og tildels kald. Men som tidligere sagt landskapet var lettere, også å gå i. Ganske flatt, bare med små høydeforskjeller. Ennå lå et så pass tjukke snøfonner at bekker og elver kunne gås på snøbruer. Ingen tåke, godt merket, det var bare å gå på i skitværet. Brukte 8½ time på de 22-24 kilometrene fram til Sandhaug turisthytte. Med to hvilepauser og en 20-25 minutter lunchpause gikk jeg stort sett etter tida oppgitt av DNT.\n\nPå <a href=\"http://www.sandhaugturisthytte.no/\">Sandhaug</a> blir jeg møtt av et meget trivelig vertskap og en halv liter øl fra eget microbryggeri. Vann fra en boret brønn på 35 meter og brygget i fjellet. Fem, nok en gang hollendere kommer fra Tronsby. Sammen har vi en hyggelig stund ved middagsbordet. Alle kjente til Mo i Rana, men bare fra gjennomreise. Stadig vekk gode føtter og fin form. I dag hørte og så jeg forresten også to <a href=\"http://no.wikipedia.org/wiki/Heilo\">heiloer</a>.\n\n<a href=\"http://www.flickr.com/photos/gisle/827533713/\"><img src=\"http://farm2.static.flickr.com/1226/827533713_2c7301cbca.jpg\" width=\"440\" height=\"330\" alt=\"Utsikt fra soverommet på Sandhaug turisthytte\" /></a>','Litlos - Sandhaug','','publish','open','open','','litlos-sandhaug','','','2007-07-04 12:27:40','2007-07-04 10:27:40','',0,'http://falleri.no/2007/07/04/litlos-sandhaug/',0,'post','',0),(68,2,'2007-07-05 17:49:51','2007-07-05 15:49:51','Starten gikk sent i dag også, først kl. 1200 la jeg i vei mot Bjoreidal. Var tidlig oppe, men tok livet helt avslappet. Tross alt går jeg nå inn i den helt flate delen av Hardangervidda. Jeg karakteriserer den som ganske kjedelig. En mann jeg traff på underveis definerte denne delen som en transportetappe. For hans vedkommende startet Hardangervidda i fra Nordmannslågen. Da ble vidda mer spennende, fisket godt, i det hele tatt mye mer variasjon i landskapsformene.\n\nMine hollandske middagsvenner ønsket å følge meg på min hjemmeside tross av at den kun er tekstet på norsk. De tok noen bilder av meg til minne om møtet.\n\nPå de 20-22 kilometerene fram til Bjoreidal brukte jeg bare 4½ time effektiv gangtid. Dette sier noe om hvilket landskap turen gikk gjennom. Samtidig traff jeg på mye trivelige folk og pratet sikkert i en god time med disse menneskene. Blant annet ble gode råd både mottatt og gitt. I tillegg, tror dere ikke at jeg nok en gang både så og hørte en par heiloer.\n\nDet har regnet godt de siste dagene og elvene er flomstore. Stort sett kommer jeg meg over på snøbroer eller sommerbroer som nå er kommet på plass. Snøsmeltingen, regnet og flommen har ikke gitt nevneverdige problemer for meg.','Sandhaug - Bjoreidalshytta','','publish','open','open','','sandhaug-bjoreidalshytta','','','2007-07-05 17:49:51','2007-07-05 15:49:51','',0,'http://falleri.no/2007/07/05/sandhaug-bjoreidalshytta/',0,'post','',0),(69,2,'2007-07-06 07:48:20','2007-07-06 05:48:20','Det ble en mye senere start i dag enn opprinnelig planlagt. En god mobilforbindelse gjorde at jeg prioriterte oppdatering av blogen. Med et litt spesielt tastatur og en meget liten skjerm, tar dette sin tid. I hvert fall ble det til slutt såpass sent at planlagt telting i Leirbotn ble skrinlagt. Kvelden og natten blir på Kjeldebu ved Fisktjørna.\n\nDagens etappe gikk etter mitt skjønn i en meget vakker del av Hardangervidda. Her var jeg merkelig nok alene bortsett fra et ungt par jeg traff litt innpå. Stien opp og inn fra Dyranut fjellstue var betydelig mindre oppgått enn tidligere. Ikke stort mere oppgått enn du kan finne stiene hjemme hos oss. Landskapet er her lett kuppert og meget variert. I tillegg får jeg det første glimtet av breene her inne. Den jeg ser heter Vestra Leirbottsskåka og kom til syne rett etter runding av Dyranutane. Et vakkert syn der breen hvelver seg over fjellkammen.\n\n<a href=\"http://www.flickr.com/photos/gisle/828391352/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1125/828391352_d5f725302a_m.jpg\" width=\"240\" height=\"180\" alt=\"Det unge paret jeg spiste sammen med\" /></a>Lenger innpå treffer jeg som sagt to ungdommer, en svensk jente og en tysk gutt. De to holder akkurat på å reise teltet sitt. Jeg spør om det er OK å få slå seg ned og lage min lunch sammen med dem. Egentlig mer et ettermiddagsmål da tida er ganske langt på ettermiddagen. Vi får en god samtale og en trivelig stund sammen. De byr blant annet på hvitvin og jeg på tørket reinkjøtt. Gutten lurer på om det kan være slik pemikan smaker. Kjøttet faller ihvertfall i god smak. Jenta har faktiskt vært i Mo i Rana sist sommer på Vikafestivlen.\n\nVidere inn til Kjeldebu går det greit i et nydelig og variert landskap. Jeg trives, ja direkte mollkoser meg her inne. På Kjeldebu, egentlig tre hytter er det hyttevakt. En trivelig dame sammen med tre barn og et vennepar på besøk. Vi får en lang og god samtale før jeg går inn og lager meg middag, i dag fenalår og langkornet ris. I morgen blir det den store manndomsprøven. Rundt 24 kilometer fram til Finse i kuppert terreng med mye snø og med flomstore elver. En riktig tøff etappe. En som kom derfra i går hadde måttet telte underveis.','Bjoreidalshytta - Kjeldebu','','publish','open','open','','bjoreidalshytta-kjeldebu','','','2007-07-06 07:48:20','2007-07-06 05:48:20','',0,'http://falleri.no/2007/07/06/bjoreidalshytta-kjeldebu/',0,'post','',1),(70,2,'2007-07-08 19:49:10','2007-07-08 17:49:10','Starten fra Kjeldebu var grei nok og kilometrene ned til elva Leiro gikk raskt. Jeg hadde fått melding om mye snø og kraftig smelting, men det som møtte meg ved Leiro var uventet. Enorme vannmengder, grå av masse fra breen og mellom meg og brua fosset det uvanlig kraftig. Uten sekk prøvde jeg å ta meg over, men ga opp. Det førte en sti nedover og selv om jeg regnet det som usansynlig gikk jeg for å sjekke mulighetene lengre ned. Det var ikke sjangs.\n\n<a href=\"http://www.flickr.com/photos/gisle/828476584/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1288/828476584_6784f5140f_m.jpg\" width=\"240\" height=\"180\" alt=\"Leiro\" /></a>Tilbake ved brua satte jeg meg ned litt oppgitt og tenkte, --- det va´ som fan, --- skal jeg virkelig måtte snu her. Etter noe betenkningstid bestemte jeg meg, jeg skal over. Uten staver sikret i leddene med sportstap, gode tunge sko, tyngde av tung sekk og god tid mellom hver fot som ble flyttet hadde det vært umulig å nå brua. Det gikk bra, men var nok med livet som innsats. Resten av turen ble nærmest et antiklimaks. Bortsett fra at kroppen var sliten gikk det greit oppover mot Leirbotn. Der måtte jeg rett og slett ta en pause. Lunchet og sov i sola på 1100 meter i nesten tre kvarter. Hadde mest lyst til å slå leir, men i tilfelle tåke neste dag ville jeg forbi  Helvetesnutane og komme meg gjennom Helvetesgilet. Planla deretter telting ved Brattefonnvatnet, og slik ble det.\n\nDagen i dag er det 7-9 km igjen og jeg gikk dem raskt, først i sol og varme for deretter i tjukk tåke de siste to kilometrene. Jeg har faktiskt ikke sett Hardangerjøkulen i dag.','Kjeldebu - Finse','','publish','open','open','','kjeldebu-finse','','','2007-07-08 19:49:10','2007-07-08 17:49:10','',0,'http://falleri.no/2007/07/08/kjeldebu-finse/',0,'post','',1),(71,2,'2007-07-09 21:49:50','2007-07-09 19:49:50','Min søndag og hviledag på Finsehytta ble meget trivelig. Mange trivelige folk, men spesielt et nederlandskt ektepar, Dita og Jan Henk. Disse to traff jeg i tåka en kilometer eller to på vei til  Finsehytta. På kvelden spiste vi middag i lag og hadde en lang trivelig samtale. Begge skal samme vei som meg og vi vil treffes på Geitrygghytta i morgen. Dagen før spanderte jeg rødvin til middagen og i dag var det Jan Henks tur.\n\n<a href=\"http://www.geiterygghytta.com/\">Geitryggen</a> er en mindre og betydelig mer intim betjent hytte enn Finsehytta. Barnas Turlag var her med 18 deltagere og ungene satte i høyeste grad sitt preg på atmosfæren. Fra Finse startet jeg som vanlig litt ut på dagen. Etter råd fra Jan Eira, verten på Gaukhei sendte jeg trugene hjem fra Finse og lettet børa med 1,8 kg. Traséen fra Finse er relativ enkel bortsett fra at høyeste punkt er på 1640 m. To Osloungdommer, Anette og Kyrre gikk samtidig som meg fra Finse stasjon og jeg hadde følge med disse helt fram. Trivelig for en \"gamling\" å bli godtatt som turkompis sammen med slike. Rast og mat på Klemsbu oppe på 1600 m. Da vi runder toppen og kommer fjellsida ned mot Bakkahelleren er det til en annen verden. Fra snø og stein til grønne fjellsider, åpne vann og elver. Hele verden lysere og det føles varmere.\n\n<a href=\"http://www.flickr.com/photos/gisle/820800527/\" title=\"Photo Sharing\"><img src=\"http://farm2.static.flickr.com/1331/820800527_a85f680a31.jpg\" width=\"440\" height=\"330\" alt=\"Gunnar og Anette spiser lunsj på Klemsbu\" /></a>\n\nI går kveld ble det en noe trykket stemning. Verten informerte oss om at et stort stei- og jordras var gått litt lengre ned i Aurlandsdalen og sperret av denne. Senere kom det beskjed om at eierne til to biler ikke var lokalisert. Søk pågår i rasområdet i dag, dagen etter.\n\nJeg skiller lag med hollenderne. De skal vestover til Vassbygdi. Mine unge venner vil jeg treffe igjen på neste mål, Kongshelleren turisthytte.\n\n<a href=\"http://www.flickr.com/photos/gisle/820801117/\"><img src=\"http://farm2.static.flickr.com/1137/822030796_0d7769c8a8_o.jpg\" width=\"440\" height=\"795\" alt=\"Gunnar i fint driv mellom Finse og Gjeiterygghytta\" /></a>','Finse - Geitrygghytta','','publish','open','open','','finse-geitrygghytta','','','2007-07-09 21:49:50','2007-07-09 19:49:50','',0,'http://falleri.no/2007/07/09/finse-geitrygghytta/',0,'post','',1),(72,2,'2007-07-10 21:57:41','2007-07-10 19:57:41','Denne ruta blir spesiell og kommer til å bli vel testet av meg. I morgen tar jeg turen tur/retur <a href=\"http://www.geiterygghytta.com/\">Geitrygghytta</a> uten eller med helt lett sekk. Glemte rett og slett GPSen igjen der i dag. Både for sikkerhetens skyld og oppdateringen av framdriften må den hentes før jeg går videre. Dagens trasse var for det meste snebar, men noe steinete. Opp fra 1200 m til passering på 1500 m og ned til Kongshelleren på 1450 m. Greie elvekryssinger med lite flom. Jeg kom fram like etter mine unge venner, Anette og Kyrre. I morgen går de videre til <a href=\"http://www.iungsdalen.no/\">Iungsdalen</a> mens jeg får en ekstra overnatting på Geitryggen.\n\nI tillegg til oss tre kom det en rutemerkegjeng fra Oslo og Omengs Turistforening til Geitryggen for overnatting. Et trivelig gjeng på seks personer. Av disse var to norske, tre danske og en tysker. Utlendingene begrunnet rett og slett sin deltagelse med at de gjennom årene hadde fått så mye og så fine opplevelser i de norske fjellene at de ville gi noe tilbake.','Geitrygghytta - Kongshelleren','','publish','open','open','','geitrygghytta-kongshelleren','','','2007-07-10 21:57:41','2007-07-10 19:57:41','',0,'http://falleri.no/2007/07/10/geitrygghytta-kongshelleren/',0,'post','',0),(73,2,'2007-07-11 22:37:13','2007-07-11 20:37:13','Turen gikk i full fart, brukte kun 2½ time på de 13-14 kilometerene. Skikkelig god følelse å kjenne at kroppen ennå var i stand til å strekke ut.  GPSen var tatt vare på og det ble stekt laks til lunch. Med sekk på bare 4 kg tok jeg returen i rusletempo og bare nøt følelsen av lett bør. Da dagen i tillegg var klar og uten tåke, en av de få den senere tid var det deilig å kunne se landskapet jeg gikk i. Denne delen av Skarvheimen er vekslende, men uten de dramatiske landskapsførmene. Rolig bølgende fjell og åser, oppbrutt av elver og vann. Med beitende sau, villrein, et rimelig bra fugleliv og frodig vegetasjon brutt av snø og noen spisse nuter. En virkelig flott del av fjellnorge. Velkommen til fjells.','Tur/retur Kongshelleren - Geitryggen','','publish','open','open','','turretur-kongshelleren-geitryggen','','','2007-07-11 22:37:13','2007-07-11 20:37:13','',0,'http://falleri.no/2007/07/11/turretur-kongshelleren-geitryggen/',0,'post','',0),(74,2,'2007-07-12 23:01:16','2007-07-12 21:01:16','Gårdagens trimtur hadde løsnet godt opp i muskelaturen og i slakt fjellterreng gikk ferden videre lett. <a href=\"http://www.iungsdalen.no/\">Iungsdalen turisthytte</a> ligger på 1140 m og med start fra 1450 til maks 1500 m forteller dette om en heller rimelig høydeklatring for deretter jevnt fall i grønne fjelllier hellende ned mot Iungsdalselva. Denne fløt som en juvel i dalboten. Dagen var noe våt og med tildels mye stein i høyden ble det forsiktig gange på sleip stein. Det er bare å ta det rolig og se etter hvor en setter foten så går alt greit nok. Derimot er det lite trivelig bare å gå og se ned og hva gjør en da: Jo, stopper opp inn i mellom for å få sett seg rundt.\n\nIungsdalen turisthytte ligger hvirkelig flott til. De gamle setervollene brukes og både ku, sau, gris og gjess går ute å beiter. Vel gjessene i innhenging, men de øvrige fritt. Maten på Iungsdalen er  noe for seg selv. Melk, fløte og rømme fra egen fjøs. Ørret fra Øljavatnet, alt råvarer til dagens middag. Vi ble servert fiskesuppe fra ørret med både fisk og annet godt i, helskjønn. Stekt ørret, 350-450 grams, fisken helt perfekt saltet og pepret med sprøstekt skinn og tatt fra panna i akkurat rette øyeblikk. Bare min svigerfar kan gjøre det like bra og muligens en tanke bedre. Rømmesaus av rømme fra egen seter og i tillegg ikke agurksalat, men en kålsalat iblandet en rømmedressing i absolutt balanse med fisken. Nyyydelig - toppet med en råmelkdessert. Dertil klorfritt, friskt fjellvann og rødvin. Avsluttet i godt selskap med en cognac. Litt av et fjelliv, eller hva?\n\nVil du vite mer, besøk perlen på <a href=\"http://www.iungsdalen.no/\">www.iungsdalen.no</a>. Forresten ikke å forglemme morgenen deretter. Da var jeg i fjøset\' og fikk en kvart liter siladroppe.','Kongshelleren - Iungsdalen','','publish','open','open','','kongshelleren-iungsdalen','','','2007-07-12 23:01:16','2007-07-12 21:01:16','',0,'http://falleri.no/2007/07/12/kongshelleren-iungsdalen/',0,'post','',0),(75,2,'2007-07-13 08:54:39','2007-07-13 06:54:39','Planen var egentlig å gå forbi Bjordalsbu og telte noe lengere framme mot Breistølen. Ble tilnærmet rådet til å stoppe og ligge over i Bjordalsbu på grunn av umulige teltforhold oppe i høyden. I stedet for start kl. 0900 startet jeg derfor rundt 1130 og var framme ved Bjordalsbu kl. 1930.\n\nTilbake til den egentlige turen. Ruta går gjennom Fødalen naturreservat i et flott lende, men opp 600 høydemeter fra 1100 til 1700 meter på det høyeste. Turen inn og opp fjelliene i Fødalen steg rolig i gress- og  lyngkledde skråninger.  Ved Klevavatnet  var det en ganske så tøff snøfonn å forsere. Jeg stemplet godt og en familie med to relativt små gutter greide å ta seg opp etter meg. Herfra gikk turen opp og rett inn i Helvetet på 1500 meter. Ja, de kaller en fjellformasjon så. I tillegg har jeg tidligere passert Helvetesnutane og Helvestesgjelet så det må være et populært ord å bruke på disse trakter. Selv syntes jeg ikke turen opp og rundt Helvetet var særlig tøff, men mulig det kan blåse kraftig i kastene i dette passet. Videre innover til Valevatnet noe opp og ned. Etter rast ved vatnet bar det jevnt oppover til 1700 meter. Her gikk jeg i en type steinur jeg var blitt forespeilet, Opp-, ned- og bortoverstein. Over en strekning på kanskje 5 km var det mest bare stein, men ispedd noen dryasområder med enorme mengder issoleie, vakkert. Ved rundingen av 1700 m koten kunne jeg ikke dy meg. Fikk av meg sekken og kom meg opp på toppen, 1771 m. Nuten har ikke noe navn på kartet, men hva gjør vel det. 1771 m er det høyeste jeg har vært oppe på. Enn så lenge er mine to steiner høyeste punkt på varden  her oppe. Herfra bare et kort stykke, 4-5 km igjen til Bjordalsbu. Mye stein i denne høyden, men også gode faste nordvendte snøfonner å gå på.\n\nVel framme regnet jeg mest med å bli aleine på hytta. Kommer ut for å hente vann, der står det jagu en mann til. Roar, han betegnet seg selv som bygutt, men måtte på fem minutter lære seg den elementære teknikken med å kløyve ved. Nå er hytta varm og vi kan la roen senke seg.','Iungsdalen - Bjordalsbu','','publish','open','open','','iungsdalen-bjordalsbu','','','2007-07-13 08:54:39','2007-07-13 06:54:39','',0,'http://falleri.no/2007/07/13/iungsdalen-bjordalsbu/',0,'post','',0),(76,2,'2007-07-15 09:02:30','2007-07-15 07:02:30','På etappen Finse til Tyinkrysset var jeg egentlig overbevist om at hadde overvurdert min egen styrke, men tatt hensyn til ekstraturen til Geitryggen brukte jeg akkurat den planlagte tiden til Tyinkrysset. Litt får en \"takke\" regn og vind på siste del av turen til Sulebu for. Nå sitter jeg søndags formiddag på Sulebu og har kun noen få kilometer igjen til Tyin, toppen 6-7.\n\nStarten fra Bjordalsbu gikk i fint tørt vær om enn ikke solskinn. Godt var dette for på de første kilometrene var det mye \"opp- og nedstein\" i tillegg til \"bortoverstein\". Et skikkelig tøft underlag som i våt tilstand også er sleipt. I det tørre fine været kunne jeg tross alt holde et vist tempo og slapp den maksimale aktpågivenheten.  I hellingen ned mot Rankebotnen, om ennå på 1500 meter var det stort sett godt underlag. Flotte fjell og grønne lier, lokkende elver og vann, hvile for både kropp og sjel. De siste kilometrene ned til Breistølen, 1025 m går det bratt, men på god sti  nedover. Klokken to benket jeg meg til kjøttkaker, ertestuing, tyttebær og brun saus. Etter 1½ time er mannen klar for de siste parogtyve  kilometrene til Sulebu. Nå regner og blåser det kanske så kraftig og lia opp fra Breistølen ser uhyggelig bratt ut. Jeg skal da tross alt opp 400 høydemeter. Til min store glede stiger det jevnt og trutt uten de helt bratte kneikene. Skulle tro traseen var stukket med tanke på en NPLer av 1937 årgang. Fram til Sulebu går det greit i bra tempo. Også her en del av samme slags ur som tidligere, mest gjennom Suleskaret og litt til. Skikkelig sleipt i regnværet. Nå er det jeg virkelig begynner å ønske meg litt sommervær. Ved Øvre Sulevann ser jeg først 6 villrein. Like etter passerer flere villreinsflokker. Sikkert mer enn 100 dyr etter det jeg kunne telle. Satt faktiskt i ro i nærmere ett kvarter for ikke å uroe mens reinen gikk forbi.\n\nKunne nok teltet tidligere på godt underlag, men alt underveis var plaskvått. Innendørs overnatting og opptørking fristet så jeg gikk helt fram til Sulebu, en ubetjent hytte. Etter 12½ time var det godt å komme inn i varmt hus, men alt folket der hadde tatt kvelden. <a href=\"http://www.flickr.com/photos/gisle/827521953/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1177/827521953_bc20078c16_m.jpg\" width=\"240\" height=\"180\" alt=\"En dansk familie oppe på Sulebu\" /></a>Neste morgen blir jeg vekket av barnelatter. En dansk familie med to små barn har tatt turen hit opp på 1330 meter. Minstegutten var bare litt over to år,  et skikkelig sjarmtroll og broren har akkurat passert fem.','Bjordalsbu - Sulebu','','publish','open','open','','bjordalsbu-sulebu','','','2007-07-15 09:02:30','2007-07-15 07:02:30','',0,'http://falleri.no/2007/07/15/bjordalsbu-sulebu/',0,'post','',1),(77,2,'2007-07-15 11:14:50','2007-07-15 09:14:50','I dag er det søndag og hviledag. Tar det helt avslappet på Sulebu. Koser meg, spiser det meste av den siste maten fra forrige depot og bare nyter livet.\n\nLitt utpå dagen kommer to middelaldrede belgiere opp til Sulebu. Jeg inviterer begge inn til varmt hus, te og tørket reinkjøtt. Det er det eneste jeg har igjen å by på. Begge, Cill og Rich (kortform for Cicilia og Richard) er privatpraktiserende leger. De eneste to på et lite sted, men med rimelig stort omland. Nå er de i Norge på en totalt avslappende ferie, uten telefonforbindelse. De fortalte litt om hvordan praksisen artet seg i et lite samfunn i Belgia. Jeg tror faktiskt de har det vesentlig hardere og må stå mer på enn tilsvarende praksis i Norge. Etter slik de fortalte lignet det mer på de gamle husleger i Norge. Telefoner fra grytidlig morgen til sent på kveld. Konsultasjoner på hjemmekontor og personlige besøk hjem til dårligere pasienter. Delvis medisin og mye trøst. Jeg ble invitert til å besøke dem i Gammelstågo. Der hadde de leid seg inn for en uke og var akkurat kommet på lørdagen.\n\nRich, en ivrig amatørfotograf med godt utstyr, hadde vel stort sett berget hele ferien og vel så det. Han var stolt og i rene lykkerusen. På turen ned fra Sulebu gikk de på en flokk villrein og Rich hadde greid å få en hel masse virkelig gode bilder. Bilder av både flokken og enkeltdyr. Litt av en gave å ta med hjem og vise venner i Belgia. Ikke å forglemme, dette er en norgesreklame verdt utrolig mye. Jeg ble servert frukt, nøtter, kjeks og kaffe. Du og du så godt å kjenne smaken av en saftig appelsin.\n\nFramme på <a href=\"http://www.tyin.no/no/\">Tyinkrysset Fjellstue</a> var begge mine depotesker, den ene med nye kart og proviant og den andre med mine MBT-sko kommet. Etter å ha dusjet, lest tilsendte eksemplarer av min lokalavis (<a href=\"http://www.ranablad.no/\">Rana Blad</a>) ble det middag og videre pleie av kropp og føtter. I morgen slipper jeg til på husets PC og da blir det en del nye bilder til hjemmesiden min.','Sulebu - Tyinkrysset fjellstue','','publish','open','open','','sulebu-tyinkrysset-fjellstue','','','2007-07-15 11:14:50','2007-07-15 09:14:50','',0,'http://falleri.no/2007/07/15/sulebu-tyinkrysset-fjellstue/',0,'post','',0),(78,2,'2007-07-16 20:55:31','2007-07-16 18:55:31','Fra Tyinkrysset og innover mot Fondsbu blir det å gå på vei. Først 5-6 km på asfalt og deretter grusvei langsetter vei 252. Bakkene opp fra Tyinkrysset er ganske seige med jevn stigning over 5-6 km. Tyinkrysset fjellstua var mitt depot for etappe 5. Med ny proviant og  fjellskoa hengende på sekken ble det 26 kg på ryggen. Heldigvis, fra Ljosland var mine MBT-sko kommet. Uten disse på føttene hadde jeg knapt kommet opp bakkene uten meget såre ben. Fjellsko, tung sekk og asfalt hører i hvert fall ikke sammen. Men det var først utpå ettermiddagen at jeg kom meg ut på veien. Oppdatering av bloggen, svar til de som besøker meg på denne, lån av fjellstuas PC og overføring av bilder tar sin tid. Klokka ni satte jeg telt ved vannet Tyin omtrent midtveis melleom Tyinkrysset og Fondsbu.\n\nDet er en ting jeg ikke kan la være å nevne; tåballen bak venstre stortå var begynt å bli hard og øm. Den var så pass plagsom at jeg spurte etter fotbehandler på Tyinkrysset, men forgjeves. Tro det eller ei, etter 24 km på grus og asfalt med MBT-sko på beina er føttene perfekte og har leget seg selv. Egentlig helt usansynlig, men sant. En stor takk til Swiss Masai.\n\nDet småregnet så smått da jeg la meg om kvelden og prognosene for neste dag var heller ikke de beste.','Telt ved Tyin','','publish','open','open','','telt-ved-tyin','','','2007-07-16 20:55:31','2007-07-16 18:55:31','',0,'http://falleri.no/2007/07/16/telt-ved-tyin/',0,'post','',0),(79,2,'2007-07-17 21:11:37','2007-07-17 19:11:37','Jeg skriver i ruteplanen for etappe 5: <blockquote><i>Den tøffeste delen av turen er tilbakelagt. Fra nå av går det mot varme og late dager i Jotunheimen.</i></blockquote>\n\nDet var med tanke på at når jeg nærmet meg 4. juli ville sommeren være kommet for fullt i fjellet. Nå er det den 17. og bortsett fra i Sirdalsheiene har det for det meste vært tåke, regn og temperaturer på fra 3-9 grader. Noen må ha glemt å sende sommeren hit til den sørlige fjellheimen.\n\n<a href=\"http://www.flickr.com/photos/gisle/844562559/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1414/844562559_14be17a608_m.jpg\" width=\"240\" height=\"180\" alt=\"En våt morgen ved Tyin\" /></a>Nå i dag tidlig våknet jeg til oppholdsvær, men det varte ikke lenge. Like etter ni tok det til med regn og dette økte på utover formiddagen. Jeg måtte ut med Morakniven, grøfte og få ledet vannet bort fra teltet. Da klokka nærmet seg to var det bare ett å gjøre, la regn være regn, koke havregrøten, spise denne med kanel, sukker, rosiner og smørøye, pakke og komme seg til Fondsbu til opptørking. Også jeg som hadde regnet med telt for det meste fra nå av. Uansett er det godt å ha slike turisthytter som Fondsbu  på veien.  Nå var det vel egentlig bare teltet og en genser som var våte, men med 1-1,5 kg vann i teltduken ble sekken nok et hakk tyngre. Med denne vekta tror jeg rett og slett beina hadde blitt en lidelse uten mine MBTsko på.\n\nNå sitter jeg her på Fondsbu, har vasket litt klær, hengt alt vått til tørk, nyter et mørkt øl fra Valdres Gårdsbryggeri og har det bare skjønt. Litt senere blir det ut og koke sin middag. Her gjelder det å spise ned vekta som skal bæres. Tar jeg all mat fra egen beholdning går det med ca. 650 gram i døgnet.','Opptørking på Fondsbu','','publish','open','open','','fondsbu','','','2007-07-17 21:11:37','2007-07-17 19:11:37','',0,'http://falleri.no/2007/07/17/fondsbu/',0,'post','',0),(80,2,'2007-07-18 14:17:01','2007-07-18 12:17:01','Dagen gikk med. På Fondsbu var de ikke villig til å låne ut PCen for bildeoverføring.  Da var det noe annet på <a href=\"http://www.eidsbugarden.no/\">Eidsbugarden Høyfjellshotell</a>. Enda jeg ikke var deres gjest var det omgående ja. Egen bærbar ble stilt fritt til min rådighet og kaffe ble servert. Linja derimot, mobilt nettverk var heller treg og overføringa av mine ganske tunge bilder tok uforholdsvis mye tid, men nå er det gjort. Kommer ut hotelldøra og hva er det jeg da ser? Jo et skilt for kroa \"Den glade vandrer\". Du snakker om sammentreff. Det er bare å komme seg bort på Fondsbu, hente sekk og få noen til å fotografere meg foran skiltet.\n\nRuta jeg tenkte å gå, \"Den gamle postveien\" ble jeg advart mot på grunn av mye flom i fjellet. (Denne postveien gikk i sin tid fra Vågå til Læhrdal). I stedet ble det turiststien over fjellet mot Gjendesbu. Her ble det telting ca. 5 km fra Gjendesbu. Det helt store var at turen gikk i solskinn denne dagen. Det er lenge siden sist og i tillegg var det behagelig varmt. Natten ble tilbragt på en kolle dekket av reinlav og med fossedur fra Veslåe.','Fondsbu - Telt Veslåe','','publish','open','open','','fondsbu-telt-veslae','','','2007-07-18 14:17:01','2007-07-18 12:17:01','',0,'http://falleri.no/2007/07/18/fondsbu-telt-veslae/',0,'post','',0),(81,2,'2007-07-19 16:26:20','2007-07-19 14:26:20','Jeg skriver i min ruteplan: <i>\"... 22 km og en skikkelig dagsmarsj. Til Memurubu mye tøft terreng, men deretter en god gammel stølsvei.\"</i> Dette var en beskrivelse fra DNT og må kalles en heller lett overdrivelse. Stølsveien var ingen vei, men heller en stølssti. Delvis i brukbar hevd, men på sine steder både steinete, våt og gjørmete, noe kronglet opp over steile berghammere og i tillegg dårlig ryddet for overhengende greiner og trær. Greiner og trær det ikke var mulig eller vanskelig å gå rundt. Det var bare å bøye seg, gå ned på kne og til dels sno seg og krype under. Gå ikke med sekk pakket høyere enn hodet ditt på denne \"veien\". Motorsag og litt mer vedlikehold hadde gjort seg. Fra teltet ned til Gjendebu enkelt og greit. Lettgått på god sti. Verten der var en meget trivelig kar han skrøt forresten av sin hjemmeside, <a href=\"http://www.gjendebu.com\">www.gjendebu.com</a>. Håper det stemmer for jeg har selv ikke funnet noen sider fra dette området verdt å linke til. Liker verten min hjemmeside lovet han å legge link til denne. Prøv også <a href=\"http://www.nasjonalparkriket.no\">www.nasjonalparkriket.no</a> om denne kan være noe.\n\nGjendebu til <a href=\"http://www.memurubu.no/\">Memurubu</a> var et reint blodslit med tung sekk. Opp mot Memurutunga er det 400 høydemeter på en strekning av 1 km. Deretter en god del opp og ned før du kommer så langt at du skal rett ned, om enn ikke like bratt fra Sjugurdtinden til Memuru. Memurudalen derimot, med elv, flere flotte vann, små breer og fjelltopper var utrolig lokkende, men en avtale til den 30. i Folldalen må holdes. Det var derfor bare å gå forbi. Nå må det ikke glemmes at underveis traff jeg på mye hyggelige folk som kom i mot. Det ble derfor en del pauser med trivelig prat og promotering av min hjemmeside. Av annet liv hørte jeg plutselig bjeller opp mot en stor snøfonn. Lurte straks på hva i all verden sauen hadde her oppe å gjøre. Det jeg ser er tre rein løpende over fonna. Siden får jeg høre at i dette området er det tamreinsdrift og ikke villrein.\n\nEtter en kraftig tallerkenrett/middag spesialtillaget for meg er det \"på han igjen\". Denne gangen etter \"stølsveien\". En \"vei\" jeg jo har jeg beskrevet tidligere. Jeg hadde planlagt å telte, men av visse grunner ble det ikke noe av dette. Underveis  passerer jeg  et ungt par, en norsk jente og en australier. Jenta hadde skadet kneet og hun bare kreket seg i vei. De ønsket heller ikke at jeg skulle gå tilbake til Memurubu for å hente hjelp. Om nødvendig ville de bruke natten for å komme seg til <a href=\"http://www.gjendesheim.no/\">Gjendesheim</a>. Da det senere tok til å regne og  jeg fikk erfare hvor ulendt stien var gikk jeg på mot Gjendesheim for å få varslet og eventuelt sendt i vei en båt. Like før Gjendesheim treffer jeg på tre ungdommer ute på leit. I tillegg ser jeg et redningshelikopter på søk, mest oppe i fjellet og innover Besseggen. Helikopteret kommer ned og jeg får pratet med mannskapet. Jeg føler meg skikkelig letter over at de er der og de tar godt i mot mine opplysninger. Kun kort tid etter er dama bragt i sikkerhet og en motorbåt er på vei etter de øvrige. Jeg får meg rom og går til sengs.','Veslådalen - Gjendesheim','','publish','open','open','','vesladalen-gjendesheim','','','2007-07-19 16:26:20','2007-07-19 14:26:20','',0,'http://falleri.no/2007/07/19/vesladalen-gjendesheim/',0,'post','',0),(82,2,'2007-07-20 15:10:57','2007-07-20 13:10:57','Et lite paradoks, etter gårdagens vidreverdigheter legger jeg om ruta og går direkte ut på veien fra Gjendesheim. Velger hard asfalt rett og slett for å få hvile for slitte bein og muskler etter gårdagens lange og tøffe fjellvandring. Med MBT-skoa på føttene og fjellskoa, liggeunderlaget og fiskestanga hengende utenpå på sekken er det litt av en pakning å se til. Veien er OK om enn noe mer kjedelig enn den opprinnelige traseen fra Gjendesheim opp mot Vestfjellet, forbi Bessvatnet, til Russvatnet og ned Russdalen. Først nå var det meningen å komme ut på vei 51. I stedet ble det strake veien til jeg teltet rundt 3 km sør om Hindsæter Fjellhotell. Kvelden var en ren nytelse, varmt vær, solskinn og ikke en dråpe regn. Den første riktige sommerkvelden på lenge.\n\nFra en vandring etter asfaltert riksveg er det ikke det store å berette. Derimot vil jeg berømme bilistene. I de langt fleste tilfellene meget hensynsfulle og de fleste slakket opp og om nødvendig stoppet helt opp for å la motgående trafikk passere. Vil du vite mer om Jotunheimen foreslår jeg hjemmesiden <a href=\"http://www.etojm.com/Norsk/Turer/Jotunheimenogvaldres/jotunheimen.htm\">www.etojm.com</a>.','Gjendesheim - Telt v/Sjoa','','publish','open','open','','gjendesheim-telt-vsjoa','','','2007-07-20 15:10:57','2007-07-20 13:10:57','',0,'http://falleri.no/2007/07/21/gjendesheim-telt-vsjoa/',0,'post','',0),(84,2,'2007-07-21 18:13:29','2007-07-21 16:13:29','Denne natten i teltet har vært den varmeste så langt oppe i fjellene. For første gang siden Sørlandet var det unødvendig med stillongs, strømper og genser i soveposen og morgenen var var god, tørr og passe varm. Etter en tidlig frokost vandrer jeg videre nedover Sjodalen og til <a href=\"http://www.hindseter.no/\">Hindsæter Fjellhotell</a>. Her har jeg planlagt formiddagskaffe, oppdatering av bloggen og en matbit før jeg går videre. Tiden ruller i veg og mange er interessert i mitt gjøren og laden. Ikke minst vekker mitt lille Nokiatastatur interesse.\n\nMatbiten på Hindsæter ble til en kraftig middag med elgkaker og rikt tilbehør. Tror nok maten bar preg av at hotellet hadde en tysk kokk. Maten smakte rett og slett himmelsk og var helt etter min gane. Veien videre til Randsverk er egentlig en ren rusletur. Allikevel kan jeg ikke la være å reflektere over en merkverdighet. Jeg følger tross alt Sjoa og Sjodalen nedover og da er det forbausende hvor mye oppoverbakker det kan være i et dalføre som går nedover. Litt nede i dalen ved sideelva Veo finner jeg et opplysningsskilt over en tidligere vei og bro. Da vegvesenet tok over ansvaret for veien gjennom dalen rev de denne broa. Nå hadde turistkontoret i Vågå samlet inn midler og fått restaurert denne. Gammelbrua hadde vært av en unik konstruksjon og jeg tok den lille kilometeren opp for å ta bilder av rekonstruksjonen.\n\nSjoa gikk flomstor, ja så stor at mine planer om en raftingtur måtte skrinlegges. To litauiske raftere hadde dagen før druknet i elva og Sjoa Rafting hadde allerede før denne tragedien innstilt all aktivitet på Sjoa. I stedet ble teltet slått opp og natta tilbrakt på Randsverk Camping. En \"gammel\" NPLer ble godt mottatt og spandert både mat og drikke av campingens eier og driver.','Randsverk','','publish','open','open','','randverk','','','2007-07-21 18:13:29','2007-07-21 16:13:29','',0,'http://falleri.no/2007/07/21/randverk/',0,'post','',0),(85,2,'2007-07-22 18:28:18','2007-07-22 16:28:18','<a href=\"http://www.flickr.com/photos/gisle/929902365/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1264/929902365_e65e4c00c0_m.jpg\" width=\"240\" height=\"180\" alt=\"Jonas, min nye bærer\" /></a>Søndagene definerer jeg i utgangspunktet som hviledager og denne er ikke anderledes. En lang rolig formiddag og deretter en sju, åtte kilometer opp til Lusæter. Formiddagen på campingplassen gikk med trivelig prat og samvær med naboene. Både \"fastboende\" og \"dagsboende\". Jonas, sønnen til en riktig trivelig Nittedalsfamilie, måtte bare prøve sekken min. Opp fikk han den, bare siste skulderreima måtte pappaen hjelpe til med. Av to av de andre naboene, Berit og Egil, får jeg gode råd og ønske om god tur på min vei videre mot Lalm og Sel. Berit hadde som ungjente gjett kyr i det fjellområdet jeg skulle over og Egil hadde som ungdom gått fra Sel til Lalm på dans og spel. Godt kjent i området var de begge to. Litt utpå dagen trasker jeg i veg. Ved vegkrysset ved Leirflaten ser jeg at Lusæter ikke bare er en seter, men også en gård. Greit nok, dit må jeg og litt forbi for å finne teltplass.\n\n<a href=\"http://home.online.no/~sjoaraft/\">Lusæter gård</a> byr på de helt store overraskelsene. Først treffer jeg på noen store, langraggede og overlegne dyr. Det er så vidt de verdiger meg et blikk og jeg har på følelsen at de tenker: <em>Ja, ja, han der må gjerne få se på oss og passere. Ikke noe å bry seg med.</em> Jeg fikk rett og slett en følelse av at dette feet var overlegne og sikre på seg selv. Vel framme ved gården ser jeg så en stor vindmølle som står og produserer strøm. Alle gårdens dører er åpne, men ikke en sjel er å se. Ikke finner jeg noen heller og jeg ser etter og finner en plass å krysse enga uten å trakke ned. Mens jeg holder på å skifte til fjellsko kommer en bil kjørende inn fra jordene. Dermed var det gjort. Den langveisfarende kom ikke derfra, men ble invitert inn på kaffe, mat og til sist en seng i eget hus. Karstua fikk jeg helt for meg selv.\n\nDessverre får jeg senere om kvelden telefon fra min kone Sara med bud om at min svigerfar døde på lørdagen. Det er bare å legge om alle planer og komme seg oppover til Hammerfest.','Randsverk - Lusæter Gård','','publish','open','open','','randsverk-lus%c3%a6ter-gard','','','2007-07-22 18:28:18','2007-07-22 16:28:18','',0,'http://falleri.no/2007/07/22/randsverk-lus%c3%a6ter-gard/',0,'post','',0),(86,2,'2007-07-22 18:46:53','2007-07-22 16:46:53','Til alle mine nye og gamle venner: Jeg fikk i dag bud om at min svigerfar Sverre Pedersen døde på sykehjemmet i Hammerfest. Sverre var nettopp fylt 95 år og var en mann full av livsglede, humor og varme. Jeg bryter midlertidig av min vandring og drar til Hammerfest og Sverres begravelse.\n\nAlt mitt utstyr blir stående på Lusæter gård og derfra vil jeg starte igjen en gang i neste uke. I mellomtiden blir det en stopp i oppdateringen av min blogg. Håper på at dere og andre jeg treffer på min vei fremdeles vil følge meg og gjerne gi en hilsen i min gjestebok.\n\nMine beste turhilsener til alle\nGunnar','Sverre Pedersen','','publish','open','open','','sverre-pedersen','','','2007-07-22 18:46:53','2007-07-22 16:46:53','',0,'http://falleri.no/2007/07/22/sverre-pedersen/',0,'post','',0),(87,2,'2007-07-31 09:22:59','2007-07-31 07:22:59','Jeg er nå tilbake etter å ha fulgt min svigerfar til hans siste hvilested. Både fra min kones side og resten av familien er alle innstilt på at jeg fortsetter \"Norge på langs\" herfra.\n\n<a href=\"http://www.flickr.com/photos/gisle/929914699/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1129/929914699_1e20dd631f_m.jpg\" width=\"240\" height=\"180\" alt=\"Skotsk langhornet kveg\" /></a> <a href=\"http://home.online.no/~sjoaraft/\">Lusæter gård</a> vil jeg gjerne få lov til å fortelle mere i fra. Gjestfriheten jeg ble møtt av skrev jeg om i min forrige oppdatering. Mer detaljert om gården er Lusæter gård den høyest beliggende fjellgården i Norge med fortsatt produksjon av melk. Kunstgjødsel brukes i beskjeden grad og dyrene går for en stor del på utmarksbeite. Her er det det skotske langhornede kveget har sin styrke. Disse dyrene utnytter utmarksbeitet maksimalt og kan gå ute hele året. Gårdens besetnining på i overkant av 100 dyr besto av vel 50 skotsk langhornet kveg, 45 melkekyr og kalver av norske raser, villsau og villsvin. De eneste dyra som ikke gikk ute på fritt beite var svina. Oppdyrket eng er på 250 dekar og brukes til grasproduksjon. Utmarksbeitet er fellesbeite for flere gårder i Heidalen her i øvre Gudbrandsdalen.\n\nDet er ikke bare gårdsdrifta som er anderledes enn de fleste. Eieren Erling Lusæter og hans samboer Hilde Engvik er meget sosialt beviste mennesker. De tok seg av ungdommer med problemer i sitt daglige liv og ga dem en ballast av varme og menneskelig fasthet. Lars arbeidet for tiden her på gården. Gutten måtte opp tidlig om morgenen og fikk sine arbeidsoppgaver. Oppgaver han kunne mestre og tid uten mas for å få det til. Ingen hang over ryggen på Lars og gutten var åpen og glad overfor meg. Jeg skal heller ikke glemme Igor Gulitskyy, praktikant fra Ukrainia. Med landbruksskole fra sitt hjemland, et halvt år ved en engelsk farm, samme ved en dansk grisefarm og nå her for seks måneder ved en norsk fjellgård med utmarsksbeite og økologisk drift. Igor er en ungdom jeg er glad for å ha fått lov til treffe og få prate med. I de dagene jeg har vært her på Lusæter gård er det Igor som har stått for middagsmaten og det ble ukainske retter på bordet. Yngste sønnen, Thor Ragnar var den som sammen med faren, Hilde og Igor drev gården. I tillegg hjalp også Lars til.\n\nVindmølla er den høyest liggende vindmølla på fastlandet i Europa og produserer strøm tilsvarende forbruket i 30 eneboliger. Det gården ikke brukte selv av strøm ble levert ut på sentralnettet. Nå arbeidet Erling med å få Hydros pilotanlegg med kraft fra vindmølle lagret som hydrogen flyttet hit opp til Lusæter gård. Hydrogenet er planlagt brukt som drivstoff til gårdens traktorer og biler. Om ikke dette skulle være nok prøver Erling og Hilde å få statens godkjenning til bruk av luftskip i noen uker hver sommer. Planen er å kunne får departementets tillatelse til å frakte turister inn over de nærliggende naturparker. Når tilfeldigheten fører meg til en slik norsk fjellgård må det være lett å forstå at jeg betegner Lusæter fjellgård som en meget spesiell opplevelse. Jeg ønsker Erling og Hilde alt godt med det arbeidet de driver med.','Lusæter gård','','publish','open','open','','lus%c3%a6ter-gard','','','2007-07-31 09:22:59','2007-07-31 07:22:59','',0,'http://falleri.no/2007/07/31/lus%c3%a6ter-gard/',0,'post','',0),(88,2,'2007-08-01 13:36:43','2007-08-01 11:36:43','Turen fra Lusæter gård forbi Lalm og opp til Svarttjønne er mest som en transportetappe å regne. Fra Lusæter bar det uten kart først langs en høgspenttrasé for så å treffe på den tidligere omtalte \"kustien\" og følge denne til vei. Jeg gikk etter en muntlig beskrivelse fram til veikrysset ved Bjølstadvatnet. Her gjør jeg nok en gang det dumme å ikke kontrollere med kartet og sjekke riktig kompasskurs. Først etter en tre kvarters gange mot Heidal begynner jeg å ane feiltagelsen. Det er bare å innrømme feiltagelsen og snu, men egentlig har det ikke skjedd noe annet enn at jeg kommer litt senere til Lalm enn jeg ellers ville gjort.\n\nFra <a href=\"http://www.lalm.no/\">Lalm</a> og opp lia tydet kartet på en rimelig bratt stigning. Før jeg la i veg pratet jeg heldigvis med lokale kjentfolk og slapp mye lettere fra det enn jeg hadde regnet med. En godt opparbeidet og velbukt sti førte meg innover Sveahaugen til Svarttjønne. Tjønna var dessverre grunn og med siv rundt kantene og egnet seg lite til slukfiske for en ukjent. Natten i teltet var noe kald og regnfull og krevde stillongs og ekstra genser på. Morgenen derimot opprant med sol og varme. Teltet ble stappet tørt ned i sekken.','Lusæter gård - Telt v/Svarttjønne','','publish','open','open','','lus%c3%a6ter-gard-telt-vsvarttj%c3%b8nne','','','2007-08-01 13:36:43','2007-08-01 11:36:43','',0,'http://falleri.no/2007/08/01/lus%c3%a6ter-gard-telt-vsvarttj%c3%b8nne/',0,'post','',0),(89,2,'2007-08-02 13:46:54','2007-08-02 11:46:54','I Folldal venter min svigerinne Ruth og Magnar på meg. Skal jeg treffe dem må jeg være framme i Folldal på lørdag og nå øyner jeg muligheten. Legger friskt i veg ned til Nord-Sel. Jeg er helt alene menneske inne i dette skog- og fjellorådet og selv om jeg beveger meg på 800 moh er det stor og fin furuskog her oppe. Alt er stille og rolig, varmt er været og svetten renner godt. Litt ned mot Nord-Sel kommer jeg for første gang så langt på rennende kildevann. Vannet blir ledet i gamle, kløyvde og uthulte trerenner til noen gårder lengre nede. Et friskt og nydelig kaldt vann. Stopper på butikken på Nord-Sel for litt drikke og frukt. Får likså lokal anbefaling om å gå Gammellia opp Rosten til Høvringen. Første del av veien/stien er meget god og lettgått. Samtidig er den et interessant studie av gammelt byggearbeid i bratt lende. De siste 1,5 kilometrene var derimot gjengrodd og noe vanskelig å ta seg fram på. Med litt forsiktighet gikk det egentlig greit også på dette stykket. Blir det derimot ikke ryddet vil det nok ikke gå lang tid før det blir helt ufremkommelig på dette strekket. Verten på <a href=\"http://www.oigardseter.no/\">Øigarden</a> fortalte at tilgroing skjer over hele området fordi det i dag er det få dyr som beiter heroppe. Samtidig er det heller ikke behov for å hugge ved til ysting og varme så all rydding må skje ved lokal dugnad.\n\nLes mer om Høvringen på <a href=\"http://www.hovringen.no\">www.hovringen.no</a>. En god hjemmeside for dette området.','Svarttjønne - Øigardsseter','','publish','open','open','','svarttj%c3%b8nne-%c3%b8igardsseter','','','2007-08-02 13:46:54','2007-08-02 11:46:54','',0,'http://falleri.no/2007/08/02/svarttj%c3%b8nne-%c3%b8igardsseter/',0,'post','',0),(90,2,'2007-08-03 23:25:22','2007-08-03 21:25:22','Etter en skikkelig god natts søvn og deretter frokost på seteren legger jeg ut på den lange etappen til Grimsdalen. Like ovenfor Øigardsseter er det ut i snaufjellet og herfra og fram er jeg ovenfor skoggrensa hele tiden. Turen går gjennom Kvannslådalen, over Sletthøe, passerer øverst i Haverdalen, forbi Storrvatnet og ned til elva i Grimsdalen. På det høyeste er jeg oppe på knappe 1580 moh. Dovre nasjonalpark er i dette området for det meste runde rolige fjell. Det er karrig her oppe og med en meget beskjeden vegetasjon. Reinlav og stein er det det er mest av. Det er også tørt og lite med rennende vann her jeg går. Derimot treffer jeg på ganske mange andre vandrere. To av disse vil jeg spesielt nevne, min bærer og en vakker hulder. Bæreren, Jonny (beklager om navnet er feilskrevet) tar meg igjen et stykke inne i Kvannslådalen og insisterer på å bære sekken min et stykke. Bare dumt å avslå et slikt tilbud. En god del senere etter at min bærer har snudd treffer jeg på huldra. Har akkurat krysset veien oppe i Haverdalen og går i egne tanker og ser meg ikke rundt. Plutselig er det en lys, kvittrene stemme som kaller på meg. Først ser jeg ingen, men så oppe på en berghammer sitter hun. Jeg må bare opp å hilse på. Det var en tysk ungjente ute på telttur. Hun jobbet som budeie på en seter i nærheten av Kvam. Nå hadde hun noen fridager og tok disse inne i nasjonalparken. Dessverre glemte jeg å få tatt et bilde av \"huldra\" mi, kanskje av forfjamselse, jeg vet ikke riktig.\n\nDe siste kilometerne forbi Storrvatnet og til Grimsdalen gikk i nydelig kveldsol og passe varmt og godt. Riktig en fin kveld til telting og kveldsmat. Teltet ble slått opp på en myk seng av reinlav og mose og med elva like nedenfor. I morgen blir jeg hentet av min svigerinner Ruth og Magnar for å besøke dem i Folldalen.','Øigardsseter - Telt i Grimsdalen','','publish','open','open','','%c3%b8igardsseter-telt-i-grimsdalen','','','2007-08-03 23:25:22','2007-08-03 21:25:22','',0,'http://falleri.no/2007/08/03/%c3%b8igardsseter-telt-i-grimsdalen/',0,'post','',0),(91,2,'2007-08-05 18:28:45','2007-08-05 16:28:45','I dag mandag kjørte Magnar meg tilbake og inn i Grimsdalen. Med lett sekk gikk jeg fra Tverrlisætre og over fjellet til Folldalen. Dermed er også dette mellomliggende stykket dekket. Ingen fusk fra min side, heller noen kilometer mere enn noen for lite.\n\nPå lørdagskvelden ble det hos Ruth og Magnar som lovet servert hjortestek med masse god rødvin til. Kaffe og cognac med trivelig prat utover kvelden ble det også. Søndagen forløp stille og rolig og ble en dag for hvile og hjemmekos. I dag mandag har jeg som sagt gått det manglende mellomstykke fra Grimsdalen til Folldalen. 17 km med lett sekk er egentlig bare å regne som kos. Terrenget var som jeg ellers har opplevd det i dette området: småkupert, innimellom nærmest slett og meget lettgått. Bortsett fra tildels store flater dekket med reinlav er det karrig her inne og det er lite rennende vann. Slik er det jeg oppfatter denne delen av Dovre. Derimot kunne et par jeg møtte vest om Mesæterhøe fortelle at året i år var et frodig år i forholdt til det normale. Vanligvis er sommerene mye tørrere her inne enn dette årets regnfulle sommer og regn som har gjort det grønnere enn det vanligvis er.\n\nEn glipp har skjedd i min planlegging for turen fra Folldal til Røros. Tre kart for dette strekket var ikke blitt sendt og lå igjen i Mo i Rana. Kartene sendes ekspress i dag til Folldal, men er neppe her før onsdag. Morgendagen blir derfor brukt til å hjelpe Magnar med skraping og maling av fjøset på gården og først onsdag kan jeg starte i retning Røros.','Tverrlisætre - Folldal','','publish','open','open','','tverrlis%c3%a6tre-folldal','','','2007-08-05 18:28:45','2007-08-05 16:28:45','',0,'http://falleri.no/2007/08/05/tverrlis%c3%a6tre-folldal/',0,'post','',0),(92,1,'2007-08-06 22:42:43','2007-08-06 20:42:43','<a href=\"http://farm2.static.flickr.com/1040/1031999622_f35383a094_o.jpg\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1040/1031999622_49cf5b69fb_m.jpg\" width=\"180\" height=\"240\" alt=\"Gudbrandsdølen Dagningen 3. august 2007\" /></a> <a href=\"http://www.gd.no/\">Gudbrandsdølen Dagningen</a> hadde 3. august fylt en side med et oppslag fra Gunnars tur:\n\n<b><i>Første 70-åring til Nordkapp</i></b>\n<i>Langtur: Gunnar Aas er den første 70-åringen som går Norge på langs. Akkurat nå trasker han i norddalen</i>\n<i>[...]</i>','Gudbrandsdølen Dagningen','','publish','open','open','','gudbrandsd%c3%b8len-dagningen','','','2007-08-06 22:42:43','2007-08-06 20:42:43','',0,'http://falleri.no/2007/08/06/gudbrandsd%c3%b8len-dagningen/',0,'post','',1),(93,2,'2007-08-08 14:12:34','2007-08-08 12:12:34','Jeg starter i elvetida og går mot Einundalen. Opp fra Folldal kommer jeg inn i et virvar av nylagte veier til et nytt hyttefelet eller kanskje heller en hytteby. Jeg roter litt blant nylagte veier før jeg finner den stien jeg har planlagt å gå. Inne på den rette stien går det greit innover til Gortjønne og videre opp Brennknattskaret. Herfra er det et virvar av sauetråkk, men retningen mot setergrenda i Kakkeldalen er grei nok den. Hittil har terrenget vært mye likt det jeg tidligere har gått i Rondane, men fra Kakkeldalen blir det noe frodigere. Fjellet over til Einundalen og Romsdalssætra er slett og lettgått. Terrenget og fjellene rundt er farget av store mengder med reinlav og er gråhvitt å se til. Jeg går forbi Stugulisætra og telter på en høyde over elva Einunna like ved Romsdalsætra. Sætra var opprinnelig opparbeidet som gård av en romsdaling på 1800 tallet, derfor navnet Romsdalsætra. Jeg legger meg tidlig med tanke på neste dag og prøver derfor ikke fisket i elva som flyter like nedenfor meg.','Folldalen - Telt v/Romsdalsætra','','publish','open','open','','folldalen-telt-vromsdals%c3%a6tra','','','2007-08-08 14:12:34','2007-08-08 12:12:34','',0,'http://falleri.no/2007/08/08/folldalen-telt-vromsdals%c3%a6tra/',0,'post','',1),(94,2,'2007-08-09 14:14:13','2007-08-09 12:14:13','Starter tidlig, klokken ti med tanke på den lange etappen fram til Savalen. Den var beregnet til 28 km, men ble nok nærmere tretti. Fra den siste leirplassen bærer det ut i et heller ulendt terreng uten stier på den første delen. Massevis av kratt og kjerr og for første gang siden Lindesnes skremmer jeg opp mye ryper. Det er artig å se at rypa også kan finnes i mengder her syd i landet.\n\nFram til Kløttvangen og sætergrenda under Gruvkløtten har fjellskoa vært på hele veien fra Folldal, men her blir det skifte til MBT-sko og gange på grusvei resten av turen fram til Savalen. Det er ikke mye folk jeg har truffet hittil i dette området. Et eldre par oppe i fjellet over for Kløttvangen. Deretter tomme veier med knapt en bil å se. I sørenden av Savalen er jeg innom Garborgtunet  Kolbotn og tar noen bilder, men også her er jeg alene. Noe lengre fram mot Savalen tar jeg til å bli sliten og prøver å finne en bra teltplass; enten mangler godt og rent vann i rimelig avstand eller så er terrenget lite egnet til telting. Muligens jeg som nordlending er forvent. I alle fall ender det med at natten tilbringes på <a href=\"http://www.savalen.no/\">Savalen Hotell</a>; ikke noe dårlig valg. Jeg ble motatt på en hjertelig måte og dagen etter ble en PC stilt til fri disposisjon for mine oppdateringer.\n\nHotellet har for noen år utvidet med et Nisseland. Ikke for å konkurrere med Rovaniemi, men de tilbyr et annet og mindre kommersielt opplegg. Egen hjemmeside har også nissen fått, <a href=\"http://www.julenissen.no\">www.julenissen.no</a>. Turen vider til Røros har jeg god tid på. I dag er det litt utpå fredagen og jeg tar først sikte på å være der i løpet av tirsdagen.','Romsdalsætra - Savalen','','publish','open','open','','romsdals%c3%a6tra-savalen','','','2007-08-09 14:14:13','2007-08-09 12:14:13','',0,'http://falleri.no/2007/08/09/romsdals%c3%a6tra-savalen/',0,'post','',1),(95,2,'2007-08-10 10:04:09','2007-08-10 08:04:09','Etter en lang etappe dagen før og oppdatering av blogg og bilder ved på hotellets PC ble dagens vandring relativt kort. Fra Savalen  til teltplassen ved Kroken er det bare 6, maksimum 7 km. Teltet ble slått opp like ved bredden av elva Tunna.\n\nFørste del av den valgte stien var godt oppgått fram til Hemstadtjørne. Stien var klassert som natursti med flere interresante oppslag. Tre av disse har jeg tatt foto av. Etter Hemstadtjørna var det slutt på stien, men i og med kort vei og til dels snaufjell skapte ikke dette noe problem. Nede ved Kroken fant jeg, som sagt en god teltplass like ved elvebredden.  Jeg hadde knapt fått etablert meg da et par kom roende oppover elva. De kunnne blant annet fortelle at i elva var det bare meget små ørret å få og at den siste gården var blitt fraflyttet alt i 1951. I sin tid var det i bygda 5 bruk på det meste og med 200-250 dyr, stort og smått iberegnet. Skogen var sterkt preget av den tidliger driften på Røros. All furuskogen var uthogd og bjørka hadde innvandret og tatt overhånd. Jeg ble innvitert inn på gården Kroken på kakao og noe å bite i. Gården var nå kun i bruk som feriested. Eldbjørg Sletten var født på Kroken og mannen Svein Sletten var opprinnelig folldøling og hadde jobbet som landbrukssjef i Sel kommune. Begge var nyss blitt pensjonister. Et ordtak sier at verden er mindre enn du tror og her stemte det. Svein hadde gått landbruksskole sammen med Magnar Dahlen som jeg nettopp hadde besøkt i Folldalen. I tillegg var han en av de personene Erling Lusæter hadde hatt sine kontroverser med. (Se <a href=\"http://falleri.no/2007/07/31/lus%c3%a6ter-gard/\">bloggen fra Lusæter gard</a> i juli). Dog skal Svein Sletten ha ære av å gi Erling Lusæter skussmål for iniativ. Svein sa rett ut at mennesker som Erling med ideer og pågangsmot er noe alle bygder trenger.\n\nPå turen hjem til teltet var det blitt ganske mørkt. Det underlige skjedde at sauer som i dagslys er ganske skye nå bare var nyskjerige og flere av dem kom helt bortil og luktet på en utstrakt hånd. Natten ble regnfull og likeså neste dag.','Savalen - Telt v/Kroken','','publish','open','open','','savalen-telt-vkroken','','','2007-08-10 10:04:09','2007-08-10 08:04:09','',0,'http://falleri.no/2007/08/10/savalen-telt-vkroken/',0,'post','',0),(96,2,'2007-08-11 10:14:33','2007-08-11 08:14:33','Dette ble en dag hvor hele etappen kun gikk på vei. For det meste på gruslagte bygdeveier bortsett fra noen ganske få, kanskje så mye som 6 km på E3. Stort mer er det egentlig ikke å si om denne dagen. Ikke møtte jeg andre mennesker og bortsett fra på E3 var det bare et par biler som passerte meg. Jeg passerte Lonsjøen, helt sivbevokst og knapt mulig å komme til uten med båt. Framme ved Skardstjønna var jeg dyvåt etter å ha gått i regn det meste av dagen. Dette tjønna var også helt sivbevokst og vanskelig å komme til. Teltplassen jeg fant var derimot helt fin og skrånet lett til tre sider slik at regnvannet rant bort fra teltet. Kvelden og natten ble heller fuktig og det var bare å bli i teltet til langt ut på neste dag.','Kroken - Telt v/Skardstjønna','','publish','open','open','','kroken-telt-vskardstj%c3%b8nna','','','2007-08-11 10:14:33','2007-08-11 08:14:33','',0,'http://falleri.no/2007/08/11/kroken-telt-vskardstj%c3%b8nna/',0,'post','',0),(97,2,'2007-08-12 10:16:39','2007-08-12 08:16:39','Dette var dagen da alt var vått og fuktig. Først litt ut på dagen ble frokostgrøten kokt inne i forteltet og sekken pakket på samme sted. Alt som ennå var tørt i en indre vanntett storpose og deretter et dyvått telt og noen ditto klær stappet inn på toppen i sekken. Jeg la i veg i regn og gikk for det meste i regn ned forbi Vingelen og ned til Tolga. Lunch/middag ble inntatt på en kafeteria og deretter 8 km på R30 til en campinghytte på Håmmålvoll.\n\nNå var det endelig blitt tørt i været. Det våte teltet ble satt opp til tørking, mann samt vått og skittent ulltøy vasket og et sent måltid inntatt. Egentlig skulle jeg etter opprinnelig plan ikke vært her, men tatt veien opp forbi Vingelen til Dalsbygda og Særersjøen for deretter å gå til Røros. Jeg valgte anderledes etter å ha fått nok av sivbevokste vann, tett skog og kratt uten stier. Greit nok, bygdene her er meget vakre med gamle maleriske tømmerbygninger. Terrenget utenfor har jeg fått nok av og kommer heller en dag tidligere til Røros. Jeg gleder meg alt til grenseområdene i Sør- og Nord-Trøndelag.','Skardstjønna - Håmmålvoll','','publish','open','open','','skardstj%c3%b8nna-hammalvoll','','','2007-08-12 10:16:39','2007-08-12 08:16:39','',0,'http://falleri.no/2007/08/12/skardstj%c3%b8nna-hammalvoll/',0,'post','',0),(98,2,'2007-08-13 20:34:02','2007-08-13 18:34:02','Dagen starter med noen skikkelige regnskyll. Heldigvis var teltet tatt tørt inn kvelden før og de nyvaskede klærne hang til tørk under tak. 25 km var jeg fortalt det var etter veien til Røros og med Os liggende omtrent midtveis.\n\nMed regn i lufta og mørke regntunge skyer tar jeg på regntøy som en ren nødløsning. Selvsagt skjer det vanlige. Med regntøy på blir du minst like våt av svette som om du gikk uten. Ikke blir du tørr mellom bygene heller. Etter noen kilometer gjorde jeg det eneste riktige, pakket regntøyet ned i sekken og ullklærne fikk ta i mot regnet. Med oppholdsvær de siste fire kilometrene var jeg rimelig tørr da jeg kom fram til Os. En stor hamburger på stedets kafeteria og et par epler ble det til lunch. Ikke før var jeg kommet under tak da det tok til å regne noe enormt. Du snakker om at regnet kom i bøtter og spann. Ventet i en times tid til det verste gir seg og legger i veg i et ikke alt for kraftig regn. Halvveis mellom Os og Røros lysner det og noen solgløtt tørker ut det meste av vannet i klærne.\n\nFramme i <a href=\"http://en.wikipedia.org/wiki/R%C3%B8ros\">Røros</a>, spør jeg en ung dame om hun kan anbefale et godt og rimelig sted for overnatting. \"Prøv i paralellgata\", sier hun og \"se om du får plass i <a href=\"http://www.froyashus.no/\">Frøyas hus</a>. Det er noe av det mest spennende vi har her på Røros og er noe for seg selv\". Vel framme ble jeg tatt imot av stedets eier, Ellen på en hjertelig måte. Gamle tømmerbygninger fra 1742 omslutter en indre gårdsplass. En gårdsplass så spesiell at jeg først bare ble stående å måpe. Både den og førsteetagen brukes til kafé og er det mest spesielle du finner på Røros. Kan en si at et sted har sjel, må det være noe som Frøyas hus. Frøyas hus og Røros i seg selv gjorde sitt til at jeg ble her en dag lengre enn planlagt.','Håmmålvoll - Røros','','publish','open','open','','hammalvoll-r%c3%b8ros','','','2007-08-13 20:34:02','2007-08-13 18:34:02','',0,'http://falleri.no/2007/08/13/hammalvoll-r%c3%b8ros/',0,'post','',2),(99,2,'2007-08-15 22:16:00','2007-08-15 20:16:00','Etter en god frokost hos Ellen på Frøyas Hus og etter også fått gjort unna de siste gjøremålene la jeg i veg fra Røros mot Glåmos. Gikk den gamle seterveien øst om Enasvola og Fjellkjerka. På høydene oppover fikk jeg en fantastisk utsikt utover Røros og de gamle gruveanleggene. Det blåste kraftig fra sørvest og først nede i skogsbeltet og ved de første setrene ble litt ly. Det så ellers ut for at det var slutt med vanlig seterdrift i dette området. De fleste setrene bar preg av, enten å være tatt til fritidsboliger eller at de rett og slett var i forfall. En eller to ble muligens brukt til alternativ drift. Nede på hovedveien mot Glåmos gikk jeg ytterligere gode to kilometer. Ved en gård spurte jeg meg for etter teltplass. Ble av bonden anbefalt å ta en sidevei litt lengre ramme. Bare skreve over en sperrebom og slå opp teltet på noen lyng- og furukledte sandmoer i området. Det ble en fin kveld ved teltet. \"Middagen\" kokt og spist, kaffen ferdig akkurat før det vanlige kveldsregnet kom. Det var bare å lukke teltet, drikke kaffen og ta kvelden.','Røros - Telt ved Glåmos','','publish','open','open','','r%c3%b8ros-telt-ved-glamos','','','2007-08-15 22:16:00','2007-08-15 20:16:00','',0,'http://falleri.no/2007/08/15/r%c3%b8ros-telt-ved-glamos/',0,'post','',0),(100,2,'2007-08-16 22:18:47','2007-08-16 20:18:47','Med litt vind og åpen furuskog tørket teltet rimelig fort etter nattens regn. Ikke alt for sent denne formiddagen tok jeg fatt på de siste kilometrene inn til Glåmos. Skulle innom Samvirkelaget for å supplere beholdningen av proviant for turen videre. I vegkrysset var det ikke merket vei mot sentrum og butikk. Det ble derfor noen hundre meter i feil retning for så å snu og gå tilbake.\n\nDen ekstra tida ble vel egentlig til et lykketreff. Nærmere butikken kommer kommer det ut flere unger og en voksen framfor meg. Disse okkuperer det eneste bordet og benkene utenfor butikken, men trenger seg sammen og gir plass til meg. Vel fått av meg sekk og lagt fra meg kartet blir de nærmeste meget interreserte og begynnet å titte på kartet. Jeg må forklare hva mine røde merker betyr og hvor jeg kommer fra og hvor jeg skal. Det hele ender opp med at også kompasset må frem og en enkel undervisning i bruk av kart og kompass blir resultatet. Ungene prøver seg både med å ta ut kurs og å gå etter denne. Alt under masser av prat, ståk og latter. En skikkelig trivelig stund. Ungene og den voksne var fra Glåmos SFO og var ute på en liten tur.\n\nNoe forsinket butikkhandel og ute igjen kommer akkurat et eldre ektepar kjørende. De spør om jeg er en av de to tyskerne de er kommet for å hente. Paret driver <a href=\"http://www.svt.ntnu.no/psy/Bjarne.Fjeldsenden/Billeder/Vinilla/InfoBilleder25062003.html\">Vinilla Turisthytte</a> over fjellet ved Storelvvollen. Om det skulle passe med mine planer og skulle trenge hus har de akkurat for øyeblikket lagt igjen en nøkkel ved Vinilla. Det er bare å ta seg inn og ordne seg på beste måte. Du snakker om gjestfrihet og tillit. Noe forsinket tar jeg fatt på den lange turen langs Aursunden og fram til teltplass like ved Evavollen.','Glåmos - Telt ved Evavollen/Aursunden','','publish','open','open','','glamos-telt-ved-evavollenaursunden','','','2007-08-16 22:18:47','2007-08-16 20:18:47','',0,'http://falleri.no/2007/08/16/glamos-telt-ved-evavollenaursunden/',0,'post','',0),(101,2,'2007-08-17 08:59:32','2007-08-17 06:59:32','Nok en morgen med lett regn og i tillegg ganske så kraftig vind. Takknemmelig for vinden, denne gjorde sitt til at teltet ikke var altfor vått. Etter hvert klarnet det opp, vinden fortsatte og teltet ble helt tørt. Oppbrudd og veien videre inn til Jamtvollen. Der fikk jeg levert fra meg mine \"veisko\" i en ferdig frankert posteske og endelig etter flere dager på bil- og seterveier kunne jeg legge til fjells igjen.\n\nFra Jamtvollen fulgte jeg delvis brukbar sti opp til Klinkengruva. Flere bilder er tatt fra denne. Det er nesten ikke til å fatte for oss mennesker i dag hvilken innsats og for et slit det må hav vært å drive ut malmen i et helt uveisomt fjellområde. Denne dagen var forresten den første helt siden Lindesnes Fyr hvor jeg ikke har vært helt i form. Litt sting for brystet og ondt når jeg hostet. Det var rett og slett slitsomt å gå. Turen videre opp til Fjellsjøen ble derfor heller tung. Vel oppe blåste det kraftig og det tetnet til og faren for regn økte. Jeg fant en brukbar teltplass på en liten høyde like ved vannet. En tørr og fin plass og hvor eventuelt regnvann kunne renne bort fra teltet. Været bød heller ikke til fiske, kalt og hustrig. Det ble mat kokt i forteltet og tidlig kveld. Natten gjennom blåste og regnet det skikkelig mye. Morgenen var våt, med sterk vind fra sørvest og innbød ikke til en ekstra dag ved Fjellsjøen. Det ble å pakke teltet og traske videre.','Evavollen - Telt ved Fjellsjøen','','publish','open','open','','evavollen-telt-ved-fjellsj%c3%b8en','','','2007-08-17 08:59:32','2007-08-17 06:59:32','',0,'http://falleri.no/2007/08/17/evavollen-telt-ved-fjellsj%c3%b8en/',0,'post','',0),(102,2,'2007-08-18 09:06:22','2007-08-18 07:06:22','Fortsatt ufyselig og kalt, og som sagt, ikke vær til en dag ved vannet. Jeg var forsatt ikke i slag og bestemte meg å gå ned til <a href=\"http://www.svt.ntnu.no/psy/Bjarne.Fjeldsenden/Billeder/Vinilla/InfoBilleder25062003.html\">Vinilla Turisthytte</a> og benytte meg av tilbudet om overnatting i hus. Det er forholdsvis kort vei fra Fjellsjøen og ned til Storelvvollen hvor hytta ligger. Jeg orienterte helt riktig og kom rett ned på Vinilla. Like før jeg har tatt meg inn svinger en bil opp på tunet og der kommer Turid Hørsdal, eier og vertinne på Vinilla. Et hyggelig gjentreff og etter middag blir kvelden ekstra trivelig. Kaffekjelen settes på og jeg får historien til stedet.\n\n<blockquote>Bonden på Storelvvollen seter bygde huset i 1880-åra da de første turistene tok til å komme. Siden ble stedet gradvis påbygd og utvidet til dagens størrelse. I første etasje, kjøkken, to stuer og et kammers. I andre, forgang, korridor og åtte eller ti separate soverom. God plass til sikkert et tyvetalls mennesker. Etter som årene gikk fant besøkende og turister andre veier og i 25 år sto stedet ubrukt og til forfall. For fem år siden kom Turid tilfeldig forbi, tittet inn et vindu og forelsket seg spontant i stedet.  Hun og mannen, Bjarne Fjellsenden fikk kjøpt stedet og satte i gang restaureringen. Ved hjelp av en innleid byggmester, eget arbeid og dugnad fra venner og bekjente tok det bare tre måneder med hardt arbeid og det grøvste var gjort. Siden 2002 har stedet vært gjenåpnet og i drift i sommermånedene.</blockquote>\n\nEn del detaljer gjenstår, men stedet er alt meget trivelig og malerisk. Litt skjevt og skakt, gamle tømmervegger med glød, vel avstemte farger. Absolutt et sted og omgivelser verdt å besøke. I morgen går turen videre mot Vekterhaugen. Muligens med en telting underveis.','Fjellsjøen - Vinilla Turisthytte, Storelvvollen','','publish','open','open','','fjellsj%c3%b8en-vinilla-turisthytte-storelvvollen','','','2007-08-18 09:06:22','2007-08-18 07:06:22','',0,'http://falleri.no/2007/08/18/fjellsj%c3%b8en-vinilla-turisthytte-storelvvollen/',0,'post','',0),(103,2,'2007-08-21 12:33:48','2007-08-21 10:33:48','Det ble nok en ekstra dag på <a href=\"http://www.svt.ntnu.no/psy/Bjarne.Fjeldsenden/Billeder/Vinilla/InfoBilleder25062003.html\">Vinilla Turisthytte</a>.  Ingen grunn til å haste fra en slik trivelig vertinne og et slikt sjelfullt sted. En passe sen og lang frokost, en rolig formiddagstund, kladd til bloggen og tiden gikk. Turid drar ut en tur for å se etter multer og jeg tar med spann for å plukke sopp. Jeg hadde sett store mengder med kantareller da jeg kom ned fra Fjellsjøen. Turids moltefangst ble heller skrall, men på mindre enn to timer plukket jeg sikkert en åtte-ti liter med store fine kantareller. Det ble middag med masser av stekte kantareller, skinke- og baconomelett, salat, hjembakt brød og en meget god sisilliansk hvitvin. I mer enn to timer satt vi og nødt maten og pratet om løst og fast. Etterpå fortsatte kvelden foran en god gammel åpen peis med kaffe, avslapping og videre samtaler. Nærmere tolv tok vi kvelden. Som sagt det ble en ekstra dag på Vinilla og først tirsdag kom jeg meg i veg mot Væktarhaugen.\n\nAnnet var vel ikke å vente, klokken ble halvett før jeg trasket i veg etter mange gode klemmer. Gikk på asfalt de første tre kilometrene opp til Øvre Fjellheim. Derfra fører en tidligere DNT-sti over til Stuggudalen. Stien er for det meste god og lettgått, spesielt i snaufjellet. Landskapet begynte nesten å få noe \"nordnorskt\" over seg og jeg føler meg nesten som hjemme. Det er en del ryper å se og mye tamrein på dette strekket. På turen ned mot Stuggudalen rett øst om Tjønndalstjønna, ja det er riktig navn, kommer jeg på et opplysningsskilt: <em>\"Med bil over Tydalsfjella i 1928\"</em>. Det fremgår at med en <a href=\"http://en.wikipedia.org/wiki/Ford_Model_T\">T-Ford</a> tok de seg fram fra Storelvvollen og ned til Stuggudalen. Utenfor vei, gjennom skog og over fjell. Til hjelp hadde de øks og sag, kjettinger og fire planker. Eieren av bilen hadde vist også veddet en hel gård på at han skulle klare turen over fjellene. Gården beholdt han.\n\nDet siste stykket opp til Væktarhaugen gikk jeg på vei. Der kom jeg fram til en lukket og låst turisthytte. Det ble telting på vollen og mat fra egen beholdning. For første gang siden Sørlandet våkner jeg til en helt tørr dag. Ikke en gang dugg på teltet eller i gresset. En deilig dag og formiddagen brukt til oppdatering av bloggen.','Vinilla - Telt ved Væktarhaugen','','publish','open','open','','vinilla-telt-ved-vektarhaugen','','','2007-08-21 12:33:48','2007-08-21 10:33:48','',0,'http://falleri.no/2007/08/21/vinilla-telt-ved-vektarhaugen/',0,'post','',0),(104,2,'2007-08-22 11:54:02','2007-08-22 09:54:02','Bloggen ble i dag oppdatert for alle dagene etter Røros. Uvergelig tar dette sin tid og det ble en ekstra sen start på turen til Nedalshytta. Så vidt sent at jeg regnet med små muligheter for middag og varm mat på den betjente hytta. Turiststien over til Nedalshytta er mest som en transportetappe å regne. Et greit åpent terreng med en del bjørkeskog og myrer, men for det meste godt fjellterreng. På turen, beregnet til 18 km traff jeg ikke på et eneste menneske. Heller ikke noen rein var å se og knapt nok en fugl. Merkverdig, spør du meg. Det meste av denne stien er dårlig merket og vardet og tydelig lite brukt. Trondheim Turistforening har her en jobb å gjøre. Er det en offisiell rute og sti og merket som sådan på kart kreves det skikkelig merking og rydding. Greit nok i sol og klart vær og for en som kan bruke kart og kompass, men for uerfarne folk, ikke bare i dårlig sikt kan den manglende merking gi store problemer. Det er forbausende mange som stoler fullt og helt på turismerkingen for å komme seg fram.\n\nLike før Nedalshytta kom jeg på en liten myr med modne molter. Faktisk de første på hele turen. Det ble noen never med bær rett ned. I tillegg spiste jeg ganske mye blåbær. Både mineraler og vitaminer for flere dager skulle være sikret. På <a href=\"http://www.tt.no/cabin.php?ca_id=5&fo_id=215\">Nedalshytta</a> ble det tross sen ankomst både middag og en par pils. Verten fortalte også hvorfor jeg ikke hadde sett rein i dette området. Jeg passerte akkurat i mellom to reinbeitedistrikter og reinen gjetes og stoppes fra og trekke sydover mot sine vinterbeiter. Sommerbeitene for den reinen ligger lengre nordover. Natten blir tilbragt i hus og forhåpentligvis en tidlig start i morgen.','Væktarhaugen - Nedalshytta','','publish','open','open','','v%c3%a6ktarhaugen-nedalshytta','','','2007-08-22 11:54:02','2007-08-22 09:54:02','',0,'http://falleri.no/2007/08/22/v%c3%a6ktarhaugen-nedalshytta/',0,'post','',0),(105,2,'2007-08-23 10:50:22','2007-08-23 08:50:22','I dag er jeg klar og ferdig pakket allerede før klokken 1000, vel forberedt  for den lange etappen til Storerikvollen.  Kun en siste kopp kaffe på trammen før avgang.  Kokka kommer ut og spør om jeg vil ha en nystekt vaffel til kaffen. Selvsagt sier jeg ja til tilbudet, det ville bare være dumt å si nei. Hva skjer, jo hele personalet kommer ut for å nyte en varm og solrik formiddag. Det blir mer  kaffe, nystekte vafler, nyrørt blåbærsyltetøy og rømme dertil. Den siste kaffekoppen på trammen tok mer enn to timer. Jeg får med meg en hilsen over til Teveltunets eier. Mens vi sitter og nyter stunden kommer en av stedets reineiere og slår seg ned ved bordet. Aslak Brandsfjell viser seg å være i slekt med Lifjellene hjemme på Mo i Rana. Jeg må også hilse til dem. Verten fleiper med meg som har det så \"travelt\" og foreslår og peker ut en fin teltplass hele 150 meter bortenfor der hvor vi sitter. Nok om det, tross alle gode forsetter ble det også i dag en sen start, men uansett en finere og bedre begynnelse kunne jeg ikke hatt.\n\nInnover Sylane går det endelig, og det på en nydelig solrik dag med mange gode minner og hilsener fra Nedalshytta. Et vakkert lett bølgende landskap omkranset av vann og høye fjell. Vakkert og nå med beitende rein overalt, streifende i jakt på sopp. Jeg legger ruta høyt til fjells og ikke etter den nylagte turiststien. Det er best å komme bort fra myr og kratt og opp på snaufjellet. Inne oppunder Bandaklumpen treffer jeg på en birøkter fra Frøya og hans kone. De har tatt seg to dager fri og telter her innpå fjellet. Oppe ved Nordsylan like før Nordre Fiskåa blir det plutselig folksomt. Først vinker jeg til fire jenter. Like etter ser jeg nok fem personer litt lengre nedenfor og til slutt går jeg rett på en hel flokk ungdommer. Det viser seg å være Friluftslinja ved Høgskolen i Nord-Trøndelag som jeg har truffet på, med 24 stykker alt i alt. En av lærerne viser seg å ha kone fra Mo i Rana, felles kjente og nok en hilsen å ta med. Etter anbefaling legger jeg om ruta, dropper Storerikvollen og går fjellene rett nordover oppe i høyden direkte mot Teveltunet.  Jeg regner med to dagsmasjer til og telting innpå fjellet. Like nord om Fiskåhøgda ser jeg en \"Renvaktarstuga\" inne på svensk side av grensen. Den viser seg å ha et åpent vindu og jeg kan ikke se noe galt i å overnatte og koke maten i hus hos broderfolket. Planen for neste dag er å gå til Rangeldalen Naturreservat og prøve fiskelykken i Rangla. Hele tiden vil jeg bevege meg på mellom 800 og 1000 moh.','Nedalshytta - Grensevarde 156','','publish','open','open','','nedalshytta-grensevarde-156','','','2007-08-23 10:50:22','2007-08-23 08:50:22','',0,'http://falleri.no/2007/08/23/nedalshytta-grensevarde-156/',0,'post','',1),(106,2,'2007-08-23 11:45:16','2007-08-23 09:45:16','Jeg har nok gått litt surr i dagene. To plasser på samme dag går nok ikke, men nå er jeg ajour.\n\nAlt tegner til å bli en strålende dag og turen til Bobbeltjønn stikkes i det alt vesentlige ut på svensk side langs etter et reingjerde. Dette følger grensen en stor del av min rute. Innover langs reingjerdet var det tildels god sti, både etter vedlikehold og tråkk etter rein.  Ved Sødra Enbågen treffer jeg på to tyske turister, de eneste menneskene på denne del av turen. To eldre mennesker som kom fra Blåhammaren og skulle til Storerikvollen og deretter til Nedalshytta. De gikk kun de merkede turiststiene og hadde nok måttet passere en del sundtråkkede myrområder. Flere av samme sorten frykter jeg at de kommer til å treffe på.\n\nLitt lengre inn mot Rangeldalen blir det lunch. Først med ½ liter nyrørte molter og deretter min standard rasjon, Bixit, suppe, te og sjokolade. Underveis har jeg skremt opp flere til dels store kull med ryper. Årsfuglene er forbausende store til å være så tidlig på høsten. Opp mot Bobbeltjønna letter det også to tranepar. Det er noen flotte fugler å se til, men sangen til disse fuglene  er ikke direkte vakker. Vel framme ved Bobbeltjønna blir det telting og heller ikke her noe fisking. Tjønna så rett og slett død ut. Med god vind som rev i teltduken ble nok et innemåltid og deretter kvelden.','Grensevarde 156 - Telt ved Bobbeltjønn','','publish','open','open','','grensevarde-156-telt-ved-bobbeltj%c3%b8nn','','','2007-08-23 11:45:16','2007-08-23 09:45:16','',0,'http://falleri.no/2007/08/23/grensevarde-156-telt-ved-bobbeltj%c3%b8nn/',0,'post','',0),(107,2,'2007-08-24 11:49:28','2007-08-24 09:49:28','Starten ble i regn og ruskevær, men etter en par timer ga regnet seg og videre hadde jeg en fin gåtemperatur og stort sett oppholdsvær. Greit terreng fram til Kluksdal. Også her med masser av fine molter og blåbær. Det var ikke å komme fra. Sekken måtte i ny og ne av ryggen og bær plukkes og spises på stedet. Vel framme ved øverste gården i Kluksdal er det ordnet til med bord og grue ved bredden av Klukselva. En riktig fin plass for litt varm mat innen de siste 12-14 kilometren til Teveltunet.\n\nOppover mot Steinkleivtjønna var det litt opp og ned og til dels med noen bratte bakker. Steinkleivtjønna er det første virkelig fine vannet så langt, men tom for proviant var jeg bare nødt til å forlate vannet og ta meg fram til Teveltunet. Synd. Nå fikk jeg virkelig oppleve terrenget som har gitt tjønna, Steinkleivtjønna sitt navn. Fjell og stein, klatring opp og klatring ned. Det er et ganske så ubeskrivelig terreng og må bare oppleves. Vel framme på Teveltunet har jeg rett og slett flaks. Anlegget er stengt for dagen og skal først åpnes neste ettermiddag. Eieren, Nils Petter Haarsaker er tilfeldig innom og jeg får min depoteske og en flott hytte for overnatting. Knapt kommet inn, der starter det med et kraftig regnskyll. Helt etter varslet, med til dels store nedbørsmengder kvelden og natten til fredagen. Får bare håpe at det jeg er blitt fortalt om regn helt fram til onsdagen ikke slår til.','Bobbeltjønn - Teveltunet','','publish','open','open','','bobbeltj%c3%b8nn-teveltunet','','','2007-08-24 11:49:28','2007-08-24 09:49:28','',0,'http://falleri.no/2007/08/24/bobbeltj%c3%b8nn-teveltunet/',0,'post','',0),(108,2,'2007-08-26 09:26:11','2007-08-26 07:26:11','Gårdagen var en heller ufyselig dag med kuling fra nordvest og store nedbørsmengder. Jeg valgte derfor å ta en hviledag på Tevelltunet og avvente og håpe på bedre vær til søndagen. Utover lørdagen bare økte vinden på i styrke og regnet fortsatte å øse ned. Godt å sitte tørt og varmt i hus.\n\nDagen i dag hadde både vinden løyet og regnet gitt seg. Marka og spesielt alle myrene var gjennomtrukket av vann og det ble mye klaskede gange gjennom disse. De 25 kilometrene fram til Angeltjønn gikk greit unna og jeg brukte totalt 7 timer på turen. Stien er jevnt godt merket og følger for det meste grensen mot Sverige og går i høyfjellsterreng. Bare synd det var så pass vått. Heller ikke i dag slapp jeg unna noen heftige regnbyger. Disse sammen med en noe lav temperatur gjorde dagen heller hustrig.\n\n<a href=\"http://www.flickr.com/photos/gisle/1258322213/\"><img align=\"right\" src=\"http://farm2.static.flickr.com/1294/1258322213_9594fe46ed_m.jpg\" width=\"240\" height=\"180\" /></a>På lørdag hadde jeg fått melding fra Øyvind Lillerødvann, en kammerat fra Mo i Rana om at han var i området og lurte på hvor jeg befant meg. Øyvind ville, sammen med noen kompiser  komme til Angeltjønnhytta lørdag og gå videre til  Teveltunet på søndag. Det var bare å gå og glede seg til vi ville møtes. Like før Huva treffes vi. Øyvind kommer da sammen med tre andre og det blir et hjertelig om enn vått møte. Akkurat da silregner det og det blir et stående treff i en femten til tjue minutter med sekken på. Utrolig å treffe venner plutselig og uventet slik  ute i ville fjellet. En times tid senere slutter regnet og jeg får meg noe mat før de siste kilometrene ned til Angeltjønnhytta. Vanlig prossedyre; først er det å få fyr i ovnen, hente vann, skifte til tørre klær, koke kaffe og slappe av mens varmen kommer sigende.\n\nBesøksprotokollen byr på et merkelig sammentreff. Denne dagen har foruten Øyvind og hans tre venner også to andre, Elise S og Harald Nilsen fra  Mo i Rana overnattet og forlatt hytta. Og deretter kommer også jeg  fra Rana. Ikke noen andre dager helt fra nyåret er det registrert en eneste Ranværing. I tillegg finner jeg igjen min \"Norge på langs\"-veninne, Inger Thommesen. Hun forlot hytta 30. juli. Heia Inger, jeg håper du når ditt mål, Nordkapp i løpet av oktober slik du har planlagt. Du er virkelig tøff, lykke til.','Teveltunet - Angeltjønnhytta','','publish','open','open','','teveltunet-angeltj%c3%b8nnhytta','','','2007-08-26 09:26:11','2007-08-26 07:26:11','',0,'http://falleri.no/2007/08/26/teveltunet-angeltj%c3%b8nnhytta/',0,'post','',0),(109,2,'2007-08-27 23:50:53','2007-08-27 21:50:53','Fra i går kveld, natten gjennom og utover formiddagen i dag er det kun måte å snakke om været på og det må sies på nordnorsk: \"Det er et ailerhelvetes vær\". Også uttrykket store nedbørsmengder er her på sin plass.\n\nUansett, jeg skal fram til NTTs hytte Ferslia i dag. Da klokka var tolv fikk været være vær og jeg la i veg over fjellet. I dag fløt det, det blåste og det var kaldt. Som enslig menneske ute i fjellet føler du deg liten under slike forhold. Du har bare en ting å gjøre og det er å gå på og sørge for å holde deg på beina. Nok om det, men etter kartet er det tre elver som skal krysses og ingen er merket med bro eller klopp. Da jeg nærmer meg Synnerelva hører jeg på god avstand først fossedur og deretter et kraftig elvesus. Jeg får bange anelser, men heldigvis er det bro på plass. Elva går brun av flomvann under meg. og uten bro hadde det vært umulig å krysse. Håpet stiger for den neste elva, Nordelva. Også denne  er flomstor, broa er der, men nedbøyd og vannet flommer en 30 cm over brodekket. Heldigvis er det ingen problemer med å komme over.\n\nI dag rant det vann overalt. Det rant nedover fjellsider og gresslier, flate myrer sto mer eller mindre under vann og stiene var blitt til bekker.  Fra oven kom det stadig ny påfylling.  I dette været var det eneste jeg oppserverte av annet liv en spurvehauk, noen sauer og to troster.\n\nForbi Blåberga rundet jeg fjellet på 800 moh. og var nå på det høyeste partiet. Ruta gikk fra nå av  stort sett rett nordover på mellom 600-700 moh. Den siste elva, Kjølhaugåa ser ut til å by på problemer. Her er det ingen bro og elva er flomdiger. Ved det merkede vadet vil nok vannet nå meg til skrittet og muligens høyere. Ikke er jeg særlig hippen på  å kle av meg i dette været, kaldt, regnfullt og blåsende som det er. Nedstrøms blir elva stadig striere, men takk og pris en 150 meter høyere oppe holder det med sko og gamasjer. Ikke helt tørr på beina, men det gikk rimelig bra. De siste kilometrene fram til Ferslia er en kald fornøyelse hvor jeg til dels vasser i vann. I det store og hele har dagen vært nokså våt. \"Skit au\", klokka fem er jeg framme på hytta og korser fingrene for en tørrere dag i morgen. Det trenges da det er hele 27 km til Innstua oppe ved svenskegrensen.','Angeltjønnhytta - Ferslia','','publish','open','open','','angeltj%c3%b8nnhytta-ferslia','','','2007-08-27 23:50:53','2007-08-27 21:50:53','',0,'http://falleri.no/2007/08/28/angeltj%c3%b8nnhytta-ferslia/',0,'post','',0),(110,2,'2007-08-29 10:47:03','2007-08-29 08:47:03','De 27 kilometrene over til Innstua ble en våt og tung tur.  Hele natten hadde det regnet og jeg bokstavelig talt vasset fra Feslia og helt fram. Bare deler av traseen gikk på rabber, berg og vei. Alt det øvrige var for det meste \"flytende\" og må nærmest lignes med et våtmarksområde. I tillegg ble det sludd og kraftige snebyger oppunder Merraskardfjellet. En tøff, men egentlig fasinerende etappe.\n\nRett øst om Sulsjøan går jeg på det første blå  \"Karolineremerket\". Det var her <a href=\"http://sv.wikipedia.org/wiki/Carl_Gustaf_Armfeldt_d.%C3%A4.\">general Armfeld</a> i 1718 kom fra Sverige med sin hær av <a href=\"http://sv.wikipedia.org/wiki/Karoliner\">Karolinere</a> for å besette Trondheim. Byen ble ikke angrepet og hele felttoget endte i tragedien i Tydal- og Rørosfjellene hvor store deler av mennene frøs i hjel. Når jeg går i disse sporene og traktene tumler tankene rundt denne tragedien. Rent maktbegjær hos en konge og noen få samfunnstopper sender nærmere 10.000 mann direkte i døden. Og akkurat her hvor jeg går kom en larmende svensk hær med sine kanoner, hester og tren, med sine soldater og våpen. Kun til egen tragedie og tragedie til de bygdene de gikk gjennom.\n\nLandskapet jeg har passert ville i godt vær vært ideelt for telting og fisking. Mange fine tjønner og vann med godt om fisk. Ved til nying og kveldsbål, gode teltplasser og stor varriasjon i omgivelsene. Dagen i dag derimot fristet ikke til teltslaging og hygge med stang, nykokt ørret, kaffekjele og nying. Det er nok best å komme seg ned det siste stykket til Innstua og opptørking.','Ferslia - Innstua','','publish','open','open','','ferslia-innstua','','','2007-08-29 10:47:03','2007-08-29 08:47:03','',0,'http://falleri.no/2007/08/29/ferslia-innstua/',0,'post','',0),(111,2,'2007-09-01 13:13:24','2007-09-01 11:13:24','Dagen er skyet med litt sol og helt uten nedbør. Det er bare å gjøre seg klar for turen over til Vera og Sveet. Jeg har sjekket på telefon og min depoteske er kommet, men gjestgiveriet er stengt for godt. Derimot er det alltids en hytte å få leid. Etter alt regnet, sne i fjellene og tre til treogenhalv uke forsinket har jeg bestemt meg for å bryte for i år på Sveet. Jeg kommer til å starte opp herfra, forhåpentligvis ved St. Hanstider til neste år. Den endelige beslutningen ble tatt i dag lørdag 1. september. Værgudene er tydeligvis også enig med meg og understreker enigheten med nok en regnfull dag. Samtidig er det kaldt nok til at nedbøren kommer som sne i høyden. Turen hit i går var frydefull. Skyet, med litt sol innimellom. Ikke noe regn og det verste hadde runnet av seg innover myrer og fjellsider. I forhold til de tidligere dagene var det tilnærmet tørt.\n\nTuren på 19-20 km gikk unna på fem timer, noen småpauser inklusive. Det eneste virkelig bløte partiet var i en myrstrekning ned mot Sveet. En merkelig opplevelse. Det var rett og slett tyngre å gå nedover i det våte enn stigningene var i tørt lende. Dette forteller litt om slitet i de foregående dagene da det fløt av vann overalt. Noe vemodig var det å gå så fort gjennom dette området. Fine fiskevann i flotte fjell lå på rekke og rad. Breivatnet, Bellinglunet, Skillevatnet, Storbellingen, Bustadtjønna, Vassdalstjønna, Midtitjønna, Nordre Vassdalstjønna samt alle tjønnene uten navn på kartet. Tre uker tidligere med en fin høst, varme og mørke kvelder, telt, bål og ørret og røye. Nåja, det hjelper ikke å gråte. Det var i går jeg gikk her og nå går turen hjem til Rana og overvintring der.\n\nVi ses til neste år.','Innstua - Sveet/Vera','','publish','open','open','','innstua-sveetvera','','','2007-09-01 13:13:24','2007-09-01 11:13:24','',0,'http://falleri.no/2007/09/01/innstua-sveetvera/',0,'post','',2),(112,2,'2008-05-29 14:24:49','2008-05-29 12:24:49','<strong>Etter vinteren hjemme i Mo i Rana skal det bli godt å ta fatt på resten av turen til Nordkapp</strong>\n\n<strong>Sveet – Sandv./Nordli 20/6 – 30/6</strong>\nLengde: 116 km + 15 %        =  133 km/17 = 8 + 1dager\n\nEtappen starter fra Sveet hvor jeg brøt av turen i fjor. I disse grenseområdene er det mye snaufjell, men også rikt bevokste daler, elver og fiskevann. I det hele en meget vekslende natur. Nå er også min kjære Golden retriever, Najax kommet med. Får håpe det går bedre i år enn i fjor og at Najax får gjort sin nytte som kløvhund og kamerat.\n\n<strong>Depot</strong>\nDepot med proviant og utstyr for etappen har jeg med fra Mo i Rana.\n\n<strong>Kart for etappen </strong>\n1822 IV Vera\n1823 III    Snåsa\n1823 II     Gjevsjøen\n1923 IV    Nordli\n\n<strong>Valgte ruter</strong>\nSiste del med NTT-merkede ruter og hytter i Nord-Trøndelag ble tilbakelagt i fjor. Fra Sveet blir det mer blandet til det etter hvert blir dårligere med stier og merkede ruter. Det gjenstående av den opprinnelige etappe 9 går fra Sveet, opp Ståggådalen til Gaundalen, videre forbi Seterlifjellet, over Hykkelfjellet, ned til Sørnes og veien fram til Nordli. En etappe med mye villmark og mange fine fiskevann.\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\n\n22/6\nFra Sveet til <a href=\"http://www.indre-namdal.no/apps/otiin/internett/otiinbip.nsf/id/DA79BAABE3FC4A70C1257081003F359C!OpenDocument\">Gaundalen</a> 476 moh. 36 km. Her har jeg valgt å forlate hyttenettet og går innover mot Ståggådalen. 2 dagsetapper regner jeg med på turen. Gode fiskemuligheter underveis og jeg ser ikke bort i fra at jeg kan bli liggende en ekstra dag underveis. Etter ca. 18 km gange telter jeg trolig ved Lakavatnet 555 moh..\n25/6\nLakavatnet til Gaundalen. De siste 18 km til Gaundalen blir tatt denne dagen. Det er ennå tidlig på året og elvene fram mot Reinsgardsbakken kan ennå være flomstore. Må kanskje passere disse noe høyere opp i fjellet enn planlagt.\n26/6\nFra Gaundalen til Gjevsjøen 468 moh. 28 km 10 t. <a href=\"http://www.nt-tur.no/trail.php?lo_id=NO_nortr&amp;fo_id=4357\">Rute nr. 28/11 og 12.</a> Dette er umerkede DNT-ruter, anslaget på\n10 timer høres litt vel mye ut. Er terrenget tyngre enn kartet tyder på får jeg eventuelt ta en overnatting på Holden.\n28/6\nGjevsjøen til Bielnienjaevrie 648 moh. 23 km. Inne ved Djuptjønna går jeg inn i den tidligere Gressåmoen nasjonalpark. Denne nasjonalparken er nå blitt en del av den utvidede <a href=\"http://www.dirnat.no/blafjella/\">Blåfjella-Skjækerfjella nasjonalpark.</a> Herfra ligger det på rekke og rad flere fine fiskevann i høyde på fra 580 til 650 moh. Ikke usannsynlig at jeg stopper tidligere enn planlagt for tidlig leirslaging og fiske.\n29/6\nBielnienjaevrie til Skardstjørnin 24 km. En eller annen plass mellom Djuptjønna og Skardstjønnin regner jeg med å ha tatt en ekstra dag ved et av vatna for avslapping og riktig å nyte livet. Ikke minst regner jeg med at både mann og hund kan gasse seg med nytrukket fjellfisk.\n30/6\nSkardstjørnin til Nordli/Sandvika 410 moh.  22 km. Herfra går turen nordaust over mytene mot Gammalsæterlia. Herfra blir det å traske 13 km på veien fram til Sandvika/Nordli.','Etappe 9b','','publish','closed','open','','etappe-9b','','','2008-05-29 14:24:49','2008-05-29 12:24:49','',7,'http://falleri.no/ruta/etappe-9b/',0,'page','',0),(113,2,'2008-06-01 09:50:29','2008-06-01 07:50:29','I fjor kom jeg fram til Sveet den 1. september. Det var masse regn og den første snøen var kommet i fjellet. Jeg valgte derfor å bryte, for å starte opp igjen i 2008. Min tur skal tross alt ikke være et slit, men til ren nytelse og fornøyelse. Den 19. juni har jeg fått på meg fjellskoene, sekken er pakket, min Golden retriever, Najax er med og vi starter opp igjen fra Sveet.\n\nI tiden fram til start oppdateres etappene fra Sveet og fram til Nordkapp. Du finner dem under knappen \"Ruta\". I tillegg legger jeg inn, under egen knapp, opplysninger om proviant, klær og utstyr. Er du interessert finner du etter hvert min subjektive vurdering for en del av de samme tingene her.\n\nVelkommen til min hjemmeside\nGunnar Aas','Nå er det like før jeg starter opp igjen','','publish','open','open','','na-er-det-like-f%c3%b8r-jeg-starter-opp-igjen','','','2008-06-01 09:50:29','2008-06-01 07:50:29','',0,'http://falleri.no/2008/06/01/na-er-det-like-f%c3%b8r-jeg-starter-opp-igjen/',0,'post','',1),(115,2,'2008-06-01 11:15:40','2008-06-01 09:15:40','<strong>Nå er jeg på hjemlige og kjente trakter. Kun på den siste delen fram til Sulitjelma går jeg igjen i ukjent terreng.</strong>\r\n\r\n<strong>Umbukta – Sulitjelma        19/7 – 29/7</strong>\r\nLengde: 170 km + 13 %        =  192 km/21 = 9+2\r\n\r\nJeg er inne i kjente områder med mange gode fiskevann og elver. Både her og videre nordover er det gode muligheter for ekstra kosttilskudd av ørret og annen ferskvannsfisk.\r\n\r\n<strong>Depot </strong>\r\nDepot med proviant og utstyr for etappen henter jeg selv hjemme i Mo i Rana.\r\n\r\n<strong>Kart for etappen</strong>\r\n2027 III    Storakersvatnet\r\n2127 IV    Virvatnet\r\n2128 III    Lønsdal\r\n2128 IV    Junkerdal\r\n2128 I        Balvatnet\r\n2129 II        Sulitjelma\r\n\r\n<strong>Valgte ruter</strong>\r\nEtappen Umbukta - Sulitjelma er beregnet til å ta 9+2 dager. For det meste legger jeg dagsetappene hytte til hytte, lokale turistforeningshytter. Uavhengig av dette lar jeg været bestemme om det i stedet blir telting.\r\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\r\n\r\n19/7\r\nFra Umbukta 530 moh. til <a href=\"http://www.turistforeningen.no/rana/index.php\">Sauvasshytta</a> 993 moh. 13 km og en lett dagsetappe, men jeg starter først noe utpå dagen. Jeg følger den merkede DNT-ruta, runder Litltoppen og går Austerskardet opp til Vestre Sauvatnet. Herfra er det bare noen få kilometer nesten rett sydover til Sauvasshytta ved austervatnet.\r\n20/7\r\nFra Sauvasshytta til Kvitsteidalstunet 600 moh. 17 km. Også dette en enkel og grei etappe. Går den merkede ruta ned Ivakskardet og nordom Melkfjellet og Nordumfjellet, ned Litlumlia og fram til hytta Kvitsteidalstunet.\r\n21/7\r\nKvitsteidalstunet til Virvasshytta 650 moh. 24 km. Jeg deler mest sannsynlig distansen i to fot telting og fisking i Kvepsendalstjønna. Det er rundt 11 km opp til nordenden tjønna. Turen går i lettgått lende rett nordnordøst. Dagen etter, forhåpentligvis etter et godt fiskemåltid, går turen videre forbi Gåstjørna, krysser Stabbursbekken, opp forbi Vardfjellet og ned til Virvasshytta.\r\n23/7\r\nVirvasshytta til Sølvbekken ca. 800 moh. 19 km.  Denne dagen regner jeg med telting en eller annen plass i nærheten av Sølvbekken. Akkurat hvor får terrenget bestemme. Fra Virvasshytta følger jeg merket sti opp og forbi Corraskoia og krysser Daumannselva. Opp øst om Corras rett nord til Andfljelltjønna. Krysser den tørrlagte Gubbeltåga of Sør-Randalen en par kilometer lengre opp mot Sølvbekken.\r\n24/7\r\nSølvbekken til Stødi 700 moh 19 km. Fra Sølvbekken legges ruta øst om Nasafjellet og forbi de gamle sølvgruvene. Akkurat her er jeg så vidt inne i Sverige for deretter å gå over Stokkafjellet på norsk side. Nedover fjellsida i et slakt, men småkupert terreng forbi vestenden av Svangstjørna og til Stødi. Her telter jeg for natten.\r\n\r\nPlanen endres og jeg går samme kvelden, 24/7 fram til Lønnsdal. <a href=\"http://falleri.no/ruta/etappe-13b\">Se etappe 13a.</a>\r\n\r\n25/7\r\nStødi til Vestre Viskisvatn 502 moh. 26 km. Jeg følger og går E6 hele veien fram til Lønsstua. Der forlater jeg E6 og går østover til Viskisvatnet for telting.\r\n26/7\r\nVestre Viskisvatn til Argaladhytta 575 moh. 23 km. Følger den merkede DNT-stien videre NØ ned til <a href=\"http://www.dirnat.no/junkerdal\">Junkerdalen</a> og Junkerdal Turistsenter. Her krysser jeg Junkerdalselva og går den gamle stien/ferdselsveien opp Skaitidalen til Skaiti og Trygvebu. Herfra mer eller mindre rett nordover til Argaladhytta.\r\n28/7\r\nArgaladhytta til Fuglevatn 600 moh.  23 km.  DNT-merket sti. Jeg følger stien på østbredden av Skiejdejåhkå fram til Balvatnet. Går vestbredden av vannet fram til Fuglevatn og telter.\r\n29/7\r\nFuglevatn til <a href=\"http://www.salten.com/sulitjelma/index.no.html\">Sulitjelma</a> 130 moh. 28 km. Trasker resten av vannet rundt til Coarvihytta. Derfra går jeg vanlig helt fram til Sulitjelma. Her vil jeg ta en hviledag før jeg legger innover mot Padjelanta nasjonalpark og Sverige.','Etappe 12','','publish','closed','open','','etappe-12','','','2010-07-22 23:39:16','2010-07-22 21:39:16','',7,'http://falleri.no/ruta/etappe-12/',12,'page','',0),(116,2,'2008-06-01 11:35:45','2008-06-01 09:35:45','<strong>Nå går turen inn i Sverige. Med blant annet Padjelanta-, Stora Sjøfallets- og Abisko nasjonalparker.</strong>\r\n\r\n<strong>Sulitjelma – Abisko 31/7 – 14/8</strong>\r\nLengde: 216 km + 10 %        =  238 km/20 = 12+2\r\n\r\nNok en lang etappe. Denne gangen uten mellomdepot, men med noen betjente svenske tutisthytter. Den første av disse er Akkastugorna, 95 km fra Sulitjelma. Selv om etappen er lang, vil neppe proviant og kilo på ryggen være det store problemet. I det alt vesentlige følger jeg fra nå av traséen til Nordkalottruta og Kungsleden til et stykke inn i Finnmark.\r\n\r\n<strong>Depot</strong>\r\nDepot med proviant og utstyr for etappen sendes etter avtale til:  Sulitjelma scooter og hytteservive, Lomianlegget Sandnes, 8230 Sulitjelma, Telefon 75 64 95 00, mobil 907 29 929\r\n\r\n<strong>Kart for etappen </strong>\r\n2129 II        Sulitjelma\r\nBD 9        Padjelanta-Sulitjelma (svenskt kart)\r\nBD 7        Sitasjaure-Ritsem (svenskt kart)\r\nBD 6        Abisko-Kebnekaise-Narvik (svenskt kart)\r\n\r\n<strong>Valgte ruter</strong>\r\nEtappen Sulitjelma - Abisko er beregnet til å ta 12+2 dager. Dagsetappene beskrives stort sett fra hytte til hytte. Overnattingene vil i praksis like gjerne kunne skje i telt på andre plasser en  i hyttene. Vær og dagsform blir det avgjørende.\r\n\r\n23/7\r\n<a href=\"http://www.ub.uit.no/baser/arkinord/categories.php?cat_id=357&amp;l=norwegian\">Sulitjelma</a> 130 moh. til Sorjoshytta 830 moh. er det etter mine beregninger 19 km, en passende dagsetappe. På de første kilometerne tas de fleste høydemetrene til vel 900 meter ved Storelvvatnan. Deretter litt opp og ned, men uten de store stigningene fram til hytta.\r\n1/8\r\nSorjoshytta til Staddajåkkåstugorna 720 moh. 18 km. Fra Sorjushytta er det 7 km til den svenske grensen og <a href=\"http://www.fjallen.nu/parker/padje.htm\">Padjelanta Nasjonalpark.</a> Jeg går i et småkupert fjellterreng langs Sorjosjávre/Sårjåsjávrre og ned langs elva til Staddajåkkåstugorna. En lettgått og forholdsvis kort etappe.\r\n2/8\r\nStaddajåkkåstugorna til Arasluoktastugorna 590 moh. 24 km. En ganske lang dagsetappe. Kartet forteller også om noe kjerr over en par kilometer. Bortsett fra dette et relativt lettgått terreng og for det meste opp på 760 m like før Arasluoktastugorna. Da har jeg for det meste beveget med rundt 600 til 650 moh.\r\n4/8\r\nArasluoktastugorna til Låddejåkkåstugan 590 moh 13 km. Denne dagen blir nærmest for en hviledag å sammenligne, med jevn stigning opp til 900 meter og deretter slake fjellet ned til Låddejåkkåstugan.\r\n5/8\r\nLåddejåkkåstugan til Kisurisstugan 590 moh. 23 km. Det er et utall av bekker og muligens \"bekkedaler\" som skal krysses. Samtidig er det flere partier inntegnet med tett vegetasjon. Ikke usannsynlig at jeg telter og legger inn en hviledag og prøver fiske i elvedraget mellom Sallohaure og Kutjaure.\r\n6/8\r\nKisurisstugan til Akkastugorna 490 moh. 13 km. På dette strekket er jeg så vidt innom Stora Sjöfalets nationalpark. Akkastugorna er den første av de selvbetjente hyttene med mulighet for proviantering. Tvers over Akkajaure til Ritsem tar jeg båt for å fortsette videre neste dag.\r\n7/8\r\nRitsem 490 moh. til Sitasjaurestugorna 640 moh.  22 km. Et greit stykke vei med liten høydeforskjell forbi Autajaure og fram til sørenden av Sitasjaure.\r\n8/8\r\nSitasjaurestugorna til Hukejaurestugan 880 moh. 19 km. Turen fra Sitasjaurestugorna går litt opp i fjellsida sør om Lithijohka og Gáiccajohka og runder øst om Kaisejaure. Da neste dags etappe er skikkelig lang bør jeg gå en fire til fem kilometer forbi Hukejaurestugan og telte nede i elvedalen nord om Ráktas.\r\n9/8\r\nHukejaurestugan til Sälkastugorna 810 moh. 31 km. Fra teltplass regner jeg med en ca. 26 km fram til Sälkastugorna. Ruta går først sørøst og deretter østover gjennom et trangt fjellpass. Krysser Tjäktjajåkka fram til den første meditationsplats til minne om <a href=\"http://www.svenskakyrkan.se/luleastift/pilgrimsleden/index.htm\">Dag Hammarskjöld</a>. Detter er en av sju slike meditasjonsplasser Jeg skal ennå treffe på fire til av disse plassene på min vei til Abisko. Fra denne miditasjonsplassen går jeg rett nordover, 5 km igjen til Sälkastugorna.\r\n11/8\r\nSälkastugorna til Tjäktjastugan 930 moh. 16 km. De fire neste dagsetappene fram til Abisko er alle relativt korte. Samlet har jeg bare 64 km igjen og i tillegg skal jeg ha en hviledag. Om jeg tar denne på en av hyttene, i telt eller på hotell i Abisko får tiden vise. Det kan være aktuelt å gå forbi Tjäktjastugan og telte på slettene ned mot elven Aliseatnu. Abisko når jeg i alle fall enten den 11. eller 12. august.\r\n12/8\r\nTjäktjastugan til Alesjaurestugorna 780 moh. 13 km. Får se hva det blir til i dag. Ny telting eller Abiskojaurestugorna? Spent på hvordan dagen blir da jeg etter 12 dager i snaufjellet skal komme inn i skoglandskap denne dagen. Fra meditasjonsplassen ved Kieronstugan nærmer jeg meg raskt Abisko nasjonalpark og fjellskog. Jeg satser på å nå fram til Abiskojaurestugorna.\r\n13/8\r\nAlesjaurestugorna til Abiskojaurestugorna 490 moh. 20 km. Alesjaurestugorna har jeg alt gått forbi og denne dagen er jeg ved Abiskojaurestugorna og har min hviledag\r\n14/8\r\nAbiskojaurestugorna til <a href=\"http://www.fjallen.nu/parker/abisko_natur.htm\">Abisko</a> 360 moh. 15 km. Vel framme i Abisko og i så tilfelle en dag tidligere enn beregnet. Overnatting på hotell og depot for den kommende etappen inn i Norge.','Etappe 13','','publish','closed','open','','etappe-13','','','2010-07-22 23:40:22','2010-07-22 21:40:22','',7,'http://falleri.no/ruta/etappe-13/',13,'page','',0),(117,2,'2008-06-01 11:50:20','2008-06-01 09:50:20','<strong>\"Nå går turen inn i Sverige. Med blant annet Padjelanta-, Stora Sjøfallets- og Abisko nasjonalparker\". – Slik ble det ikke. – Svenske bestemmelser for hund i Padjelanta nasjonalpark ødela hele opplegget. Det er absolutt forbudt med kløvhund i nasjonalparken. Ny etappe blir:</strong>\r\n\r\n<strong>E6 fra Stødi til Kobbvannet – Abisko    25/7 – 10/8</strong>\r\nLengde: 364 km + 10 %                =  400 km/26 = 15+2\r\n\r\nMin henvendelse til Länsstyrelsen i Norrbotn ga følgende svar:\r\n<em><strong>\"Tillstånd att medföra hund inom nationalpark</strong>\r\nLänsstyrelsen är mycket restriktiv att medge dispens att medföra hund i nationalparkerna under barmarkstid. Anledningen är förutom skydd för djurlivet också att det kan störa renskötseln.\r\nDet är sommartid bara tillåtet att medföra kopplad hund (hund i band) längs Kungsleden.\r\nDu kan inte gå genom Padjelanta nationalpark med klövjehund på det sätt som du planerat utan särskild dispens från Länsstyrelsen. Länsstyrelsen ger i regel bara dispenser till handikappade (ledarhundar).\r\nDet är Länsstyrelsen som beviljar dispenser inom nationalparkerna. Om Du ändå vill ansöka om dispens att medföra hund så tar vi ut en prövningsavgift 2 000 kronor.\"</em>\r\n<em><strong>När det gäller böter för brott mot nationalparksföreskrifterna så är det dagsböter beroende på vilken inkomst man har som gäller. </strong></em>\r\n\r\nJeg velger derfor en annen rute utenom Padjelanta og Stora Sjöfallets nasjonalparker og går E6 fra Stødi til Kobbvannet i Sørfold. Derfra legges veien inn i fjellet nordøstover til jeg kommer inn på Nordkalottleden oppe i fjellet ved Hellemobotn i Tysfjorden. Herfra inn mot Akkastugorna/Ritsem og inn på min opprinnelige rute.\r\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\r\n\r\n<strong>Depot</strong>\r\nProviant og utstyr for etappen blir levert på Stødi og ved Kobbvannet.\r\n\r\n<strong>Kart for etappen</strong>\r\n2128 III    Lønsdal ev. veikart Norge 1:1 000 000\r\n2130 II        Gjerdalen\r\n2230 III    Linnajávrre\r\n2230 IV    Hellemobotn\r\nBD 7        Sitasjaure-Ritsem (svenskt kart)\r\nBD 6        Abisko-Kebnekaise-Narvik (svenskt kart)\r\n\r\n<strong>Valgte ruter</strong>\r\nEtappen Stødi - Abisko er beregnet til å ta 15+2 dager. De første 170 km går på vei; E6 fram til Megården, gjennom 15 veitunneler (blir kjørt gjennom tunnelene ca. 47 km) og til Kobbvannet. Herfra blir det anleggsvei til Gjerdalsvatnet før jeg endelig er ute i terrenget igjen.\r\n\r\n25/7\r\nStødi 700 moh. til <a href=\"http://www.polarhotell.no/norsk.html\">Polarsirkelen Høyfjellshotell</a> 500 moh. 21 km. Najax., min kløvhund blir hentet på Stødi og slipper de kommende dagene og milene på asfalt. Jeg utstyrer meg med lettere sekk og vil overnatte og til dels spise på hotell.\r\n26/7\r\nPolarsirkelen Høyfjellshotell til <a href=\"http://www.rognan.norlandia.no/\">Nordlandia Rognan Hotell</a> 5 moh.  47 km. Den desidert lengste etappen. Det må bare bli slik da andre overnattingssteder ligger for nært.\r\n27/7\r\nNordlandia Rognan Hotell til <a href=\"http://www.rica.no/index.cfm?oa=hotel.display&amp;con=5\">Fauske Hotell</a> 20 moh. 31 km. Rett og slett en kortere og forhåpentligvis en behagelig dag. Noe som sikkert vil være god for beina.\r\n28/7\r\nFauske til første tunnel ved Megården 24 km,. Herfra blir jeg kjørt 47 km gjennom og forbi alle veitunnelene, til sammen 15 tunneler til veikryss E6/Kobbvann. Tar meg fram et stykke, 6 km innover anleggsveien mot Reinoksvatnet og telter på Soldatmoen 90 moh.\r\n29/7\r\nSoldatmoen til Livsejávrre 710 moh. 17 km. Endelig helt ferdig med traskingen på asfalt. Jeg følger anleggsveien fram til Stilleelva. Derfra går det Livsselia oppover mot fjellet og Livsejávrre.\r\n30/7\r\nLivsejávrre til Gurtejávri 295 moh. 20 km. Denne dagen kommer jeg forbi mange flotte fjellvann. Skal ikke se bort i fra at det blir aktuelt å telte tidligere enn inne ved Gurtejávri.\r\n1/8\r\nGurtejávri til Rautojaure 480 moh. 27 km. Jeg er nå inne på <a href=\"http://www.karesuando.se/kdo/nordkalottleden.htm\">Nordkalottleden</a> og krysser svenskegrensen ved Válddajávri, I denne første delen av Sverige legger jeg inn ganske lange dagsetapper.\r\n2/8\r\nRautojaure til Vaisaluoktastugan 14 km eventuelt til Akkastugorna 490 moh. 24 km. Ved Akkastugorna kommer jeg inn på min opprinnelig planlagte rute.  Her er også den første svenske hytten med mulighet for proviantering. Tvers over Akkajaure til Ritsem tar jeg båt for å fortsette videre neste dag.\r\n3/8\r\nRitsem 490 moh. til Sitasjaurestugorna 640 moh.  22 km. Et greit stykke vei med liten høydeforskjell forbi Autajaure og fram til sørenden av Sitasjaure.\r\n4/8\r\nSitasjaurestugorna til Hukejaurestugan 880 moh. 19 km. Turen fra Sitasjaurestugorna går litt opp i fjellsida sør om Lithijohka og Gáiccajohka og runder øst om Kaisejaure. Da neste dags etappe er skikkelig lang bør jeg gå en fire til fem kilometer forbi Hukejaurestugan og telte nede i elvedalen nord om Ráktas.\r\n5/8\r\nHukejaurestugan til Sälkastugorna 810 moh. 31 km. Fra teltplass regner jeg med en ca. 26 km fram til Sälkastugorna. Ruta går først sørøst og deretter østover gjennom et trangt fjellpass. Krysser Tjäktjajåkka fram til den første meditationsplats til minne om <a href=\"http://www.svenskakyrkan.se/luleastift/pilgrimsleden/index.htm\">Dag Hammarskjöld.</a> Fra denne meditasjonsplassen går jeg rett nordover, 5 km igjen til Sälkastugorna.\r\nDet finnes sju slike meditasjonsplasser Jeg skal ennå treffe på fire til av disse plassene på min vei til Abisko. Det passer meget bra å bruke søndagen den 3. som hvile- og rekreasjonsdag her inne på Dag Hammarskjöldleden.\r\n7/8\r\nSälkastugorna til Tjäktjastugan 930 moh. 16 km. De fire neste dagsetappene fram til Abisko er alle relativt korte. Samlet har jeg bare 64 km igjen og det er ikke usannsynlig at jeg kommer en dag tidligere enn planlagt fram til Abisko.\r\n8/8\r\nTjäktjastugan til Alesjaurestugorna 780 moh. 13 km. Får se hva det blir til i dag. Ny telting eller Abiskojaurestugorna? Fra meditasjonsplassen ved Kieronstugan nærmer jeg meg raskt Abisko nasjonalpark og fjellskog. Jeg satser på å nå fram til Abiskojaurestugorna. I så fall kommer jeg en dag tidligere til Absiko.\r\n9/8\r\nAlesjaurestugorna til Abiskojaurestugorna 490 moh. 20 km. Alesjaurestugorna har jeg muligens alt gått forbi og dermed avsluttes dagen i Abisko.\r\n10/8\r\nAbiskojaurestugorna til <a href=\"http://www.fjallen.nu/parker/abisko_natur.htm\">Abisko</a> 360 moh. 15 km. Vel framme og overnatting på hotell samt depot for den kommende etappen inn i Norge.','Etappe 13b','','publish','closed','open','','etappe-13b','','','2010-07-22 23:41:19','2010-07-22 21:41:19','',7,'http://falleri.no/ruta/etappe-13b/',13,'page','',0),(122,2,'2008-06-04 15:18:10','2008-06-04 13:18:10','<strong>Noen få mil igjen og jeg er tilbake i Norge. Det blir indre Troms på vei mot Kilpisjävre og Finland.</strong>\n\n<strong>Abisko – Kilpisjävre 11/8 – 21-23/8</strong>\nLengde: 207 km + 15 %        =  238 km/24 = 10+1+2\n\nFjellet i det nordlige Sverige har vært en ny opplevelse for meg. Det samme vil det være med turen inn i indre Troms og videre til Treriksrøysa og Finland med Kilpisjävre.\n\n<strong>Depot </strong>\nDepot med proviant og utstyr for etappen sendes etter avtale:  Abisko Lapporten, 98107 Abisko, Sverige, Tlf. +46 (0)202 32 221. Åpent: Mandag-fredag 1000-1900, lørdag 1000-1800, søndag 1100-1700.\n\n<strong>Kart for etappen</strong>\nBD 6        (Abisko-Kebnekaise-Narvik (svenskt kart))\n1532 III    Salvasskardet\n1532 II        Altevatnet\n1632 IV    Julosvarri (mangler)\n1632 IV    Rostadalen\n1633 III    Signaldalen\n1633  II        Helligskogen\n<strong>\nValgte ruter</strong>\nEtappen Abisko– Kilpisjävre er i utgangspunktet beregnet til å ta 10+1+2 dager. Jeg tar derimot proviant med for 1 ekstra hvile- og rekreasjonsdager. Etappen byr på så mange muligheter i et nydelig landskap med vann og fiske. kan ikke tenke meg at det bare er å haste forbi.\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\n\n11/8\nAbisko 360 moh. til Lappjordhytta 544 moh. 26 km . Denne deletappen går for det meste på vei og delvis sti langsetter Torneträsk og fram til <a href=\"http://www.vinterfestuka.no/vu95/svbjgrav.htm\">Tornehamns kyrkogård.</a> Denne skal være vært en stopp og jeg tar derfor  pause her. De siste 12 kilometerne fram til Lappjordhytta  går i lettkupert skogsmark.\n12/8\n<a href=\"http://www.turistforeningen.no/cabin.php?ca_id=76&amp;x=16&amp;y=24&amp;v=3&amp;fo_id=6292\">Lappjordhytta</a> til Altevasshytta 540 moh. 30 km. Nå er jeg inne på <a href=\"http://www.turistforeningen.no/troms/index.php\">Troms Turlags</a> merkede ruter. Kanskje er det litt vel langt fram til Altevasshytta og det er mulig jeg telter ved utløpet av Salvassjohka. Hit er det rundt 24 km. Neste dag blir i såfall til Gaskashytta i stedet for Vuomahytta.\n13/8\nVed utløpet av Salvassjohka til Gaskashytta 530 moh. 16 km. En kort og behagelig etappe i rimelig slagt terreng. Hytta ligger ved Gaskkasjohka eller Fiskløyselva. Elva kommer fra fine vann lenger inne i fjellet. Uten tvil må det være mulig å dra seg noen pinner her og jeg tar muligens en ekstra hviledagen inne ved elva morgen. Får se.\n14/8\nGaskashytta til Vuomahytta 730 moh 19 km. 4 km før Vuomahytta kommer jeg inn i <a href=\"http://www.dirnat.no/content.ap?thisId=3015\">Øvre Dividal Nasjonalpark.</a> Et eldorado for \"attåtnæring\" med bær og fisk. Jeg tar kvelden enten på hytta, men går helst videre til Anjavassdalen og telter ved Anjalohka, nord om Blåfjellet.\n15/8\nVuomahytta til Dividalshytta 580 moh. 19 km.  Siktemålet for denne dagen er Dividalshytta, men jeg har lyst til å nå fram til Skaktarjohka og prøve fiskelykka i elva der. Dit er det ytterligere 8 km og det blir i tilfelle sein kveld, men med klar himmel vil natten er ennå rimelig lys. I tillegg blir neste dags etappe noe kortere.\n16/8\nDividalshytta til Dærtahytta 745 moh. 26 km. Jo mer jeg ser på kartet og planlegger disse dagen, dess mer kribler det i kroppen for å komme dit. Alt ser så lovende ut at det spørs om ikke de planlagte 10 dagene på etappe 14 må utvides med ennå en par dager.\n18/8\nDærtahytta til Rostahytta 465 moh.  17 km.  Noen få kilometer igjen og jeg går ut av nasjonalparken. Videre nordover går jeg både høyt oppe i fjellet og har, ikke minst, foran meg øvre delen av Rostadalen med alle dens herligheter.\n19/8\nRostahytta til Gappohytta 706 moh. 22 km. Fra Rostahytta og innover Isdalen skal jeg opp vel 500 høydemeter. Stigningen skjer for det meste jevnt over en strekning på ca. 8 km.  På det siste stykket krysser jeg så vidt gjennom Sverige og går grensen fram til Gappohytta.\n20/8\nGappohytta til Goldahytta 527 moh. 13 km. Liten høydeforskjell, men småkuppert terreng fram til Goldahytta. Jeg lar det bli en kort dag og overnatter her. Tar mye sannsynlig en kveldstur bortom <a href=\"http://www.karesuando.se/kdo/treriksroset.htm\">Treriksrøysa</a> samme kvelden. (Fant dessverre ingen god link til en norsk side om Treriksrøysa.)\n21/8\nGoldahytta til Kilpisjävre Hotell 490 moh. 25 km.  Denne dagen tar jeg siste strekket til hotellet ved Kilpisjärvi med overnatting og en bedre middag. Turen går opp under fjellet Iso-Malla i Finland, gjennom passet Kihtsekordsi og ned til nordenden av Kilpisjävri. Herfra går jeg E8 fram til mitt nattelosji.\nJeg kommer mest sannsynlig til bli en par dager ekstra inne i fjellene på den etappen. Kommer trolig først til Kilpisjärvi den 23. august. I så fall tar jeg to overnattinger og min søndagshvil på hotellet.','Etappe 14','','publish','closed','closed','','etappe-14','','','2010-07-22 23:42:23','2010-07-22 21:42:23','',7,'http://falleri.no/ruta/etappe-14/',14,'page','',0),(124,2,'2008-06-04 16:51:34','2008-06-04 14:51:34','<strong>Noen mil gjennom Finnland og deretter Finnmarksvidda med middels lange dagsmarsjer.</strong>\n\n<strong>Kilpisjävre – Máze – Skaidi    25/8 – 9/9</strong>\n\nLengde: 268 km + 15 %        =  308 km/20 = 15+2 dager\n\nKilpisjävre - Masi 156 km (136 km + 15%)   Masi - Skaidi 152 km (132 km + 15%)\n\nDepot med proviant og utstyr for deletappen Kilpisjävre - Máze sendes etter avtale: Kilpisjävre Hotell\nMellomdepot i Masi: Máze Post i Butikk, Joker Masi, 9525 Máze, tlf. 78 48 50 76\n\n<strong>Kart for etappen</strong>\n1633  II        (Helligskogen)\n1733 III    Coahppejávri\n1733 II        Cierte\n1833 III    Ráisjávri\n1833 I        Cárajávri\n1933 IV    Masi\n\n<strong>Kart etter Masi </strong>\n1934 II         Iesjávri\n1934 I        Cohkarássa\n1935 II        Stabbursdalen\n1935 I        Repparfjorden\n\n<strong>Valgte ruter</strong>\nEtappen som beskrives er på hele 308 km og beregnet å ta 15+2 dager. Lengden gjør at jeg må legge inn et mellomdepot i Máze. Fra Kilpisjávre følger jeg delvis Nordkalottruta, men tar av fra denne ved Meekonjärvi og østover mot øvre del av Reisadalen og deretter inn på Finnmarksvidda.\n\n<em>NB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.</em>\n\n25/8\nKilpisjávre 490 moh. til Autiotupa Saarijärvi 680 moh. 11 km. \"Omgivningen runt stugan vid Saarijärvi är stenig, men i närheten kan du finna några stenfria tältplatser. Det finns en bro över bäcken bredvid stugan.\"\n\n26/8\nAutiotupa Saarijärvi til Autiotupa Meekonjärvi 597 moh. 19 km. Denne strekningen går mest i snaufjellet fram til Meekonjärvi\n\n27/8\nAutiotupa Meekonjärvi til Autiotupa sørøst om Harrejávri 640 moh. 20 km. Dette er den siste finske hytta før jeg går inn i Norge og den øvre delen av Reisadalen. Tar mye trolig søndagen som hviledag her på hytta.\n\n28/8\nAutiotupa sørøst om Harrejávri til teltplass ved Nieidajohka 460 moh. nordvestom Gámoaivi, 18 km. Bare noen få kilometre igjen før jeg går inn i <a href=\"http://www.dirnat.no/reisa/\">Reisa nasjonalpark.</a> Terrenget fram kan være litt tøft uten å by på de helt store utfordringene. Søndag 24. ved elva Nieidajohka.\n\n29/8\nNieidajohka til Goikeluoppal 460 moh. 20 km. Nå går jeg inn på den egentlige Finnmarksvidda, stort sett nordøstover. Terrenget vil være forholdsvis lettgått med noe myr og skogbevokste rabber og uten de store høydeforskjellene.\n\n30/8\nGoikeluoppal til en høyde ved utløpet av Viekcejávrrit 450 moh. 20 km. Terrenget er fortsatt likt, men noe mer myrlendt.\n\n31/8\nViekcejávrrit til øst om Guhkesjávri 480 moh. 22 km. Dagens rute fører opp i et høyere terreng opp mot 600 m.\n\n1/9\nGuhkesjávri til Máze 370 moh.  28 km. Et noe langt strekk, men heller ikke her byr ruta på de store utfordringene. Telter eventuelt underveis og tar en delvis forskjøvet søndagshvil på deletappen.\n\n3/9\nMáze til Bealgejávri 440 moh. 21 km. Nye forsyninger ble hentet på posten i dag. Deilig å tenke på at jeg bar har et depot igjen før den siste etappen til Nordkapp.\n\n4/9\nBealgejávri til Gukkesluoppal 430 moh. 21 km. Nå bærer det nordover mot Iesjávri den største innsjøen på denne delen av vidda. Passerer i høydedragene på vestsida av sjøen. Det kan passe bra og ta søndagshvilen i dette området.\n\n5/9\nGukkesluoppal til Bojobeaskihytta 390 moh. 21 km. Hytta ligger 1 km rett øst om Bojobeasjávrihar og har egen AOT-lås. Det er lite aktuelt å skaffe nøkkel til denne hytta.\n\n6/9\nBojobeaskihytta til 4 km nord om Ruhkkojávri 360 moh. 24 km. Nok en ren transportstrekning, men nå inne i den søndre del av Stabbursdalen og elvesystemene som fører inn i denne.\n\n7/9\n4 km nord om Ruhkkojávri til Bastinjavri 271 moh. 21 km. I dag går ruta rett vest om <a href=\"http://www.dirnat.no/content.ap?thisId=3013\">Stabbursdalen nasjonalpark.</a>\n\n8/9\nBastinjavri til Cahppesjávri 360 moh. 21 km. Det går videre mer eller mindre rett nordover. Terrenget er mer eller mindre som tidligere; vann, myr og rabber.\n\n9/9\nCahppesjávri til Skaidi 80 moh. 23 km. De siste kilometerne fram til Skaidi skal det være brukbar sti. Denne følger for det meste kraftlinja fram til vei. Vel framme blir det overnatting og avslapping på <a href=\"http://skaidihotel.no/Skaidi%20Hotel.html\">Skaidi Hotell.</a> Det blir et døgn ekstra på hotellet og en velfortjent hviledag.','Etappe 15','','publish','closed','closed','','etappe-15','','','2010-07-22 23:42:43','2010-07-22 21:42:43','',7,'http://falleri.no/etappe-15/',15,'page','',0),(125,2,'2008-06-04 17:07:51','2008-06-04 15:07:51','<strong>Da gjenstår den siste etappen til Nordkapp, for det meste på vei og gjennom en undersjøisk tunnel.</strong>\n\n<strong>Skaidi – Nordkapp 20/9 – 16/9</strong>\nLengde: 140 km + 7,55 %        =  151 km/25 = 6 dager\n\n<strong>Depot </strong>\nDepot med proviant og utstyr for etappen sendes etter avtale med:  Skaidi Hotell, Skaidi, 9620 Kvalsund, tlf. 78 41 55 00.\n\n<strong>Kart for etappen </strong>\n1935 I        Repparfjorden\n2036 III    Kokelv\n2036 I        Magerøysundet\n2037 II        Nordkapp\nVeikart Norge 1:1 000 000\n\n<strong>Valgte ruter</strong>\nEnkelt og greit, det blir for det meste å gå E6, ter av fra denne og vandrer \"Ungdomsveien\" ned til Smørfjorden. Deretter E69 fram til Nordkapp bortsett fra 12,5 km ut i terrenget mellom Reinelvbukta og Kåfjorden.\nNB!  Navn framhevet med brunt linker til aktuelle nettsider for etappen.\n\n11/9\nSkaidi 80 moh. til Smørfjord 10 moh. 28 km. Vandrer E6 fra Skaidi og opp mot Günnajávri. Tar av fra E6 og går nordøstover ned til vei 889 og følger denne til Smørfjorden.\n12/9\nSmørfjord til Indre Svartvika 20 moh. 26 km. Den traseen går ut langs <a href=\"http://www.fascinatingland.com/Norsk/Naturen/sommerd.html\">Porsangerfjorden</a> og jeg følger E69 hele veien. Vel framme telter jeg ved elva som kommer ned fra Caccevagejavrit.\n13/9\nIndre Svartvika til Ánddámáhtejávri 136 moh. 24 km. Som dagen før går jeg veien langs fjorden til jeg passerer Repvåg og tar av ved Reinelvbukta og følger elva opp til Ánddámáhtejávri. Teltslaging og forhåpentligvis mulig med fiske i vannet.\n14/9\nIndre Ánddámáhtejávri til Studentervika 20 moh. 25 km. Denne dagen er jeg på  den delen av etappen som går utenfor asfaltert vei og i terrenget. Planen er å komme meg gjennom <a href=\"http://www.nordkappbompengeselskap.no/pages/nordkapptunnelen.htm\">Nordkapptunnelen</a> denne dagen. Endelig bestemmelse tar jeg framme ved Fisketindelva. Muligens telter jeg og tar tunnelen på friske bein neste morgen.\n15/9\nStudentervika til teltplass 230 moh. 1,5 km nord om veien til Gjesvær. 28 km. Det skal kun være to dager igjen til Nordkapp. Jeg ser litt stort på den resterende veien. Dersom jeg teltet ved Fisketindelva blir det en ekstra dag på den resterende veien. I så tilfelle blir det Nordkapp den 17. i stedet for den 16. september.\n16/9\nSiste dagsetappe til Nordkapp 310 moh. 20 km. Der, <a href=\"http://www.nordkapp.no/\">endelig ved målet.</a> Her vil min kjære kone Sara vente på meg.','Etappe 16','','publish','closed','closed','','etappe-16','','','2010-07-22 23:42:57','2010-07-22 21:42:57','',7,'http://falleri.no/ruta/etappe-16/',16,'page','',0),(132,2,'2008-06-08 13:13:43','2008-06-08 11:13:43','1. <a href=\"proviant\">Proviant</a>\r\n2. <a href=\"dette-har-jeg-pa\">Dette har jeg på</a>\r\n3. <a href=\"klær-i-sekken\">Klær i sekken eller i kløven</a>\r\n4. <a href=\"sekk-og-utstyr\">Sekk og utstyr</a>\r\n5. <a href=\"for-hygiene-til-medisinsk-og-personslig-bruk\">For hygiene, til medisinisk eller personlig bruk</a>\r\n6. <a href=\"sikkerhets-og-reparasjonsutstyr\">Sikkerhets- og reparasjonsutstyr</a>\r\n7. <a href=\"til-najax\">Til Najax</a>','Proviant og utstyr','','publish','closed','open','','proviant-og-utstyr','','','2010-07-04 21:58:44','2010-07-04 19:58:44','',0,'http://falleri.no/proviant-og-utstyr/',1,'page','',0),(133,2,'2008-06-08 13:53:27','2008-06-08 11:53:27','<strong>Proviant  for  10 dager -  Grunnlag for beregning\n</strong>Denne proviantlisten brukes for hele turen og danne grunnlaget for hver depoteske for de enkelte etapper, se <a href=\"http://falleri.no/ruta/\">Ruta</a>.  Sammensetningen gir en kaloritilførsel i gjennomsnitt på  3087 kcal./dag. -  Hundeforet gir Najax en kaloritilførsel i gjennomsnitt på 2725 kcal./dag. Fisk  o.l. underveis blir et rent tilskudd til kosten.\n\n<strong>10 stk frokoster bestående av: </strong>\n8x200 g havregryn\n8x40 g tørrmelk\n8x25 g rosiner\n2 poser kanel 15 g\n16x12 g smør  8x25 g sukker\n8x5 g salt\n\n2x6 skiver Wasa havre knekkebrød         Sum   400 g\n2x100 g spekepølse                             <em>\nDette til frokost når jeg har hviledager</em>\n<em><strong>320 g skal skal ikke i sekken, men spises den første dag</strong></em>\n\n<strong>10 stk lunch bestående av:</strong>\n10x31,5 g Bali kyllingsuppe/ Suppe m/ost og urter, rett i koppen\n10x100 g sjokolade\n10x50 g Bixit havrekjeks. 3 stk.            Sum 1815 g\n\n<strong>10 stk middager bestående av:</strong>\n8x100 g tørka reinkjøtt m/17-24 g fett av rein (<a href=\"http://www.norskdesign.no/emballasjedesign/helt-rein-og-helt-vilt-article3199-217.html\">fra ARV</a>)\n2x190 g bokser makrellfilet i tomat\n6x90 g poser med potetmos m/melk\n2x150 g langkornet ris\n2x140 g/poser gul ertersuppe\n10x7 g salt                        Sum 2370 g\n\n<strong>Drikke:</strong>\nRent vann blir den vesentlige drikken underveis, men i tillegg tar jeg med\n15 ps. te og 20 stuter frysetørret kaffe        Sum  70 g\n\nLitt godterier til kveldskaffen:\n10x50 g forskjellig sjokolade/sukkertøy        Sum   500 g\n\n<strong>Vurdering:</strong>\nForrige år varierte jeg med risengrøt og fenalår. Disse to ble jeg nokså lei av etter noen måltider og har derfor kuttet ut begge i år.\n\n<strong>Reserve-/nødproviant:</strong>\n200 g sukker, kokesjokolade, kjeks, rosiner\n100 g havregryn m/tørrmelk            Sum   300 g\n\n<strong>Totalt 8,053 kg -320 g = 7,733 kg</strong>\n<strong>\nHundemat:</strong>\n10x250 <a href=\"http://www.baltoshop.no/\">Balto Competition</a>\n10x300 g Specific aktive tørrfor\n\n<strong>Totalt   5,500 kg - OBS!  550 g spises</strong> <strong>kvelden før, bæres ikke = 4,950 kg</strong>\n\n<strong>\n</strong>','Proviant','','publish','closed','open','','proviant','','','2008-06-08 13:53:27','2008-06-08 11:53:27','',132,'http://falleri.no/proviant-og-utstyr/proviant/',0,'page','',0),(134,2,'2008-06-08 14:12:25','2008-06-08 12:12:25','1 ”       Nord-Pro hundekløv,        1360 g\n1 ”       Kennel brystsele til hund,          120 g\n1 ”       brettbar hundeskål i stoff,            90 g\n2 \"     13 l tykke pakkposer, Sea to Summit,      175 g\nExspot mot flått- og myggplager\nRimadyl smertestillende tabletter\nDihydrostreptomysin tabletter ved diare\nPrednisolon tabletter ved huggormbit - <em>Til og med Umbukta</em>\nFyra Ess potesalve\n\n<strong>Vekt i kløven</strong>\nAlt etter dagsetappenes lengde vil Najax bære mellom 11 og 13 kg. Dermed vil han, alt etter hvor langt vi er kommet ha plass til mellom 4 og 7 kg  av min mat eller mitt utstyr,','Til Najax','','publish','closed','open','','til-najax','','','2008-06-08 14:12:25','2008-06-08 12:12:25','',132,'http://falleri.no/proviant-og-utstyr/til-najax/',0,'page','',0),(135,2,'2008-06-08 14:21:52','2008-06-08 12:21:52','Fløyte\nPetzl Signal, rød diodelykt, 22 g\nReflexvest\nComet nødpenn med 3 stk. farge- og smellpatroner\nAluminiumsbelagt plast redningspose\nFyrstikker og ripeflate i filmboks\nSkjøtehylse og reserveledd til teltstang -  <em>Inkludert i teltet</em>\nLapper og lim for ev. rep. av rifter i telt og vanntette poser\nElastisk elektrikertape\nCa. 5 m sterk og tynn snor\nReserve gamasjestrikker\nNoen store sikkerhetsnåler\nPar-tre synåler\nLitt tynn og sterk sytråd','Sikkerhets- og reparasjonsutstyr','','publish','closed','open','','sikkerhets-og-reparasjonsutstyr','','','2008-06-08 14:21:52','2008-06-08 12:21:52','',132,'http://falleri.no/sikkerhets-og-reparasjonsutstyr/',0,'page','',0),(136,2,'2008-06-08 14:33:42','2008-06-08 12:33:42','Kontaktlinser\nLesebriller i etui\nProgresive briller i etui\nLiten saks\nTannbørste\nNoen Jordans tannstikker\nTannkrem, 1/2 tube\n1/2 stk Sunlight sepe\nNødv. mengde foreskrevne medisiner\nProlip for såre lepper\nCompeed krem for tørr og sprukken hud\nCompeed gnagsårplaster\nSolkrem\nSolbriller\nMyggolje\nSportstape\nTynn hudtape\nVanlig tekstilplaster\nEnkeltmannspakke\nStøttebandasje\nLiten grytesvamp\n½ rull tørkepapir','For hygiene, til medisinsk og personslig bruk','','publish','closed','open','','for-hygiene-til-medisinsk-og-personslig-bruk','','','2008-06-08 14:33:42','2008-06-08 12:33:42','',132,'http://falleri.no/proviant-og-utstyr/for-hygiene-til-medisinsk-og-personslig-bruk/',0,'page','',0),(138,2,'2008-06-14 14:50:02','2008-06-14 12:50:02','Også for utstyret har jeg endret litt fra i fjor, men i det alt vesentlige fungerte det jeg hadde lagt opp til tilfredsstillende.\n\n1 stk Klättermusen Mjölner ryggsekk (60 l) - 2500 g\n2 ” 7 liters sidelommer til sekken - 250 g\n1 ” 60 liters innerpose til sekken - 200 g\n1 \" vannflaske m/slange - 140 g - Fylles med vel 1 liter vann\n1 stk <a href=\"http://www.hilleberg.se/EUROPE.HTM\">Hilleberg Nallo 2 GT telt</a> - 2640 g\n1 \" lett presenning til bunn i fortelt - 250 g - Brukes også som regnkep\n1 ” skum liggeunderlag - 450 g\n1 ” Ajungilac Taiga dun sovepose - 1060 g\n1 ” Petzl Tikka plus hodelykt - 80 g\n3 \" 13 l lette pakkposer, Sea to Summit - 140 g\n1 ” Fiskars kniv, liten for alle behov - 70 g\n1 ” skje, kniv og gaffel og - 40 g\n1 ” liten tresleiv - 15 g\n2 ” brettbare plast kokser - 60 g\n2 \" kjeler 1,0 + 1,5 l, smale og høye - 380 g\n1 ” Primus multibrenner m/tilbehør og bensin - 1170 g\n1 ” ekstra brennstoffbh. m/bensin - 890 g - Muligens på de lengste etappene\n1 ” Suunto speilkompass - 80 g\n1 ” Garmin GPS - 170 g - Brukes alt vesentlig til posisjonsbestemmelse\n1 sett kart for den aktuelle etappe - 280 g - Etappen med flest kart, 7 stk\nFystikker vanntett emballert\nTørr never, vanntett emballert - Fornyes underveis\n1 stk Nokia N80 IP mobil m/USB-kabel - 180 g\n1 ” Nokia tråløst tastatur SU-8W - 220 g\n1 ” Olympus U 790 SW kamera - 150 g\nEkstra batterier og lader Både til Garmin og Nokia - 60 g\n1 ” Kinetic 4-delt kasteslukstang, (4-15 g sluker) - 150 g\n1 ” Silstar haspelsnelle med ny nylon - 310 g\nDiverse sluker, ankler, snuere etc.\n1 pk ballonger, 20 stk - Brukes sammen med stang til fiske\n\n<strong> Samlet vekt: 11,2 kg avrundet - </strong>Vann og ekstra bensinbh. er ikke med i summen\n\n<strong>Vurdering</strong>\nKlättermusen Mjölner ryggsekk på 60 l lå helt på grensen til å være stor nok. Klättermusens løsning med rullelukking i topp og nerdel gjør sitt til at volumet kan utnyttes maksimalt. Bæresystemet er lett å regulere under gange for å variere belastningen mellom skuldre og hofter. I år skifter jeg over til Klättermusens nye modell og øker til en 75 liters modell. Denne får jeg først etter Umbukta.\nKomperdell teleskop vandrestaver. Vandrestavene avlaster både ben og rygg og det er vesentlig lettere å ta seg fram, ikke minst i stigninger. Ved kryssing av strie elver er stavene uunnværlige. Teleskoplåsingen fungerte problemfritt.\nAtlas Elektra 1025 truger. Det tar noe tid å spenne på og av seg trugene tros en god patent. I praksis fører dette til at trugene brukes på noe lengere snødekte strekk. Atlas Elektras truger er lette å gode å gå med, nesten som å gå på bar mark.\n<a href=\"http://www.hilleberg.se/EUROPE.HTM\"> Hilleberg Nallo 2 GT</a> tunneltelt. Dette teltet er suverent. Lett etter størrelsen, meget god ventilering og med flere vel gjennomtenkte detaljer. Jeg kunne valgt en mindre og lettere modell, men et rimelig stort fortelt er godt å ha i dårlig vær. Både for koking og en tørr oppbevaring av sekk og utstyr.\nNokia N80 IP mobil, Nokia tråløst tastatur SU-8W og Olympus U 790 SW kamera. Med dette utstyret oppdaterer jeg min hjemmeside. Tekst og koordinater sendes som tekst til min sønn Gisle i Bergen. Bilder likeså fra kameraet, men fra bredbånd der jeg måtte få tilgang underveis. Utstyret fungerer, men både mobil og tastatur må behandles nemsomt og embaleres vanntett. Kameraet derimot er robust og tåler vann inntil 3 meters dyp og fall fra 1,5 meters høyde.','Sekk og utstyr','','publish','closed','open','','sekk-og-utstyr','','','2008-06-14 14:50:02','2008-06-14 12:50:02','',132,'http://falleri.no/proviant-og-utstyr/sekk-og-utstyr/',0,'page','',0),(139,2,'2008-06-14 15:10:36','2008-06-14 13:10:36','Noen små endringer er gjort siden i fjor.\nVektmessig fører endringen til ca. 100 g mindre å bære.\n\n1 stk Klättermusen Liv dunjakke - 340 g\n2 ” Janus underskjorter i ull - 540 g\n1 ” Devolds ulltruse/shorts - 75 g\n1 ” Devolds lange underbukser i ull - 180 g\n1 ” Janus balaclava i 50 % ull - 70 g\n1 ” Janus tynn ullhals - 50 g\n2 par Falke TK4 sokker - 190 g\n2 \" Ulvang tynne ullsokker - 80 g\n1 ” vanter i ull - 100 g\n1 ” Bergans vindvotter i Dermizax - 80 g\n1 ” Bergans gamasjer i Dermizax - 330 g\n2 stk spennremmer, 50 cm - 40 g\n<strong> Samlet vekt: 2,075 kg</strong>\n\n<strong>Vurdering</strong>\nKlättermusen Liv dunjakke erstatter tynn ullgenser. Denne dunjakken er lettere enn genseren. Er fylt med 100% dun av beste kvalitet, fillpower 800+, og er vindtett. Det rette plagget på kjølige kvelder i fjellet.\nJanus underskjorter i ull er av superb kvalitet. De brukes i all slags vær, til alle døgnets tider og vaskes annen hver dag med Sunlight såpe i nærmeste bekk.\nDevolds ulltruse/shorts. Jeg finner ingen bedre. Truser i bomull er bannlyst på en tur som denne.\nDevolds lange underbukser i ull. Denne erstatter samme fra Janus. Jeg syntes rett å slett den lange fra Janus ga for dårlig isolasjon i forhold Devolds produkt.\nJanus balaclava og Janus tynn ullhals var ikke i bruk forrige år, men må ha sin faste plass i sekken.\nFalke TK4 sokker har jeg funnet i et større nummer enn for mine opprinnelige Bridgedale Summit sokker. Størrelsen er den eneste årsaken til mitt bytte av sokker. Kvalitetsmessig vil jeg tro det er hipp som happ.\nBergans gamasjer i Dermizax. For sikkerhets skyld krysses alle strie elver med fjellskoene på. Gamsjene når til knærne og de to spennremmene spennes hårdt om ankelen. Kommer nesten alltid tørrskodd over.','Klær i sekken','','publish','closed','open','','kl%c3%a6r-i-sekken','','','2008-06-14 15:10:36','2008-06-14 13:10:36','',132,'http://falleri.no/klær-i-sekken/',0,'page','',0),(141,2,'2008-06-14 16:02:04','2008-06-14 14:02:04','Jeg har gjort svært små endringer i påkledningen fra i fjor til i år.\nTynn ullhue ble tidlig i fjor byttet ut med en caps. Ulvangs tynne ullsokker erstatter tynne ullsokker kjøpt hos Dressmann.\n\nCaps - En god skjerm beskytter nakken mot sola fra syd\nMontana vindtett lett anojakke\nKlättermusen Mithril bukse\nJanus underskjorte i ull\nDevolds ulltruse/shorts\nMedima thermo knevarmere\nUllvang tynne ullsokker\nFalke TK4 sokker\nJörnkängan fjellsko\nKrokbelte m/line for hunden\nSeal Line vanntett kartmappe\nKomperdell teleskop vandrestaver\n\nMBT lærstøvler for asfalt/vei - Disse skoene brukes bare på vei. Sendes til og fra depoter underveis\n\n<strong>Vurdering</strong>\nDen daglige påkledningen som du ser her virket perfekt. Det eneste var de tynne sokkene fra Dressmann. Disse krympet rett og slett for mye og er byttet med tynne sokker fra Ulvang.\nI regn er påkledningen med Janus ullunderskjorte, Montane ultralett anojakke og Klättermusen Mithril bukse perfekt. Jeg går uten regntøy. Er varm så lenge jeg er i bevegelse og alle plaggene tørker på kroppen i løpet av 1/2 time når regnet slutter. Ja bortsett fra ullskjorta som selvsagt er full av svette.\nMedima thermo knevarmere er nok noe som alderen krever. De gir meg den nødvendige ekstra støtte knærne trenger.\nBridgedale Summit sokkene er gode og varme. Jeg ble overhode ikke klam på beina og sokkene var like hele da jeg sluttet for året. Det eneste jeg har å innvende er at den største størrelsen levert i Norge er nr. 44-47. For meg med store føtter er den litt i minste laget. 48+ burde vært tilgjengelig.\nJörnkängan er en fjellsko som passer mine føtter perfekt. Ingen Gortexmembran, cellgummi i den nedre delen, resten i topp kvalitet lær. Selv i trøndermyrene og med store mengder med regn ble jeg overhode ikke vår på beina. Vel og merke gikk jeg også med gamasjene på. Heller ikke et eneste gnagsår på hele turen.\nDen siste posten på lista <a href=\"http://www.swissmasai.no/\">MBT-skoene</a> er rett å slett uunnværlige på strekkene med vei og asfalt. MBT-skoene fanger opp støt og gir en fantastisk myk gange på vei. Med tung sekk på vei over flere mil hadde jeg absolutt ingen problemer med beina. Derimot, i terrenget passer disse skoene overhode ikke. De er forholdsvis tunge og derfor sender jeg, så langt det går, skoene mellom de depotene der jeg må ut på vei.','Dette har jeg på','','publish','closed','open','','dette-har-jeg-pa','','','2008-06-14 16:02:04','2008-06-14 14:02:04','',132,'http://falleri.no/dette-har-jeg-pa/',0,'page','',0),(142,2,'2008-06-20 19:32:16','2008-06-20 17:32:16','Bikkja satte til livs to halve plastposer og mistet all matlyst. Plasten måtte ut før vi kunne starte. Torsdag og fredag var alt OK og i morgen, lørdag har jeg sjåfør som kjører meg til Sveet. <strong>Endelig søndag 22. juni trasker jeg i vei.</strong>\n\nMin aller beste takk til Jan-Erik Vargdal som på kort varsel tilbød seg å kjøre ned og ta bilen hjem igjen.\n\n<a href=\"http://www.flickr.com/photos/gisle/2601083034/\" title=\"\"><img src=\"http://farm4.static.flickr.com/3022/2601083034_129e398700.jpg\" width=\"440\" height=\"330\" border=0 /></a>','Det ble en liten forsinkelse','','publish','open','open','','det-ble-en-liten-forsinkelse','','','2008-06-20 19:32:16','2008-06-20 17:32:16','',0,'http://falleri.no/2008/06/20/det-ble-en-liten-forsinkelse/',0,'post','',1),(143,2,'2008-06-21 12:09:19','2008-06-21 10:09:19','I dag ble jeg kjørt til Vera og Sveet. Var som snarest og hilste på fru Olsen og de øvrige på Sveet. Det var hos dem jeg avsluttet i fjor. Rundt kl. 1630 dro min sjåfør Jan-Erik Vargdal hjemover. Jeg hadde glemt mine vandrestaver hjemme, men du snakker om hell: Her ved ungdomshuset hilste jeg på et eldre ektepar. Tro det eller ei, kona kommer med sine egne staver. Versågod, kan disse brukes får du dem av meg. Jeg er fruen dypt takknemlig. Samme kveld går jeg ca. 5 km opp til Klumptjønnin og telter for natten. Starten er så langt vellykket og jeg ligger godt an til morgendagen.','Ankomst Veressjøen','','publish','closed','open','','ankomst-veressj%c3%b8en','','','2008-06-21 12:09:19','2008-06-21 10:09:19','',0,'http://falleri.no/2008/06/21/ankomst-veressj%c3%b8en/',0,'post','',0),(144,2,'2008-06-22 12:13:55','2008-06-22 10:13:55','Trøndelag fornekter seg ikke, det er mye myr og en del kjerr hele veien. I tillegg er myrene våte etter mye regn. Bortsett fra dette er terrenget slakt og kunne vært lettgått hadde det ikke vært for lange strekninger med våt myr. Det er ingen store høydeforskjeller av betydning. Jeg har en blanding av sol og regn underveis. De siste par timene før Lakavatnet skinner solen og jeg kommer tørr og fin fram.\n\nLeter litt og finner en absolutt brukbar teltplass. På tross av mye myr rundt teltplassen er det noe kjølig og vindfullt. Dette gjør sitt til at hverken jeg eller Najax plages av mygg. Her oppe ved Lakavatnet på 555 moh hadde et svanepar slått seg til. Et majestetisk syn inne i fjellet. I kveld båser det friskt fra nordvest. Det er kaldt og jeg kryper i posen og lar fiskestanga hvile denne første kvelden inne i fjellet.','Klumptjønnin - Lakavatnet','','publish','closed','open','','klumptj%c3%b8nnin-lakavatnet','','','2008-06-22 12:13:55','2008-06-22 10:13:55','',0,'http://falleri.no/2008/06/22/klumptj%c3%b8nnin-lakavatnet/',0,'post','',0),(145,2,'2008-06-23 12:18:29','2008-06-23 10:18:29','Jeg hadde opprinnelig planlagt å bli et ekstra døgn ved Lakavatnet, men tidlig i femtida om morgenen kom det kraftig søraustlig vind og regn.  Jeg ble i teltet og ventet han av. Ved ni-tida ble været noe bedre. Jeg kokte min havregrøt, pakket og brøt leiren med retning for Gaundalen fjellgård. Etter ennå mere våte myrer, kryssing av elver og ennå mere regn var jeg framme ved gården i sekstida, søkke blaut. Der på gården ble det husvære, tørkerom, store mengder med stekt flesk og egg og til slutt kaffe og bøtkake.\n\nAkkurat nå når jeg sitter og kladder for hjemmesiden laver regnet ned og det blåser ganske mye. Deilig på en dag som denne og være inne i hus, godt og varmt. Jeg savner sant og si ikke teltet akkurat nå.\n\nPS! Bjørn store havregryn er forresten rett og slett en flause. Jeg har alltid vært meget glad i havregrøt. Det var også grunnen til at jeg satset på dette produktet som frokost i min planlegging. Det jeg nå ser til min fortvilelse er at Vaksdal Mølle må ha lagt ned sin produksjon. Merkenavnet Bjørn er vel dermed solgt til AXA, Cerela Foods. Produktet smaker nå rett og slett pyton og jeg greier med nød og neppe å tvinge grøten i meg. Jeg vet rett og slette ikke hvordan jeg skal løse dette problemet for resten av turen.','Lakavatnet - Gaundalen fjellgård','','publish','open','open','','lakavatnet-gaundalen-fjellgard','','','2008-06-23 12:18:29','2008-06-23 10:18:29','',0,'http://falleri.no/2008/06/23/lakavatnet-gaundalen-fjellgard/',0,'post','',0),(146,2,'2008-06-24 15:41:59','2008-06-24 13:41:59','Dagen i dag starter med regn og storm fra nordvest og kun 5°C. Det ble å ta det med ro innomhus og avvente endring i været. Litt over halv tolv la jeg i vei og gikk over til Holderen på fem timer. Jeg fikk sludd oppe i fjellet på 800 moh og gikk i søkkvåte sugende trøndermyrer. Det virker som om sommeren fra i fjor i Sør-Norge skal forfølge meg, kaldt og vått. Krysser fingrene og håper på sol og varme i dagene framover.\n\nOm dagens landskap er det å si at bortsett fra våte myrer, etter i hvert fall 2 dagers regn, er det rimelig lett-gått. Slakt opp fra 460 til 800 meter og deretter for det meste unnaland ned til Holderen på 445 moh. Holden fjellgård er nedlagt og husene eies i dag av Statskog. Alt er meget velstelt og husene er til leie. Ett av husene leies og drives av Nord-Trøndelag turistforening som vanlig turistforeningshytte. Et apropos: en par måneder hvert år kommer det en drift fra Snåsa med melkekyr og annet fe. Da setres det på Holden og landskapet holdes i hevd.','Gaundalen - Holderen','','publish','open','open','','gaundalen-holderen','','','2008-06-24 15:41:59','2008-06-24 13:41:59','',0,'http://falleri.no/2008/06/24/gaundalen-holderen/',0,'post','',0),(147,2,'2008-06-25 15:51:46','2008-06-25 13:51:46','Stort sett samme våte værlaget og jeg tok derfor kun dette strekket til Gjefsjøen. Knappe fire timer fra hus til hus, men også i dag gjennom søkkvåte myrer. Men heldigvis, i morgen går jeg inn i snaufjellet og jeg håper inderlig på omslag i været. Litt sol og varme fram under Gauptjørnaksla og inn mot Urdalsfjellet og Hykkelfjellet. Uansett blir det telting i fjellheimen og tjangs til å få brukt fiskestanga.','Holderen - Gjefsjøen fjellgård','','publish','open','open','','holderen-gjefsj%c3%b8en-fjellgard','','','2008-06-25 15:51:46','2008-06-25 13:51:46','',0,'http://falleri.no/2008/06/25/holderen-gjefsj%c3%b8en-fjellgard/',0,'post','',0),(148,2,'2008-06-26 15:55:55','2008-06-26 13:55:55','Her i dag er jeg kommet fram til Bielnienjaevrie i solskinn og har det helt deilig. Telter og belager meg på et ekstra døgn her inne ved vannet. Klarværet sist natt kom med frost og på morningen var teltet kvit-rimet. Mosen var frossen og hard å gå på med bare bein. Is hadde også lagt seg på mindre pytter.\n\nFormiddagen tilbringes horisontalt bare avslappende i solhellingen. Litt utpå ettermiddagen tar jeg stanga fatt og prøver fiskelykken. Ørreten vaker absolutt over hele vannet og jeg får det jeg trenger til meg og hunden. Etter 2 fisker har jeg ett godt halvkilo; mer er det ikke plass til i min kasserolle og egentlig er det mat nok til oss begge. Kvaliteten på ørreten her er fantastisk, feit, rød og fin. Det er bare å glede seg til en sen middag.\n\nIngenting tyder på at det er stor trafikk av fiskere ved disse vannene. To vann ligger etter hverandre med bare 4-5 meters avstand og kun 6 meters høydeforskjell. Jeg observerte bare tre gamle kaffebål, en lite markert sti rundt vannet og noen få gjenglemte markbokser. Disse var av gammeltypen som ruster.\n\nBielnienjaevrie ligger på 648 moh. Turen hit i går fra Gjefsjøen gikk i et flott terreng.  Det var for det nærmeste slutt på blautmyrer. Delvis bart fjell og fast lyngmark kun med enkelte få og små våte partier innimellom. Det var i det hele tatt en fryd å vandre i går. Passe varmt, lettskyet og en kjølig bris. Ruta  jeg hadde stukket på forhånd stemte meget bra. Det ble bare noen mindre korrigeringer etterhvert som jeg gikk og kunne se hvordan terrenget så ut i virkeligheten.','Bielnienjaevrie','','publish','open','open','','bielnienjaevrie','','','2008-06-26 15:55:55','2008-06-26 13:55:55','',0,'http://falleri.no/2008/06/26/bielnienjaevrie/',0,'post','',0),(149,2,'2008-06-28 16:02:48','2008-06-28 14:02:48','Etter to gode dager fortsetter ferden. Jeg går nå innover mot Urddalsfjellet. På veien dit ble jeg tidliger tilrådt å krysse elva fra Luruvatnet helt oppe ved utløpet. I tilfelle mye avrenning ville det være best å krysse elva fra vannet der. Noe stigning oppover mot Urddalsfjellet. Det ligger som en fjellrygg jeg må gå over. Videre passerer jeg Urddalen helt oppe i fjellet. Det jeg kan se av dalen nedover tyder på at den er meget frodig og sikkert med gode beiter for elgen. Det er et  fantastiskt fjellandskap jeg går i. Nesten ikke myr og ellers alt vesentlig lettgåtte dryasheier med et fast og fint dekke. Videre innover Hykkelfjellet fortsetter det på samme måten, men med den forskjellen at her ligger vannene og tjønnene tett i tett. Flott å gå, skyet, enkelte glimt av sol, ikke for varmt og bare en par små regnskurer.\n\nNed fjellsida mot Skardstjørnin velger jeg å telte noe tidliger enn opprinnelig planlagt. Jeg har vært på beina i vel 10 timer og det blir bare med å telte, få meg mat og krype inn i posen. I tillegg ser det ut til å være ganske skogbevokst ved tjønnene og sikkert mye mer mygg og knott enn her oppe i fjellsida. Folk har jeg ikke truffet på her inne, heller ikke sett bjørn. Derimot har jeg oppservert hele 8 sangsvaner inne på fjellvannene. I det hele tatt de fjellene jeg i de siste dagene jeg har gått i er et eldorado av fiskevann og også et fantastiskt rypeterreng.\n\n<a href=\"http://www.flickr.com/photos/gisle/2635376089/\" title=\"Fra Hykkelfjellet\"><img src=\"http://farm4.static.flickr.com/3147/2635376089_5c2d2c31df.jpg\" width=\"440\" height=\"330\" alt=\"Fra Hykkelfjellet\" /></a>','Bielnienjaevrie - Skardstjørnin','','publish','open','open','','bielnienjaevrie-skardstj%c3%b8rnin','','','2008-06-28 16:02:48','2008-06-28 14:02:48','',0,'http://falleri.no/2008/06/28/bielnienjaevrie-skardstj%c3%b8rnin/',0,'post','',0),(150,2,'2008-06-29 20:50:29','2008-06-29 18:50:29','Jeg teltet ganske nær Skardstjørnin og avstanden fra min teltplass er omtrent lik som fra tjønnene. Totalt beregnet til ca. 22 km hvorav 13 km på vei. Jeg tok kompasskurs mot Bustadlia og la i vei. Deretter var det strake veien til Sørnes, en nedlagt fjellgård. Derfra gikk jeg veien langs Laksjøen til Nordli.\n\nUnderveis passerte jeg et meget stort område inngjerdet med elektriske gjerder. Jeg mener det var hele fire strenger i høyden opp til sikkert en god meter. Gjerdingen er et forsøk på å holde bjørnen adskilt fra sauene. Etter hva jeg forstår uten noe vellykket resultat.  Storsamfunnet vil ha bjørn, men bygdene og bygdenæringen er det som sliter med problemet.\n\nFramme i Nordli fikk jeg leid hytte av Astrid Formo Monsen, en meget trivelig dame. Neste dag ble jeg servert en lang og god morrakaffe med mye godt til. Morrakaffen tok i hvert fall bortimot to timer. Her på Nordli lå et av mine depoter med blandt annet mine <a href=\"http://www.swissmasai.no/\">MBT-sko</a> for de kommende mil på asfalt. Kvelden på hytta ble tilbragt med oppdatering av bloggen og matlaging. Tre unge gutter i nabohytta tok imens godt vare på Najax.','Skardstjørnin - Nordli','','publish','open','open','','skardstj%c3%b8rnin-nordli','','','2008-06-29 20:50:29','2008-06-29 18:50:29','',0,'http://falleri.no/2008/06/29/skardstj%c3%b8rnin-nordli/',0,'post','',0),(151,2,'2008-06-30 09:51:55','2008-06-30 07:51:55','I dag ble det en kort etappe. Derimot traff jeg på mange hyggelige folk. Det som skjer er at Najax må ha fått i seg noe nede på Nordli. I hvert fall startet både han og jeg friskt iveg. Men bare etter to til tre kilometer etter veikrysset mot Røyrvik ble hunden slappere og slappere. Jeg måtte bare sette meg ned med Najax. Heldigvis får jeg stoppet en bil med to hyggelige damer, Ann Katrin Bergli og Ingrid Bergli. De er bare hjelpsomheten selv og tar en telefon til Kvelibua og ordner det slik at Najax kan bli kjørt dit og tatt vare på. Jeg går etter og kommer fram i femtida. Der ligger Najax pent og venter. Jeg får teltplass ved butikken. Åpent toalett og dusj er det også der. Butikkeieren, Roland Aagård regner med at han neste dag vil greie å ordne bilskyss for Najax helt fram til Røyrvik. Der har <a href=\"http://visitborgefjell.no/index.php?side=1012&a=13\">Limingen Gjestegård</a> sagt ja til å ta i mot hunden. Det blir å spise, få opp teltet og sove der om natta.','Nordli - Kvelibua','','publish','open','open','','nordli-kvelibua','','','2008-06-30 09:51:55','2008-06-30 07:51:55','',0,'http://falleri.no/2008/06/30/nordli-kvelibua/',0,'post','',0),(152,2,'2008-07-01 09:54:56','2008-07-01 07:54:56','Neste dag ordner alt seg. En Roger Granli med gårdsbruk ved Limingen er på vei til sin regnskapsfører i samme huset som Kvelibua. Han kan ta Najax med til Røyrvik. I mellomtiden mens jeg venter kommer det mye hyggelige folk innom butikken. Det blir pratet mye om løst og fast. Litt om mitt prosjekt og jeg får vite en hel del om Kvelia. Ikke minst det pratet mye om bjørn.\n\nLierne, som Kvelia ligger i er et av de nasjonale kjerneområdene for bjørn. Resultatet er at alle sauebøndene i Kvelia har måttet slutte med saueholdet. Bjørnen forsyner seg grovt. Bøndene har lagt ned sauedrifta. Flere tusen sau er dermed rett og slett borte fra bygda. Tenk på hva dette må si for næringsgrunnlaget i ei lita bygd. I mangel på sau har bjørnen de siste årene begynt å forsyne seg godt av den lokale elgstammen. Bjørnen må jo ha mat. I år var det hittil funnet rundt 20 drektige elgkyr, slått av bjørn og hvor kua var åpnet og bare fosteret spist. I en av de svenske nabobygdene var det i år bare f.eks. bare påvist 2 fjorårskalver og ingen drektige elgkyr. Hva skal bjørnen spise når også elgstammen desimeres? Det må være et litt skummelt tankekors. I Lierne legges det fra storsamfunnet opp til 7 ynglende binner. Oppe i Hattfjelldal/Bleikvassli i mitt nærområde er målet 2 ynglende binner. Dette er beregnet å tilsi 60-70 bjørn totalt. Hva må da ikke 7 ynglende binner bety?\n\nOver til noe hyggeligere. Vi pratet da ikke bare om bjørn, men kom også inn på et lokalt spel: <a href=\"http://www.oppgarden.no/PeTorsa.htm\">Per Torsa spelet</a>. Dette går for seg i siste helga i august. Et meget populært spel og siste året var det solgt billetter til 2400 betalende tilskuere. Vi snakket også om et nystartet selskap, <a href=\"http://www.norbakkguide.com\">Norbakk Grenseguiding</a>. Det de holder på med høres meget interessant ut og de har en fin utviklet hjemmeside.','Kvelibua','','publish','open','open','','kvelibua','','','2008-07-01 09:54:56','2008-07-01 07:54:56','',0,'http://falleri.no/2008/07/01/kvelibua/',0,'post','',0),(153,2,'2008-07-01 10:08:45','2008-07-01 08:08:45','Rundt kl. 1500 hentet Roger Granli hunden for å kjøre han til Røyrvik. Samtidig ble jeg invitert til å besøke gården hans ved Limingen neste dag. Limingen Gjestegård hadde sagt ja til å ta i mot Najax og stelle godt med han til jeg kom. Jeg startet opp samme ettermiddagen og gikk bakkene opp til Sverige. Går noen kilometer på svensk side før jeg kommer ned til Tunsjø Senter. Der er jeg i halvsjutida og skal ta en liten pause. Kommer i snakk med en hyggelig dame, Siv Estil. Hun inviterer meg likegodt på en kopp kaffe og jeg takker med glede ja. Kaffekoppen ble til et nydelig kveldsmåltid med hjembakt brød, masser av godt pålegg, lefser og annet godt. Først klokken 2100 ble det oppbrudd etter en meget trivelig kveld. Jeg gikk da en time fram til den vakkert beliggende fjellgården Solberg. Det var utrolig pent og velstelt på tunet og rundt bygningene. Der var ingen hjemme, men jeg tillot meg å slå opp teltet for overnatting. På denne måten må jeg få takke familien Aasen for en god natt og en deilig morgen der på tunet. Hjertelig takk og jeg håper dere ikke tar det ille opp at jeg tillot meg å overnatte der hos dere.','Kvellibua - Solberg','','publish','open','open','','kvellibua-solberg','','','2008-07-01 10:08:45','2008-07-01 08:08:45','',0,'http://falleri.no/2008/07/01/kvellibua-solberg/',0,'post','',0),(154,2,'2008-07-02 10:10:09','2008-07-02 08:10:09','<a href=\"http://www.flickr.com/photos/gisle/2636197580/\" title=\"Siri og Roger vinker god tur videre\"><img src=\"http://farm4.static.flickr.com/3055/2636197580_5a39eb9b60_m.jpg\" width=\"240\" height=\"180\" alt=\"Siri og Roger vinker god tur videre\" align=\"right\" /></a>Nå tar jeg fatt på den siste etappen fram til Røyrvik og Limingen Gjestegård; 34 km gjenstår på vei og asfalt. Etter gårdagens avtale med Roger Granli skulle jeg komme til hans gård, Limingen Østre rundt kl. 1200. Der traff jeg i tillegg hans kone Siri og en av sønnene. Heller ikke her ble det bare til et kort besøk. Der ble det lunch med fire egg og masser av bacon. Først nærmere halv fire gikk turen videre. Nå bare med en liten svipptursekk. Roger og Siri skule senere inn på sætra nærmere Røyrvik. Da kom de til å ta med min mye tyngre tursekk. Til Limingen Gjestegård kom jeg fram kl. 2200. Ble hjertelig mottatt av vertskapet Borgny & Fritz. Selvsagt også av min hund Najax. Han hadde hatt det skikkelig godt med god mat og flere lufteturer. Selv fikk jeg så sent på kvelden et gedigent måltid med en pils og en hel liter melk. Deretter var det bare å krype til sengs i en myk og god seng mellom hvite lakener. Ingen liten kontrast til teltlivet.','Solberg - Røyrvik','','publish','open','open','','solberg-r%c3%b8yrvik','','','2008-07-02 10:10:09','2008-07-02 08:10:09','',0,'http://falleri.no/2008/07/02/solberg-r%c3%b8yrvik/',0,'post','',0),(155,2,'2008-07-03 10:12:31','2008-07-03 08:12:31','<a href=\"http://www.flickr.com/photos/gisle/2635376737/\" title=\"Fra Limingen Gjestegård\"><img src=\"http://farm4.static.flickr.com/3014/2635376737_0480f09148_m.jpg\" width=\"240\" height=\"180\" alt=\"Fra Limingen Gjestegård\" align=\"right\" /></a>Dagen i dag er brukt til god mat, oppdatering av bloggen og ikke minst en konsert på kvelden med den kjente svenske musikeren Kent Nilsson (piano) sammen med Lasse Hörnfäldt (saxofon) og den lokale sangeren Toril Gustavsen (sang, gitar og dikt). En fantastisk opplevelse å ta med seg inn i Børgefjell.\n\nVertskapet Borgny & Fritz kan jeg ikke berømme nok; blide og hyggelige. De tar godt vare både på gjest og hund. Lager nydelig og variert mat. De er bare fantastiske. <a href=\"http://visitborgefjell.no/index.php?side=1012&a=13\">Gjestegården</a> i seg selv er meget trivelig og et topp anlegg med fine nyoppussede rom, gode senger, en stilig spisesal. I det hele tatt en absolutt positiv opplevelse og absolutt verdt ikke bare ett, men flere besøk.  Jeg er blitt tatt godt vare på, fått klærne vasket og nærmest stelt og dullet med. I det hele tatt topp service. Anbefales på det varmeste for alle \"Norge på langstravere\". Nå er det sent og jeg tar kvelden. I morgen bærer det inn i <a href=\"http://www.dirnat.no/borgefjell/\">Børgefjell</a>, men først turen langsetter Namsvatnet.','Limingen Gjestegård','','publish','open','open','','limingen-gjestegard','','','2008-07-03 10:12:31','2008-07-03 08:12:31','',0,'http://falleri.no/2008/07/03/limingen-gjestegard/',0,'post','',1),(156,2,'2008-07-04 13:06:04','2008-07-04 11:06:04','Denne dagen er det  klart, solrikt og varmt. For hunden Najax skyld ordner jeg bilskyss fra<a href=\"http://visitborgefjell.no/index.php?side=1012&amp;a=13\"> Limingen Gjestegård</a> til Fredheim ved Namsvatnet. Najax må få slippe asfalten på en glohet dag. Strekningen på 14 km kommer jeg til å gå senere, mest sannsynlig i august til neste år.\n\nEtter  råd fra en lokalkjent har jeg lagt om ruta noe. Turen videre går nå på en tømmervei. Jeg tar av fra denne litt lengere framme og går rabbene mot Gollomselva. Der skulle det etter sigende være bro over elva. Det stemte ikke. Kartet var nok det som viste riktig; ingen bro her. Vannføringen i elva var heldigvis liten og på en terskel i elva gikk det greit over. Videre fram til Storvika var rabbene ganske så tettvokst og tildels med tett kjerr. Jeg gikk derfor for det meste myrene innover. Etter dagens sol og tørt vær i flere dager var disse greie og lette og gå.\n\nDagen ble utrolig varm og vi ble nødt til å ta mange og lange raster. Senere ved Viermanestet traff jeg folk og fikk opplyst at det hadde vært over 30 grader denne dagen. Som en illustrasjon nevner jeg om en rast med nøttesjokolade: Den måtte spises med skje da sjokoladen var helt flytende i sølvpapiret. Lett blodtapping ble det også underveis. Klegg, mygg og blinding var midelst talt iltre.\n\nJeg teltet ganske tidlig i Storvika på en superb plass. Like nedenfor teltet på svaberg i vannkanten blåste så pass at mygga stort seg holdt seg borte. Med bål, matlaging og bading ble det en riktig behagelig og sval kveld.','Røyrvik  - Storvika/Namsvatnet','','publish','open','open','','r%c3%b8yrvik-storvikanamsvatnet','','','2008-07-04 13:06:04','2008-07-04 11:06:04','',0,'http://falleri.no/2008/07/04/r%c3%b8yrvik-storvikanamsvatnet/',0,'post','',0),(157,2,'2008-07-05 13:12:59','2008-07-05 11:12:59','Det er fortsatt varmet, men heldigvis ikke så ekstremt varmt som i går. Turen fram til Viermahytta  er bare på 12-13 km. Der på hytta skal jeg møte mine turkamerater Tor B. og Olav Erik for den videre ferd inn i Børgefjellet. Traseen fra Storvika er udramatisk. Jeg skjærer av fra vika og opp i høyden. Går forbi vest om Lomtjønnin og ned mot brua over Orvasselva. Det er ingen stier i dette området og jeg bommer med en liten kilometer.  Jeg må derfor traske litt rundt i et landskap formet av arbeidssomme bevere før jeg kommer fram til brua. Det er utrolig at et lite dyr kan felle bjørk med tverrsnitt på anslagsvis 20 cm, men trærne lå nå der. Et lyn- og tordenvær med voldsomt regn og store hagel gjorde meg gjennomvåt på et øyeblikk. Men bortsett fra på beine tørket jeg raskt opp da uværet dro forbi. Jeg kom fram til Viermahytta ganske så pent etter avtalt tid.\n\nMange gode timer ble det med bål, kaffekos, god mat, fisking, prating og ikke å forglemme en liten \"kommar\". Fossefallene  i Storelva må også tas med. Disse var gedigne og imponerende. Det er utrolig som vann kan fasinere når det kaster seg ned fjell og berg i store mengder. Natta tilbringer vi under tak i Viermahytta og sparer teltingen til Børgefjell.','Storvika/Namsvatnet - Viermahytta','','publish','open','open','','storvikanamsvatnet-viermahytta','','','2008-07-05 13:12:59','2008-07-05 11:12:59','',0,'http://falleri.no/2008/07/05/storvikanamsvatnet-vierma-hytta/',0,'post','',0),(158,2,'2008-07-06 13:19:29','2008-07-06 11:19:29','Vi starter alle fire litt ut på dagen for å gå opp til Viermavatnet. En middels lang tur, men Viermadalen kan være dryg nok. Litt nord om Sapmanselva tar vi en lengre rast med bål og kaffe. Sola varmer, men vi har en lett bnris fra nordøst. Brisen gjør værlaget meget behagelig. Med ly for vinden når vi raster og et kjølig drag i lufta når vi går, rett og slett det perfekte turværet. Fram til Viermavatnet når vi sånn passe tidlig ut på ettermiddagen. Mens to av oss slår opp teltene går Olav Erik ned til vannet med stang, mark og dupp. Perfekt timing: omtrent samtidig med at teltene er reist og kaffen kokt kommerer fiskeren tilbake med ørret til middagsmaten. Børgefjell på sitt beste, skyfri himmel, telt ved vannet, og nydelig ørret på kok. En litt kjølig kveld etter turen opp til Viermavannet og oss alle i riktig godlage. Med ly for vinden og en varmende kveldssol til maten blir dagen perfekt. Starten inn i Store Bøegefjellet kunne ikke vært bedre timet.','Viermahytta - Viermavatnet','','publish','open','open','','viermahytta-viermavatnet','','','2008-07-06 13:19:29','2008-07-06 11:19:29','',0,'http://falleri.no/2008/07/06/viermahytta-viermavatnet/',0,'post','',0),(159,2,'2008-07-07 13:30:12','2008-07-07 11:30:12','Tar du en titt på kartet ser du at det ikke var akkurat det lange strekket vi tilbakela denne dagen. Men god tid er klart en del av hensikten med turen gjennom Børgefjellet. Min \"Norge på langs-tur\" skal ikke være et stress. Vi skal ta det rolig og riktig ha noen gode og late dager inne i fjellet.\n\nHer oppe ved Steinvatnet kommer vi opp i høyder på over 1000 moh. Isen ligger ennå på vannene og det er masser av snøfonner innover fjellsidene. Riktig en drømmedag for Najax med bading mellom isråkene, rulling og aking/skliing nedover snøfonnene, samt en kløv som ikke er alt for tung. For oss andre tre er ikke fiskeforholdene de beste og middagen blir etter hva sekkene måtte inneholde. Alt fra <a href=\"http://www.norskdesign.no/emballasjedesign/helt-rein-og-helt-vilt-article3199-217.html\">tørket reinkjøtt</a> (nam-nam) til posemat og potetmos. Telting skjer på en fin og slett morenerygg. Alt i alt ingen dårlig dag og kveld i den kanskje mest golde delen av Børgefjellet.','Viermavannet - Steinvatnet','','publish','open','open','','viermavannet-steinvatnet','','','2008-07-07 13:30:12','2008-07-07 11:30:12','',0,'http://falleri.no/2008/07/07/viermavannet-steinvatnet/',0,'post','',0),(160,2,'2008-07-08 13:36:12','2008-07-08 11:36:12','En litt lengre etappe enn gårdagens, men også denne dagen skal nytes. Været er skjønt og omgivelsene er bare nydelige. Snedekte fjelltopper og fjellsider, innimellom irrgrønne lier, noen isdekte vann, noen blinkende vann, strømmende elver, strie elver, stilleflytende elver, fjellbekker, kalt og godt drikkevann, lemen, ryper, fjelljoer, bare herligheter. Blomster og urter,  kartlav og rustlav, vier og kjær, myr, tørrrabber og dryasmark. Alt er fryd, stillhet og spektakulære senarier. Midt opp i det hele oss tre små mennesker med en hund. Ahh, det glemmer jeg nesten, store flokker med rein oppe i fjellsidene og innover snøfonnene. Vi er i samenes og reinens rike, men bare tillåns og med nemsom fremferd.\n\nSom en god illustrasjon på freden vi føler kommer følgende kommentar fra Olav Erik etter vi har spist ved et av vannene: \"Ser dokker, då vi kom og rastet for lunch lå isen på vatnet. Nu når vi e´ ferdig e´ isen gått\". Tidlig kveld ved Ránoenjokhe, ingen fisk og resten av provianten går for det meste med.','Steinvatnet - Rátnoenjokhe','','publish','open','open','','steinvatnet-ratnoenjokhe','','','2008-07-08 13:36:12','2008-07-08 11:36:12','',0,'http://falleri.no/2008/07/08/steinvatnet-ratnoenjokhe/',0,'post','',2),(161,2,'2008-07-09 13:42:00','2008-07-09 11:42:00','Fri for mat (nesten) må vi bare si farvell til Børgefjell for denne gang. Turen ned mot Tiplingan går forbi masser av små tjønner med vakende ørret i solskinnet. Tiplingelva krysses over hengebrua midt mellom de to Tiplingvatna. Najax er virkelig flott å se på når han som den første tar seg over en svingende hengebru med manglende sidestabilistor og et godt åpent brudekke og bordene på langs. Vel over brua går vi innom hytta til gamle optiker Stensland. Han var ennå ikke kommet, men det ble en rast på trappa før turen videre opp stien mot Susenfjellenden. På veien ned fra fjellet treffer vi på de første andre fjellvandrerne. Tre bergensere, en far med to tenåringsgutter er på vei inn i Børgefjellet for å nye livet der inne. I 10 dager skal de vandre, telte, fiske og riktig leve livet. God tur til dere alle og må de to guttene få noen flotte dager å minnes.\n\nVi krysser Susna ved Oksvollen og blir hetet av Tor B sin kone Tone. På Golvertun på Trallerudmoen venter en kald pils, en kjempeposjon av årets jordbær med sukker og fløte, og deretter en god dusj hos noen \"nære\" naboer; Eileen og Ansgar.','Ránoenjokhe - Golvertun/Susendal','','publish','open','open','','ranoenjokhe-golvertunsusendal','','','2008-07-09 13:42:00','2008-07-09 11:42:00','',0,'http://falleri.no/2008/07/09/ranoenjokhe-golvertunsusendal/',0,'post','',0),(162,2,'2008-07-10 13:46:38','2008-07-10 11:46:38','For oss to fjellfanter; Tor B og Gunnar (Olav Erik er dratt hjem) er dette en god dag på hytta. Det er tross alt behagelig å spise ved et bord, få servert veltillaget mat og riktig samle krefter. Hytta til Tone og Tor B ligger vakkert til på en høy elvebredd på østsiden av Susna rett overfor to sideelver. Begge to, lille Susna og Klijviejokha, drenerer ut i Susna rett ovenfor. Noe av det mest unike er at ingen av elvene er ødelagt med regulering eller på annen måte utsatt for inngrep. Alle elvene flyter fritt.\n\nFor Najax derimot ble ikke dagen like bra. Brått ser Tone at hunden halter og ikke vil trå ned på den venstre framfoten. Bedre blir han heller ikke utover dagen og det er ingen annen råd ennå ringe veterinær. Kjøring 6 mil ned til Trofors. Veterinæren Roy Anders Berg går grundig og betryggende til verks. Han konstarerer ingen brudd oppover i overarm og legg, alle små ben hele og uten brudd. Mest sannsynlig kun et skadet klofeste, men med ganske store smerter.  Smertestillende og Najex vil få skyss hjem til Mo i Rana. Jeg går videre fram til Umbukta og i mellomtiden skal Najax få hvile og ro. Hunden vil være klar for ny innsats når vi etter planen starter opp\nigjen fra Umbukta i siste halvdel av måneden.','Golvertun','','publish','open','open','','golvertun','','','2008-07-10 13:46:38','2008-07-10 11:46:38','',0,'http://falleri.no/2008/07/10/golvertun/',0,'post','',1),(163,2,'2008-07-11 15:13:22','2008-07-11 13:13:22','Tor B. Kjører meg opp til Harvasstua og jeg er i gang igjen. En lat dag på Golvertun har gjort underverker med både kropp og sjel.\n\nNå er jeg i starten på den merkede delen av <a href=\"http://www.nordlandsruta.no/\">Nordlandsruta</a> og kommer stort sett til å følge den videre. Merket rute eller ikke, kart og kompass er allikevel et must. Kartet henger alltid på brystet og kompasset med utstukket kurs klart i en sikret lomme. Stien oppover mot Daningen er god, nesten for god og jeg følger den litt for langt. Jeg skulle ha brutt av tidligere og gått fjellryggen fram til brua mellom Daningen og Bliehrekke. I stedet ble det kjerr å brøyte seg gjennom i liene langs Daningen. Bruker sikkert en halv time ekstra på feil veivalg, men vel over hengebrua er det videre greit å gå. Et åpent fint fjellandskap og en godt merket sti. Litt opp og ned, alt er som det skal være. <img src=\"http://www.flickr.com/photos/gisle/2713502758/\" height=\"20\" width=\"20\" /> Elva ut fra Rorvatnet var lita, snøsmeltinga er for det meste ferdig. Ved snøsmelting og flom kan denne elva være krevende å krysse, men akkurat nå var det bare å gå rett over. Jeg hadde fått opplyse om åpen hytte ved Rotvatnet, men nå var den låst. I og med jeg først startet i ettida var det meningen å overnatte her ved vannet. Det ble telting på en bra plass på østsiden litt opp fra vannet, sånn ca. midtveis. Jeg prøvde noen kast med stang of spinner. Dessverre negativt og det ble ertersuppe og tørket reinkjøtt til kveldsmiddagen min.','Golvertun/Harvasstua - Rotvatnet','','publish','open','open','','golvertunharvasstua-rotvatnet','','','2008-07-11 15:13:22','2008-07-11 13:13:22','',0,'http://falleri.no/2008/07/11/golvertunharvasstua-rotvatnet/',0,'post','',0),(164,2,'2008-07-12 15:36:40','2008-07-12 13:36:40','Tidlig oppe til en nydelig dag. Jeg har til hensikt å ta igjen noe av den distansen jeg ligge bak etter min opprinnelige plan. Men helt fram til Tverrelvnes og Gunder Gardsmark kan bli litt i lengste laget denne dagen.\n\nFjellene innover fra Rotvatnet og fram til Midtfjellet er lettgåtte. Det som tar noe tid er en fantastisk rik blomsterflora innover fjellet. Stadig må kameraet fram og det blir mange fine bilder. Av dyr og fugler er det derimot lite å se. Noen få oksereiner, de er skilt fra simlene og kalvene som er i <a href=\"http://www.susendal.no/Borgefjell.html\">Børgefjell</a>. Av fugler er det heiloene i tillegg til noen bekkasiner som holder meg med selskap. Ingen ryper og kun en enslig fjellvåk med sin karakteristiske  varseltone av de store rovfuglene. Til gjengjeld følger den meg et godt stykke på vei.\n\nLia ned fra Midtfjellet mot Unkervatnet ser bratt ut på kartet og den var bratt. Ganske så tung å gå nedover med sekk på ryggen. Et stykke nede i lia treffer jeg på to damer på vei opp. Oppe på toppen av Midtfjellet er en av postene til Hattfjelldals kommunes \"Fjelltrimmen\". Tøffere poster enn det Hattfjelldal har lagt opp til skal du lete lenge etter og må tyde på mange spreke innbyggere. Vel ned og ved veien kommer jeg til en nedlagt liten gård. Her bodde Olaf Klementsen, en samisk skogsarbeider med sin familie. Stedet er fredet og er en del av \"Fotefar mot nord\".\n\nHengebru over Skarmodalselva, fylkesveien fram til Ørnes og stien videre oppover lia derfra. Det ser ut til å være lite bekker oppover. På ei av hyttene jeg passerer går jeg derfor ned for å få etterfylt min vannbeholder. Til etterfyllingen ble jeg servert kaffe og brus, gullrotkake og lefse med gomme. Slike tjenester som vannpåfylling her nord kan ta noe tid. Jeg bukker og takker vertskapet for en hyggelig time jeg fikk sammen med dere. Nå er det bare å komme seg opp Ørneslia og ta de siste seks til sju kilometerne fram til Sæterfjellet. Ved en av bekkene frampå fjellet finner jeg hva jeg trenger. En god og tørr lyngrabbe med en flott utsikt mot <a href=\"http://home.online.no/~oe-aase/okstindene.htm\">Okstindene</a> i nordvest. Det er nesten så jeg føler meg hjemme igjen.','Rotvatnet - Sæterfjellet','','publish','open','open','','rotvatnet-s%c3%a6terfjellet','','','2008-07-12 15:36:40','2008-07-12 13:36:40','',0,'http://falleri.no/2008/07/12/rotvatnet-s%c3%a6terfjellet/',0,'post','',0),(165,2,'2008-07-13 20:03:07','2008-07-13 18:03:07','En ambisiøs    etappe, jeg beregner den til et sted mellom 27 og 28 kilometere. Fjellområdet jeg for det meste skal gå i er derimot stadig for det alt vesentlige det fine lette terrenget. Først av alt ble det en morgenvask i fjellbekken. Full kroppsvask denne gangen, fra hode til tær, kalt men skjønt.\n\nFra Sæterfjellet og ned til Tverrelvnes  går det radig. For det meste slake unnabakker på en god sti, noe på vei og ikke for bratt opp til gården Tverrelvnes. Der blir jeg møtt og tatt i mot av Gunder Gardsmark og budt inn på kaffe. Han og kona disker opp. Kaffekjøtt ble det også med kjøtt fra en riktig feit og fin sau. Bare nydelig for en fettsugen fjellvandrer. Med trivelig prat og gode råd gikk tida og først godt og vel 1330 var det på han igjen. Rått regnet 21 km igjen til Krutvatnet og <a href=\"http://www.statskog.no/index.asp?\">Statskogs</a> åpne hytte. Der var mitt opprinnelige planlagte stopp for dagen.\n\nSom tidligere sagt, fjellene her er lettgåtte. Ikke for bratte og med et godt og fast dekke å gå på. Lite stein og ingen vanskelige elver å krysse. Det går greit innover, litt regn, men ikke til å bli skikkelig våt av. Derimot knapt et vindpust og riktig stasvær for mengder av mygg, klegg og fluer. Fri for myggolje ble jeg rene foringsmaskinen og etter capsen å dømme ga jeg mye blod. For andre fjellvandrere, dere finner en åpen hytte i nordenden av Mosvatnet. Det er ei åpen reingjeterhytte, enkel, men helt grei for å ta inn i. Dersom noen planlegger å bruke den anbefaler jeg å ta med litt ved fra lavere områder. Det er ovn, men det kan være dårlig med brensel og vi skal vel heller ikke bruke av den veden samene har kjørt inn.\n\nVidere innover forbi Brunreinvatnet trasker jeg trasker jeg i veg. Her inne hadde jeg blitt anbefalt Grensetjørne som gode ørretvann med fine teltplasser. En stopp her med fiske krever nok en ekstra liggedag. Jeg har derimot andre planer og et annet område jeg har tenkt å ligge over ved. Går derfor forbi og ned til Krutvatnet. Det siste stykket forbi Fisktjørna og til Statskoghytta (GPS-pos. 74470 - 86700) går på en meget god og lettgått sti. Hytta ble oppsatt i 1997 og er åpen med gass, ved og parafinlampe.\n\nTil slutt må jeg få gi skryt til de ansvarlige for rutemerkinga på denne delen av <a href=\"http://www.nordlandsruta.no/\">Nordlandsruta.</a> Merkinga var god, til dels meget god. Bare på noen få plasser tok jeg feil. Det var mest for at jeg selv var uoppmerksom, men med kursen på kompassen er det lett å ta seg inn igjen. Den siste delen derimot fra omtrent Rauvassdalen og videre mot Fisktjørna manglet det til dels merking. For en uerfaren og mindre sikker person kan dette muligens by på noen problemer. <a href=\"http://www.turistforeningen.no/brurskanken/\">Brurskanken turlag</a>, er det dere som har ansvaret har dere gjort en flott og imponerende jobb.','Sæterfjellet - Krutvatnet','','publish','open','open','','s%c3%a6terfjellet-krutvatnet','','','2008-07-13 20:03:07','2008-07-13 18:03:07','',0,'http://falleri.no/2008/07/13/s%c3%a6terfjellet-krutvatnet/',0,'post','',0),(166,2,'2008-07-14 20:44:56','2008-07-14 18:44:56','Litt regn og tåke er det oppe i fjellen nå på morgenparten. Jeg skriver mine kladder til bloggen inne på Statskoghytta. Spiste, ordnet hytta og gjorde meg klar for neste etappe, ca. 19 km fram til Åke Sæterstad ved <a href=\"http://www.krutfjellvegen.net/ipub/pages/overnatting.php\">Famnvatnet.</a> Da jeg forlot hytta nærmere 1200 var solen kommet fram og tåka hadde lettet. Alt lå til rette for nok en herlig dag i fjellene. Langs Krutvatnet var stien nettopp ryddet for småkjerr og busker. Tørr og flott å gå. Opp mot Ole-Anderstjønna treffer jeg på et eldre ektepar. Selvsagt var også dette Hattfjelldalinger på vei fra en fjelltrimpost.\n\nVed Ole-Anderstjønna er det satt opp en fin gapahuk med grill- og bålplass under tak. Ikke noe for overnatting, men fin for ly en regnfull dag. Vær også oppmerksom på at Ole-Anderstjønna er et godt fiskevann. Videre innover går fjellet oppover i slake stigninger. Det er lettgått, men likevel tar det lang tid å ta seg fram. Jeg går rett og slett i et fjellets blomstereng. Blomster overalt, i alle farger, små og store, ja helt ned til museørets små røde blomster og de som er ennå mindre. Dette er tiden da alt blomstrer og jeg må stadig ned på kne og mage med kameraet. Med sekken på ryggen blir etter hvert lårene ganske så møre. På denne måten går det til jeg er forbi Fiskløysvatnet og helt til jeg runder ved Reinhagen og går nordøstover. Nå er det bare å gå på til målet for dagen.\n\nHos Åke Sæterstad er det ikke lengre mulig å få overnatting. Derimot ca. 2 km lengre ned på Sivertgården er det hytte å få leid. Vil du ha det riktig standsmessig kan du leie gammelstua fra 1850. Den har eierne, Stine og Per-Sverre Engdal restaurert og satt pietetsfullt i stand. Stua er et klenodium i fjellheimen, men kanskje litt vel mye for en enslig vandrer. For flere eller en familie må Sivertgården og Famnvatnet være ideell. Ta en titt på hjemmesiden <a href=\"http://www.sivertgarden.no/\">www.sivergarden.no.</a> Jeg derimot leide den enkleste hytta til en meget rimelig penge. Som et lite apropos, presten i Hattfjelldal har valgt å bosette seg her på Sivertgården. Tross 7 mil til kirken leier han et av husene på gården og bor der med sin kone.','Krutvatnet - Sivertgården','','publish','open','open','','krutvatnet-sivertsgarden','','','2008-07-14 20:44:56','2008-07-14 18:44:56','',0,'http://falleri.no/2008/07/14/krutvatnet-sivertsgarden/',0,'post','',0),(167,2,'2008-07-15 22:15:22','2008-07-15 20:15:22','Etter en god dag, en deilig fredfull natt og en herlig formiddag på Sivertgården planlegger jeg for to korte og lette etapper med telting og fiske. I dag er målet Ingetjørna, hele 5 km å gå fra  fra Sivertgården. Kort og greit. Jeg vandrer først stien vestover mot Varntresk, dreier østover ved Møllebekken i retning mot Storbekktjørna og Ingetjørna. Strake veien langs Kilvassaksla på en gammel, delvis rødmerket, men god sti. Etter mindre enn 2 timer er det ennå tidlig ettermiddag når slår jeg opp teltet på en høyde like ved tjønna. Jeg monterer fiskestanga og tar en tur rundt vatnet. Dessverre, ikke et napp i Ingetjørna. Heller ingen fiskelykke i Storbekktjørna som jeg besøker neste morgen. Uansett har jeg  en behagelig og avslappende dag, akkurat som planlagt. Ikke noe stress, bare meg i fred med naturen og alt liv som pulserer rundt meg.','Sivertgården - Ingetjørna','','publish','open','open','','sivertgarden-ingetj%c3%b8rna','','','2008-07-15 22:15:22','2008-07-15 20:15:22','',0,'http://falleri.no/2008/07/24/sivertgarden-ingetj%c3%b8rna/',0,'post','',0),(168,2,'2008-07-16 23:03:25','2008-07-16 21:03:25','Denne dagen har jeg også bare en kort tur foran meg. Fram til Tiurskardet og ned til tjørnene er det neppe mer enn 6 km. Ingen grunn til å haste på selv om jeg forventer bedre fiskelykke i Tiurskardtjørna enn det jeg hadde i Ingetjørna og Storbekktjørna. På veien over fjellet tar jeg det pent og rolig og riktig nyter fornuften i hva en helt avslappende dag kan gi. På veien ned mot tjørnene ser jeg fisken vake i det søndre vannet. Og selvfølgelig stiger håpet for en kveld med bål og nytrukket ørret.\n\nVed utløper av nordre Tiurskardtjørna finner jeg min hittil fineste teltplass. Like ved utløpet, to meter fra elva, et passe høyt elvebrus, et ypperlig underlag for teltet, tørt og mykt, og med en fantastisk utsikt til vann og fjell i alle retninger. Nå er det bare bålet og fisken som mangler., men dessverre, med ørret ble det igjen magert. Nordtjørna var grunn og både sluk og spinner dro med seg bunnvegetasjon for hvert eneste kast. Det var heller ikke noe fisk å få i elva og dermed ble kveldsmiddagen den vanlige. I dag med gul ertersuppe og tørket reinkjøtt dertil. Skam og si, men jeg lurer ofte på hvordan jeg skulle overlevd uten reinkjøttet fra <a href=\"http://www.norskdesign.no/emballasjedesign/helt-rein-og-helt-vilt-article3199-217.html\">ARV</a>. Jeg spiser det til lunch, til kveldsmiddag og såmen blir det av og til nattmat med reinkjøtt.','Ingetjørna - Tiurskardtjørna','','publish','open','open','','ingetj%c3%b8rna-tiurskardtj%c3%b8rna','','','2008-07-16 23:03:25','2008-07-16 21:03:25','',0,'http://falleri.no/2008/07/24/ingetj%c3%b8rna-tiurskardtj%c3%b8rna/',0,'post','',0),(169,2,'2008-07-17 18:03:43','2008-07-17 16:03:43','Jeg er tidlig oppe for å prøve fiskelykka i den søndre Tiurskardtjørna. Vak så jeg da jeg kom over fjellet i går ettermiddag. Det er nok røyr som er i disse vatna og en rolig formiddag med nytrukket røye til lunch ville vært en ideel start på nok en rolig dag. Skuffelsen siger innpå da også denne tjørna viser seg å være grunn. Samme resultat som i går. Ingen fisk, men bunnvegetasjon hvert eneste kast. Resultatet blir derved en tidlig frokost og alt kl. 0800 er leiren brutt og ryddet.\nKursen legges med <a href=\"http://www.hemnesturistforening.no/archives/category/hytter/gressvasshytta\">Gressvasshytta</a> som  mål. 32 km beregner jeg avstanden til å være. Kanskje noe i lengste laget, men jeg får se hvordan dagen utvikler seg.\n\nTuren ned Tiurskardet og opp Aksla er grei. (Et lite apropos, om 14 dager vil de som kommer etter meg kunne vasse i store mengder modne multer). Derimot går det tungt ned nordsida av fjellet. Etter litt regn er vegetasjonen og underlaget vått og glatt. Tett høystaudevegetasjon skjuler bunnen. Bratt og steinete er det også og det er derfor bare å ta nedturen langsomt og forsiktig. Vel gjennom og lengre ned kommer jeg inn i stor gammel bjørkeskog. For meg ser den nærmest ut som urskog å regne og gir et trolsk og fascinerende inntrykk. Like før Storelva kommer jeg til fredet furu, Storfurua. Denne furua er, sitat \" . . den siste gjenlevende av de trærne som utgjorde fjellskogen i dette området for 400-500 år siden. Vi må anta at klimaendringer (kjøligere og fuktigere vær), samt menneskers og dyrs påvirkning, har bidratt til at furuskogen har blitt borte. Blant annet vet vi at i nødsårene på 1600 og 1700-tallet gjorde folk nytte av furubark i mjølet som de laget brød av. Det ble også felt skog til trevirke og tjærebrenning.\"\n\nEtter en god rast går jeg ned til veien og nordbukta. Tar av like før Granheim og starter på veien inn mot og opp fjellet aust om Spjeltfjelldalen. Dalen er vakker der den ligger nedenfor meg. Et Frodig og rik, et reservoar og oppvekstområde for dyr og fugler. Spesielt er <a href=\"http://nordland.miljostatus.no\">Spjeltfjelldalen</a> kjent for sin rike bestand av ryper. Etter Skrikarbekken tar det til med nokså mye myr. Med noe regn denne dagen er myrene våte, men ikke så alt for tunge å gå. Framme ved Balskoia var denne en skuffelse. Koia er rett og slett ikke brukbar til en rast. Jeg fant derfor i stedet en tørrabbe og hengte opp teltduken til beskyttelse mot vind og regn og fikk i meg litt varmt og noe mat. Med bare 7-8 km igjen til Gressvasshytta var det nå egentlig bare en ting å gjøre. Få teltduken ned i sekken. Ta beina fatt og komme seg fram til Gressvasshytta og overnatting i en tørr og god hytte.','Tiurskardtjørna - Gressvasshytta','','publish','open','open','','tiurskardtj%c3%b8rna-gressvasshytta','','','2008-07-17 18:03:43','2008-07-17 16:03:43','',0,'http://falleri.no/2008/07/17/tiurskardtj%c3%b8rna-gressvasshytta/',0,'post','',0),(170,2,'2008-07-18 00:10:39','2008-07-17 22:10:39','Nok en etappe på vel 30 km står for tur. Men med denne etappen gjør jeg en liten vri. Fra Gressvasshytta fram til Gressvassdammen går jeg med normal sekk. Der setter jeg igjen alt jeg ikke trenger for dagen. Blant annet telt, sovepose, ekstra klær, fiskeutstyr osv. Det er fordelen med å komme hjem. Alt dette utstyret kjører jeg opp og henter dagen etter.\n\nFra Kjensvassmoen går jeg innover Storskaret med uvanlig lett sekk. I et fint hjemlig terreng går det derfor ekstra lett innover. Oppe i Storskaret tar jeg min første lange rast. Sola skinner og livet er bare herlig. Etter runding av Grassfjellurda er Grassfjellkoia inne ved Djupdalsbekken neste naturlige stopp. Så langt har jeg ikke truffet på andre folk etter at jeg forlot Sivertgården, men i gjesteboka her i koia begynner jeg å finne de første hjemlige kjente navnene. Rart med det, men bare å se kjente navn gjør godt.\n\nFra Grassfjellkoia er det en noe mer kuppert terreng fram til Umbukta Fjellstue. En kan si hva en vil, men vel 60 km på to dager kjennes i beina. Det er derfor godt å komme fram til Fjellstua, bestille en god og kald pils, og så ut i friluft, få av seg på beina og vifte med tærne. Jeg riktig nyter sivilisasjonens goder mens jeg vente på skyss hjem til <a href=\"http://no.wikipedia.org/wiki/Mo_i_Rana\">Mo i Rana</a>.','Gressvasshytta - Umbukta Fjellstue','','publish','open','open','','gressvasshytta-umbukta-fjellstue','','','2008-07-18 00:10:39','2008-07-17 22:10:39','',0,'http://falleri.no/2008/07/18/gressvasshytta-umbukta-fjellstue/',0,'post','',0),(171,2,'2008-07-28 10:38:43','2008-07-28 08:38:43','Min opprinnelige plan om å starte opp igjen fra Umbukta 19. juli er helt forkastet og er utsatt til 1. august. Det er flere grunner til forsinkelsen og ikke bare latskap. 2 favner ved ble levert og må tas vare på og lagres. Plener må stelles og slås. En sønn flytter til ny leilighet og hjelp behøves både til flytting og vasking av gammel bolig. Diverse småting i forbindelse med turen må videre ordnes. Alt dette tar mer tid enn en skulle tro og hva jeg selv hadde regnet med.\n\nUt over det jeg har nevnt har jeg med lengsel ventet på min nye ryggsekk. Sekken er nyutviklet fra den svenske produsenten <a href=\"http://www.klattermusen.se/start.php?lang=SE\">Klättermusen</a>. En genial konstruksjon bassert på en symbiose av gamle velprøvde og helt nye teorier for bærekomfort. Sekken med typenavnet \"Mjölner\" ble 2 måneder forsinket fra leverandør. Da jeg endelig fikk beskjed om at den kom til Stockholm med fly og kunne leveres meg 30. juli fra Rana Spesialsport valgte jeg å vente. Prototypen har jeg prøvd og det skal bli spennende å få prøvd sekken i full målestokk. For de som måtte være interessert, utførlige rapporter både på godt og ondt vil bli lagt ut på min blogg etter hvert.\n\nOppstart skjer fra <a href=\"http://www.turistforeningen.no/rana/\">Virvatnet</a> og ikke fra Umbukta. Tross alt har jeg tatt strekket Umbukta - Virvatnet mange ganger. Både på ski og til fots på barmark, fram og tilbake utallige ganger. Et problem har jeg igjen som må løses.  Arctic Rein og Vilt kan ikke levere meg mer tørket reinkjøtt med det første. Jeg håper at en dialog med dem kan gi en løsning på problemet. <a href=\"http://www.norskdesign.no/emballasjedesign/helt-rein-og-helt-vilt-article3199-217.html\">ARVs</a> spesielle reinkjøtt er tross alt min viktigste kilde til fett og proteiner.','Mo i Rana - 19.-31. juli','','publish','open','open','','mo-i-rana-19-31-juli','','','2008-07-28 10:38:43','2008-07-28 08:38:43','',0,'http://falleri.no/2008/07/28/mo-i-rana-19-31-juli/',0,'post','',0),(214,2,'2007-05-19 01:36:04','2007-05-18 23:36:04','Jeg vil gjerne takke mine sponsorer som har vært så vennlig å utstyre meg for turen.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/hilleberg-150x150.jpg\" alt=\"Hilleberg\" />\nHvorfor valgte jeg teltet Nallo 2 GT fra <a href=\"http://www.hilleberg.se/EUROPE.HTM\">Hilleberg</a>?  Jeg falt rett å slett for løsningen med forteltet. Når dertil Hilleberg er kjent for gode produkter var valget enkelt. Jeg ser fram til å få brukt teltet på min tur \"Norge på langs\".\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/logo_mbt-150x150.jpg\" alt=\"MTB Logo\" />\n\"Norge på langs\" går til dels på vei og asfalt. Er det noe som røyner på må det være mil etter mil med asfalt. Her var det at MBT-skoen fra <a href=\"http://www.swissmasai.no/\">Masai Norge AS</a> kom som dalende fra himmelen. Aldri har jeg hatt noe bedre på på hardt og slett underlag. Vent på min rapport og vurdering.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/08/klattermusen.png\" alt=\"Klättermusen\" />\nPå min vei mot Nordkapp bruker jeg i år en del utstyr fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a> - Blant annet:  Mjölner, en helt nyutvikklede ryggsekk  - Tivar dunsovepose  - Liv dunjakke  - Mithril bukse. Testen av dette utstyret vil bli en interresant erfaring å ta med seg.\n\n<img src=\"http://falleri.no/wp-content/uploads/2007/05/baltologosekundar.thumbnail.jpg\" alt=\"Balto Logo\" />\nFor Najax, min Golden Retrivier er \"Norge på langs\" en stor utfording. Deler av turen går med opptil 14 kg i kløven og protein- og fettbehoved blir stort. Spesialforet <a href=\"http://www.baltoshop.no/\">Balto Competition</a> sammen med annet høyverdig tørrfor blir hovedføden. I tillegg blir det vel også spedd på med en del fisk.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/wwwarctic-circleno-150x150.jpg\" alt=\"Arctic Circle Logo\" height=\"115\" width=\"115\" />\nVårt lokale turistkontor, <a href=\"http://www.arctic-circle.no/\">Turistkontoret Mo i Rana</a> og Polarsirkelen Reiseliv er unikt interresert i friluftsliv og har skaffet meg alle kartene jeg trenger til en meget gunstig pris.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/rana-jakt-og-villmark-150x150.png\" alt=\"Rana Jakt og Villmark Logo\" height=\"104\" width=\"121\" />\nDet er mangt som skal til for en vellykket \"Norge på langs\". Listen med utstyr, både av  smått og stort ble lang. Hadde ikke <a href=\"http://www.jaktogvillmark.no/\">Rana Jakt og Villmark AS</a> det jeg trengte, skaffet de det eller fant et godt alternativ. Servicen var topp og i tillegg ga de meg ekstra gode priser.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/stensland.png\" alt=\"Specsavers Stensland\" height=\"72\" width=\"130\" />\nVed et tilfeldig besøk hos <a href=\"http://old.monet.no/viggo/interoptik/index2.php?link=visjon\">Specsavers Stensland</a> ble jeg anbefalt å bruke månedslinser på min videre ferd mot Nordkapp. Jeg syntes ideen var god og etter en en nitidig og grundig tilpassing er jeg nå på vei med linsene på og brillene i sekken. Ingen problemer videre med nedduggede briller.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/rss.gif\" alt=\"Rana Spesialsport\" height=\"90\" width=\"133\" />\nPå min vei mot Nordkapp bruker jeg i år en del utstyr fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a> levert av <a href=\"http://www.spesialsport.no/\">Rana Spesialsport</a>.','Takk til sponsorer','','inherit','open','open','','19-revision','','','2007-05-19 01:36:04','2007-05-18 23:36:04','',19,'http://falleri.no/2007/05/19/19-revision/',0,'revision','',0),(173,1,'2008-07-29 20:59:55','2008-07-29 18:59:55','','Specsavers Stensland','','inherit','closed','open','','specsavers-stensland','','','2008-07-29 20:59:55','2008-07-29 18:59:55','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/stensland.png',0,'attachment','image/png',0),(176,2,'2008-07-30 18:21:16','2008-07-30 16:21:16','<em>Jeg vil på denne måten gi en takk til alle, både mine nærmeste og andre som på en eller annen måte har gjort turen mulig. Enten direkte til hjelp eller gitt meg assistanse underveis.</em>\r\n\r\nFørst og fremst gjelder det min kone Sara som har tatt belastningen med å ha meg gående alene bare med hunden i fjellet.\r\n\r\nDernest min sønn Gisle som har forberdt og vedlikeholder min hjemmeside. Han oppdaterer også bloggen etter hvert som jeg sender E-post og bilder fra turen. Gisle, sammem med Flemming og Mikkel kjørte meg også fra Bergen til Lindesnes. Deretter kom han til Vigeland og hentet Najax hjem til seg slik at jeg kunne komme meg videre. Takk til Gisle, Anne Marie og barna som tok seg av Najax mens jeg gikk fra Lindesnes til Sveet.\r\n\r\nI 2008 var det min andre sønn Eskil som tro til. Han hentet Najax og unødig utstyr på Stødi der jeg måtte ut å gå på E6. Han kom deretter med hund, proviant og fjellutstyret til Megården. Kjørte meg gjennom 15 veituneller (47 km) til Kobbvatnet.\r\n\r\nEspen Holmesland på Vigeland som lot meg og Najax slå opp telt i hagen og kjørte oss til og fra veterinær i Mandal da Najax hadde skadet et klofeste. Espen har forresten tilgode noen skikkelige taskekrabbner fra Helgeland. De får du Espen få i 2009.\r\n\r\nAv Erling Lusæter og Hilde Engvik blir jeg mottatt som en kjær gjest. Her på Lusæter gård i Nord-Gudbrandsdalen får jeg beskjed om at svigerfar i Hammerfest er død. Erling og Hilde tar vare på alt mitt ustyr. Ordner med skyss slik at jeg kan komme meg til Oslo. Tilbake igjen henter de meg i Lillehammer.\r\n\r\nPå Glomås treffer jeg Turid Hørsdal og hennes mann. Turid inviterer meg til å besøke Vanilla  Turisthytte. Den er stengt for året, men jeg får låne nøkkel med beskjed om å bare ordne meg på beste måte. Akkurat da jeg skal til å låse meg inn kommer Turid kjørende. Jeg ble der i tre dager med hyggelig selskap, god mat og vin sammen med nyplukkede kantareller.\r\n\r\n<em>21. juli 2008 er jeg klar til å starte opp igjen. Det er blitt problemer med avtalt skyss ned til Sveet, 6 mil øst om Stiklestad. Ingen problemer med offentlig transport til Stiklestad, men det er ingen offentlig rute inn til Sveet. Her på Sveet brøt jeg i fjor og skal starte opp derfra igjen i år. Hvordan ordner jeg vel dette?</em>\r\n\r\nDa får min kone en trivelig beskjed. Som en reddende engel stiller Jan-Erik Vargdal opp. Ingen probleme, han kan kjøre meg ned til Sveet. Her er det bare å takke og bukke.\r\n\r\nPå vei fra Nordli  til Rørvik går turen på asfalt. Opp bakkene i varmen fra Nordli blir Najax enten dårlig eller han mister all motivasjon. Uansett jeg kommer meg bare meget sakte i veg. Litt oppe i bakkene stopper en bil. To hyggelige damer, Ann Katrin Bergli og Ingrid Bergli tar affære, ringer til  Kvelibua, ordner med teltplass og får noen til å passe Najax mens jeg kommer etter på veien.\r\n\r\nDet er 54 km og asfalt igjen til Røyrvik og neppe noe for Najax. Her ordner butikkeieren opp, ringer rundt og ordner bilskyss for hunden.\r\n\r\nRoger Granli driver Østre Limingen gård. Han skal en tur til Nordli og Kvelibua. På retur kan Roger ta med Najax og kjøre han til Røyrvik.\r\n\r\nI mellomtiden har jeg snakket med Limingen Gjestegård i Røyrvik. Vertskapet der, Fritz Skjærvik og Borgny Schjølberg sier straks ja til å ta i mot Najax. De steller godt med han de to dagene det tar meg å komme til gjestegården.\r\n\r\nTor B. Håkonsen og Olaf Erik Strøbmerg holder meg med selskap gjennom Børgefjellet. Nede på Golvertun halter plutselig Najax. Tor B. kjører til dyrlege på Trofors og tar deretter Najax med hjem til Mo i Rana. Jeg fortsetter allene i fjellene hjemover.','Takk til personer','','publish','closed','open','','takk-til-personer','','','2010-07-19 10:25:53','2010-07-19 08:25:53','',0,'http://falleri.no/takk-til-personer/',98,'page','',0),(225,2,'2007-05-19 00:37:41','2007-05-18 22:37:41','Dette er gjesteboka for “Den glade vandrer\". Her kan du legge igjen en liten hilsning eller hva du ellers måtte ha på hjertet. Underveis på turen vil vi med jamne mellomrom lesa gjesteboka og vi setter stor pris på alle som skriv seg inn.\n\nHilsen fra Gunnar og Najax','Gjestebok','','inherit','open','open','','17-revision','','','2007-05-19 00:37:41','2007-05-18 22:37:41','',17,'http://falleri.no/2007/05/19/17-revision/',0,'revision','',0),(177,2,'2008-08-01 09:20:34','2008-08-01 07:20:34','<a href=\"http://www.flickr.com/photos/gisle/2734233935/\" title=\"Minste Rødøyværing på Virvasshytta\"><img src=\"http://farm4.static.flickr.com/3205/2734233935_da5370045e_m.jpg\" width=\"240\" height=\"180\" align=right alt=\"Minste Rødøyværing på Virvasshytta\" /></a>Endelig, etter 12 dager hjemme på Mo er jeg i gang igjen. I går ettermiddag ble jeg kjørt opp til \"dammen\" og gikk de 6 kilometerne til Virvasshytta. God plass var det på tross av at 21 Rødøyværinger var ved vatnet. Bare 4-5 av ungene og en bestemor overnattet på hytta. Resten av gjengen teltet og alt matstellet gjorde de ute ved bålet. For meg ble det en trivelig kveld sammen med hyggelige mennesker.\n\nFredags morgen den 1. august legger jeg endelig i vei på min videre ferd mot Nordkapp. Nå er det bare meg og Najax i fjellet. Dagen er solrik og stille på formiddagen, men utover dagen kommer det inn en kjølig bris fra nord. Timene og kilometerne forløper problemfritt og alt er bare godt. Men så skjer det. Etter at vi har passert Auronasa aner jeg det verste. Med vinden i mot får Najax ferten av noe og legger i veg. Det må være rein der foran. Selv om Najax ikke jager er han nysgjerrig. Ganske riktig, i det jeg runder fjellet ser jeg en stor mengde rein og hunden ute på en snøfond. Tro det eller ei, men der ser jeg en stor Golden Retriever akende på buken nedover fonna og rein som nysgjerrig står og ser på det rare dyret. Najax går til og med opp igjen for ny aketur og reinen kommer nysgjerrig nærmere. Her burde jeg hatt et godt videokamera. Videre får nok Najax gå i kobbel eller bak meg under full kontroll. Ansvaret er tross alt mitt fullt og helt.\n\nDet er tørt i fjellet og lite vann i Gubeltåga. Vi vader derfor elva et godt stykke ovenfor inntaksdammen. Najax får litt problemer og må ha noe hjelp. Det er vel ikke bara bare å måtte svømme i rimelig strek strøm med kløv på. Tidlig kveld ved Sølvbekken akkurat som planlagt.\n\n<a href=\"http://www.flickr.com/photos/gisle/2735067962/\" title=\"Sølvbekken er nesten tørr\"><img src=\"http://farm4.static.flickr.com/3197/2735067962_4d21c47636.jpg\" width=\"440\" height=\"330\" alt=\"Sølvbekken er nesten tørr\" /></a>','Virvatnet – Sølvbekken','','publish','open','open','','virvatnet-%e2%80%93-s%c3%b8lvbekken','','','2008-08-01 09:20:34','2008-08-01 07:20:34','',0,'http://falleri.no/2008/08/01/virvatnet-%e2%80%93-s%c3%b8lvbekken/',0,'post','',0),(178,2,'2008-08-02 09:23:46','2008-08-02 07:23:46','I dag er det en riktig lang etappe som står for tur. Først 17-18 km i fjellet fram til Stødi og deretter 24 km på E6en til Lønsstua. Innover fjellet fra Sølvbekken går det greit. Sola skinner og tørt og fint er det innover. Slakt oppover, men noe steinete noe høyere oppe. Vi passerer øst om gamle Nasa Sølvgruver, men tar først en lengre rast godt nedenfor reingjeterhytta. Vannet i bekken er ganske så kaldt og er nesten det beste av alt. Med silende svette er det godt å få kjølt seg med vann i hår og ansikt, drukket en masse og få fylt opp vannbeholderen. Herfra er det også mye rein i fjellet og Najax må stort sett gå koblet.\n\n<a href=\"http://www.flickr.com/photos/gisle/2734232787/\" title=\"Jeg blir hilset velkommen på veien fra Stødi\"><img src=\"http://farm4.static.flickr.com/3051/2734232787_aace2c07ce_m.jpg\" width=\"240\" height=\"180\" align=right /></a>Slette fjellet for det meste, litt opp og ned er det selvsagt inntil vi går nedover til Stødi og E6. Etter avtale kommer min sønn Eskil dit for å hente Najax og alt overflødig utstyr med til Mo i Rana. Fra Stødi blir det å gå E6 fram til Megården, 26 km nord om Fauske. Det er derfor lite fornuft i å plage en hund med totalt 130 km E6 og asfalt. Selv kommer jeg til å overnatte på hotell underveis. Fjellstøvlene tar Eskil med ned til Mo i Rana og mine eminente MBT-sko fra Masai Norge AS er på beina og vei- og asfaltvandringen tar til. Første delen fra Stødi på den gamle veitraseen, fri for biler, stilt og behagelig. Etter noen kilometer må jeg dessverre ut på bilveien med det som følger av støy og bulder. Men noe godt følger det med den trafikkerte delen av E6. En mils vei før Lønsstua blir det vinket heftig fra en bil. Jeg vinker tilbake om enn litt i siste liten. Etter en stund kommer en YA-bil (bilnummer fra Mo i Rana) sigende opp bak meg og stopper. Greta Skatland, en kjenning fra Rana Turistforening er sjåføren. Hun måtte bare snu for å hilse på. Litt etter har nok en YA-bil snudd. Det er min venn fra Børgefjellet, Tor B. Håkonsen som mente å ha sett meg og Greta stå i veikanten og prate. Det hele ender med at Tor B. kjører ryggsekken min til Lønsstua.\n\nTil Lønsstua kom jeg etter 13,5 timer på beina. Storhytta er mørk, men på sikringshytta skinner lyset. Der møter jeg nok en Moværing, Marit Lind og hennes hund. Jeg varmer min middag, som Eskil hadde med fra Mo i Rana. Marit og jeg prater fjell og natur, hund og Falun Gong og i det hele om hvor godt vi har det i vårt nordiske paradis.','Sølvbekken – Lønsstua','','publish','open','open','','s%c3%b8lvbekken-%e2%80%93-l%c3%b8nsstua','','','2008-08-02 09:23:46','2008-08-02 07:23:46','',0,'http://falleri.no/2008/08/02/s%c3%b8lvbekken-%e2%80%93-l%c3%b8nsstua/',0,'post','',0),(179,2,'2008-08-03 09:28:57','2008-08-03 07:28:57','Det ble seint i går og jeg våknet først nærmere halvni. En rask frokost, opprydding og ut på veien klokken 0945. Marit tilbyr seg å kjøre ryggsekken ned til Nordnes og kafeen/campingen der. Jeg takker med glede ja. Omentrent halve veien uten sekk letter betraktelig. Jeg har tross alt 48 km på vei foran meg. Det vil samtidig passe ypperlig å spise middag på Nordnes. En 7-8 kilometerne før Nordnes kan jeg forlate den nye E6 og gå resten av veien til Rognan på gammelveien. Der er det så godt som bilfritt, bare enkelte lokale biler med folk fra distriktet og stilt og fredelig borte fra den tette sommertrafikken.\n\nDe 48 kilometerne går som en drøm, ingen problemer med beina tross av mellom 17 og 18 kg på ryggen merker jeg knapt ryggsekken. Bortsett fra en lang middagspause på Nordnes, tar jeg bare noen få korte pauser underveis. Flatt på ryggen, skoa av og beina hvilende høyt på sekken. Da er jeg som ny etter bare en 10 minutters hvil.\n\nEtter 13 timer er jeg framme og sjekker inn på hotellet. En rask dusj, men dessverre er kjøkkenet stengt og det blir bare gatekjøkkenmat å få. Deretter en halv pils på hotellets brygge i godværet og rett til sengs.','Lønsstua – Rognan Hotell','','publish','open','open','','l%c3%b8nsstua-%e2%80%93-rognan-hotell','','','2008-08-03 09:28:57','2008-08-03 07:28:57','',0,'http://falleri.no/2008/08/03/l%c3%b8nsstua-%e2%80%93-rognan-hotell/',0,'post','',0),(180,2,'2008-08-04 09:31:41','2008-08-04 07:31:41','<a href=\"http://www.flickr.com/photos/gisle/2734234905/\" title=\"Saltenfjorden på vei mot Fauske\"><img src=\"http://farm4.static.flickr.com/3172/2734234905_20738b97ec_m.jpg\" width=\"240\" height=\"180\" alt=\"\" align=right /></a>I dag er det rene hvileetappen med kun 32 km til Fauske. Det er 3 veitunneler på E6, men på spørsmål får jeg opplyst at jeg kan gå rundt samtlige tre. Det er godt å vite at jeg både slipper faren med å gå gjennom tunnelene og alt bulderet og eksosen. 7,5 timer bruker jeg på veien inklusiv pauser. Framme på <a href=\"http://www.fauskehotell.no/\">Fauske Hotell</a> blir jeg hjertelig mottatt og i tillegg møtt av en journalist fra Saltenposten, den lokale avisa på Fauske. Det blir et langt og godt intervju og det skal spennende å se hva som kommer ut av det.\n\nHittil har jeg ikke nevnt noe om min nye sekk, Mjölner fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a>. Men her er mine første inntrykk og min første vurdering. Fra og med 1. til 4. august har jeg gått 35 km i fjellet og 130 km på vei, et gjennomsnitt på vel 37,5 km pr. dag. Kanskje ikke med den helt store vekta, men dog fra 16 til 19 kg på det meste. Så langt er bare det å si, ryggsekken Mjölner er superb og svarer til forventningen. Etter til dels lange dagsmarsjer kjenner jeg ingen ting i skuldrene, hofter eller rygg. Bena kjenner jeg det selvsagt i og det skulle bare mangle. Fra 6. august øker jeg belastningen til ca. 24 kg, men dagsetappene blir betraktelig kortere og fra nå av er det fjellet som gjelder. Konklusjon så langt: Klättermusens nye sekk er bare unik og fortjener sin Outdoor Industry Award 2008. Ryggsekken holder alt hva produsenten lover. Jeg vil gi en fyldig vurdering etter endt tur utpå høsten eller forvinteren.','Rognan – Fauske Hotell','','publish','open','open','','rognan-%e2%80%93-fauske-hotell','','','2008-08-04 09:31:41','2008-08-04 07:31:41','',0,'http://falleri.no/2008/08/04/rognan-%e2%80%93-fauske-hotell/',0,'post','',0),(182,2,'2008-08-05 16:08:39','2008-08-05 14:08:39','Siste dag på E6; 26 km fram til Megården. Der kommer min sønn Eskil for å kjøre meg gjennom 15 tuneller og 50 km vei fram til Kobbvatnet. Han har samtidig med Najax og proviant for de kommende 13-14 dagene fram til Innset ved Altevatnet. Turen fra Fauske gikk for det meste på Holtanveien, bygdeveien ovenfor E6. Denne veien gikk jeg helt fram til Straumen. Toppen 6 biler passete meg da medregnet både mot- og medgående trafikk. På en gård brant et stort bål og jeg hørte barnegråt. Jeg måtte bare inn og høre hva som sto på. Heldigvis ikke noe alvorlig. Det ble til en hyggelig prat av episoden både med mammaen og pappaen til den lille piken som gråt. Jenta tødde også opp og før jeg forlot gården kom hun med en stor sjokolade til meg. Den skulle jeg ha og kose meg med underveis.\n\nJeg går uten veikart og i et umerket kryss må jeg inn på en ny gård for å spørre om riktig vei. Der har det nettopp vært stort bryllup. En sønn er giftet bort og 20 av gjestene hadde nettopp spist frokost og forlatt. Jeg ble budi inn på kaffe og \"rester\". En time eller vel så det går med. Etter Straumen er det inn på E6 og fullt av biler, men bare etter få kilometre kunne jeg ta av på den gamle riksveien og gikk denne nesten helt fram til Megården. En times tid senere kom Eskil og kjørte meg fram til Kobbvatnet og Basis.','Fauske - Soldatmoen (Basis)','','publish','open','open','','fauske-soldatmoen-basis','','','2008-08-05 16:08:39','2008-08-05 14:08:39','',0,'http://falleri.no/2008/08/05/fauske-soldatmoen-basis/',0,'post','',0),(183,2,'2008-08-06 16:15:02','2008-08-06 14:15:02','Endelig ute i naturen igjen. Først anleggsveien fram til sørenden av Stillelva og deretter lia opp mot Livsejavrre. Det går for det meste slakt opp på brukbar sti, delvis merket. Sør om Livsejavrre går jeg i gammelt brelandskap. Nede ved vatnet med en reguleringshøyde på 60m ser det lite innbydende ut. Teltet blir derfor slått opp i høyden en liten kilometer fra vatnet. Da Livsejavrre heller ikke innbyr til fiske blir det egen proviant for kvelden.','Basis - Livsejavrre','','publish','open','open','','basis-livsejavrre','','','2008-08-06 16:15:02','2008-08-06 14:15:02','',0,'http://falleri.no/2008/08/06/basis-livsejavrre/',0,'post','',0),(181,1,'2008-08-07 10:32:23','2008-08-07 08:32:23','<a href=\"http://farm4.static.flickr.com/3270/2740269363_af2dc1be64_o.jpg\" title=\"Avisoppslag Saltenposten 7/8-2008\"><img src=\"http://farm4.static.flickr.com/3270/2740269363_144f3d2a90_m.jpg\" width=\"164\" height=\"240\" align=right alt=\"Avisoppslag Saltenposten 7/8-2008\" /></a>I dagens utgave av Saltenposten er det er flott <a href=\"http://www.saltenposten.no/nyheter/article173192.ece\">oppslag</a> om Gunnars tur.\n\n<blockquote><em>FAUSKE: Mandag ringer Saltenposten den spreke 71-åringen fra Mo i Rana. Ryktet sier at han nærmer seg Fauske på sin tur fra Lindesnes til Nordkapp. ...</em></blockquote>','71-åring går Norge på langs','','publish','open','open','','71-aring-gar-norge-pa-langs','','','2008-08-07 10:32:23','2008-08-07 08:32:23','',0,'http://falleri.no/2008/08/07/71-aring-gar-norge-pa-langs/',0,'post','',0),(184,2,'2008-08-07 16:17:11','2008-08-07 14:17:11','Vann- og elvesystemet ved Gieversluoppal ser på kartet meget lovende ut. Jeg har tidligere planlagt en ekstra dag i dette området med fiske og en ekstra hviledag. Turen dit er forholdsvis kort og jeg regner derfor med å være tidlig framme. I fjellsida nord for Gihttse sitter jeg bare og beundrer landskapet og vannsystemet. Etter rasten går det greit nedover et forholdsvis bratt terreng. Vel ned kommer jeg til et elveleie med bratte steile fjellskråninger på begge sider. Najax får problemer og jeg bruker ganske lang tid innen jeg finner en plass som både kan vades og hvor både jeg og hunden kan komme ned og opp fra elva. Vel framme går jeg på flere gamle kaffebål og finner etter en stund en riktig fin teltplass. Dessverre, også her vann uten fisk. Ikke et vak og heller ikke en fisk å se i elvesystemet.\n\nNajax får i alle fall hvilt seg etter slitet med tung kløv. Samtidig blir det til at vi tar en tur i et sterkt kuppert terreng for å ta ut den beste traseen for morgendagen. Jeg finner også den stien som er avmerket på kartet. Stien er merket med steinvarder med jevne mellomrom. Flere gamle kaffebål går jeg også på og konkluderer med at det nok er jegere og ikke fiskere som bruker terrenget her. Området er som skapt for fjell- og lirype og også for elg.','Livsejavrre - Giversluoppal','','publish','open','open','','livsejavrre-giversluoppal','','','2008-08-07 16:17:11','2008-08-07 14:17:11','',0,'http://falleri.no/2008/08/07/livsejavrre-giversluoppal/',0,'post','',0),(185,2,'2008-08-08 16:20:08','2008-08-08 14:20:08','I dag hadde jeg egentlig planlagt en helt kort etappe fram til Gurtejávre. Dessverre var også dette et fisketomt vatn. Da jeg er på ca 200 moh og rett øst om Hellemobotn legger jeg derfor veien videre opp kanten av canyonen og innover mot Sverige. Hellemobotn og landskapet oppover i fjeller er bare helt utrolig spektakulært. Jeg er også heldig med været; sol og helt klar luft, ikke en regndråpe. Heldig både for å se og ikke minst for å ta seg trygt fram i steile bergsider på bart fjell. Fotfestet blir sikkert og godt.\n\nVel oppe treffer jeg først på tre lokale mennesker. De anbefaler meg å ta meg fram til Njallajávrre på svensk side. Både god teltplass i en liten bjørkeskog og den beste muligheten for å få fisk i elva er det der. Videre innover i fjeller treffer jeg så på en engelsk dame og en mann. De hadde leid seg båt for å ta seg inn fjorden og var på dagstur oppe i fjellet. Begge skrøt uhemmet av sine opplevelser her oppe. Begge fortalte at de hadde vært vidt omkring i verden, men turen opp her overgikk alt de hadde sett og opplevd tidligere.\n\nVel framme finner jeg som fortalt en fin teltplass. Elva derimot var en skuffelse. Det er svært tørt i fjellet og lite vann. Uten makk og dupp og bare med spinner og sluk er det bare å gi opp. Heller ikke fisk til kvelds i dag. Trøsten er at jeg har kommet meg mye lengere enn planlagt.','Gieversluoppal - Njallajävrre','','publish','open','open','','gieversluoppal-njallajavrre','','','2008-08-08 16:20:08','2008-08-08 14:20:08','',0,'http://falleri.no/2008/08/08/gieversluoppal-njallajavrre/',0,'post','',0),(186,2,'2008-08-09 16:25:43','2008-08-09 14:25:43','Jeg er nå inne i Sverige og den delen av nabolandet jeg må låne på min tur til Nordkapp. I et meget flatt, åpent og lettgått terreng går det fort framover. Det er ikke så mye mer å si om denne delen enn at jeg følger Nordkalottleden på en god sti. Over hver minste myr er stien kloppet. Likeså er det stort sett bro elle klopp over alle elver og nesten hver minste bekk. Noen få reiner treffer vi  på, ellers nesten ikke et liv hverken av fugel eller dyr er å se. Najax er som vanlig nyskjerrig på reinen, men holder seg på plass og bak meg når han får beskjed. Ved Rautujauvre er det satt opp en kåte med ovn og to brisker. Etter noe slik som 20 km passer det bra å ta kvelden her.','Njallajávrre - Rautujauvre','','publish','open','open','','njallajavrre-rautujauvre','','','2008-08-09 16:25:43','2008-08-09 14:25:43','',0,'http://falleri.no/2008/08/09/njallajavrre-rautujauvre/',0,'post','',0),(187,2,'2008-08-10 16:27:58','2008-08-10 14:27:58','Nordkalottleden byr ikke på mange overraskelser på denne delen. Den er mest å regne som en transportetappe. Jeg bruker rundt 4 timer fram til Vaisaluokstugan, en av STF sine hytter. Derfra vil jeg ta båt på Akkajauvre til Ritsem. Båten går først kl. 2015 og jeg har masser av tid til rådighet.\n\nAkkurat da jeg kom var det bytte av hyttevert. Begge to trivelige personer som tok hjertelig i mot meg. Den som skulle dra var en ung dame. Hun hadde hatt sitt første år som hyttevert og brukte 4 uker av sin sommerferie til dette. En anderledes ferie som hun ville gjøre om igjen flere ganger. Avløseren var en mann fra Stockholm, selvstendig næringsdrivende innen media. Han hadde mange år bak seg som hyttevert på forskjellige hytter her i nord, men han tok kun på seg jobben ved de små hyttene. Helst langt til fjells og med forholdsvis lite besøk. For han var dette den ultimate ferie. Borte fra storbystresset samtidig som han traff en passende mengde trivelige fjellmennesker.\n\nI morgen tar jeg søndagen på Ritsem og opdaterer min blogg ettersom jeg har fått lånt vertens private \"laptop\".','Rautujaure - Ritsem','','publish','open','open','','rautujaure-ritsem','','','2008-08-10 16:27:58','2008-08-10 14:27:58','',0,'http://falleri.no/2008/08/10/rautujaure-ritsem/',0,'post','',0),(188,2,'2008-08-12 08:34:14','2008-08-12 06:34:14','Etter en ekstra natt på Ritsem, så la en vel uthvilt hund og mann i vei kl. 1230 mot Sitasjaure. Også der kommer jeg mest sannsynlig til å ta inn på en av hyttene til STF. Dagen i dag vil bli på 22 km og går på vei, men på en lite trafikkert vei. De første 2 kilometerne er asfaltert, deretter en gruset anleggsvei bygd av Vattenfall.\n\nOm turen fram til Sitasjaure er det egentlig ikke så mye å si. Vei er nå engang vei, men hovedsaken er at Najax slipper asfalt. På asfalt mister han fullstendig motivasjonen og vil mest legge seg ned. På grusvei derimot går han som en drøm. Vi brukte bare 6 timer fram til hytta ved Sitasjaure. Der ble vi helt alene. Ikke en gang den obligatoriske hytteverten var tilstede. Det var kun et oppslag om at hyttevert manglet. Litt ut på kvelden reagerer Najax og jeg ser to mann på vei ut mot et nes for telting. Litt senere kommer en av karene opp til meg for å spørre om lov til å fiske da fiskekortet deres først gjelder fra neste dag. Jeg har så men ingen ting å innvende til det og ber dem bare å sette i gang. Karene viser seg å være fra Nord-Troms og har kjørt 60 mil for å fiske røyr etter anbefaling fra noen venner. De dro neste dag skuffet derfra. Sitasjaure er stort, regulert og bare egnet for garnfiske. Kun smårøyr på 10-12 cm hadde de sett og fått på kroken. Selv monterte jeg ikke en gang fiskestanga, men konsentrerte meg om en meget interessant bok om de \"Fjellnäre skogene\".','Ritsem - Sitasjaure','','publish','open','open','','ritsem-sitasjaure','','','2008-08-12 08:34:14','2008-08-12 06:34:14','',0,'http://falleri.no/2008/08/12/ritsem-sitasjaure/',0,'post','',0),(189,2,'2008-08-13 08:42:40','2008-08-13 06:42:40','I dag står det ca. 22 km for tur fram til Huejaurestugan. Jeg har det slettes ikke travelt og tar det derfor med ro og forlater hytta først kl. 1050. I det området jeg nå er i er det svært flatt for å si det mildt. Andre folk er det heller ikke å se. Her er det bare meg og Najax i fjellet. Det er også lite annet liv. Ingen dyr, ikke en gang en rein og knapt nok en fugel å se. Selv myggen holder seg borte. Det er mest sannsynlig for tørt for den. Etter en god kilometer med litt stigning opp fra Sitasjaure går jeg igjen ut i det mer flate, langstrakte svenske landskapet. Det føles noe uvant ut for en nordmann, men er ganske raskt å ta seg gjennom og egentlig ganske fasinerende. Like etter broen over Ainnajokka treffer jeg på de to eneste andre menneskene på denne deltraseen. To svensker, begge fra Stockholm. Det er etter deres mening knapt noen \"norrlendinger\" som tar seg inn i fjellene på denne måten.\n\nDet blir en begivenhetsløs tur videre fram til Hukejaure. Der ble vi, forutenom hytteverten alt i alt tre tilsammen som overnattet. Blant de to andre en en ung tysk vakker pike, Bettina. Kvelden ble lys, med sol og varme. Jeg gjorde klar min fiskestang for å prøve røyra i Hukejaure. Spør så Bettina om hun har lyst til å bli med. Med et godt smil takker hun ja. Det ble ingen fisk, men en deilig kveld. Bettina hadde så vidt prøvd slukstang tidligere og jeg fikk derfor gleden av å lære henne litt mer. Blant annet om hvordan hun skulle la spensten i stanga gi de lange kastene. Også om hvordan stanga skulle holdes ved innsveivingen. I det hele tatt, en meget trivelig avslutning på dagen og kvelden ved Hukejaure.','Sitasjaure - Hukejaurestugan','','publish','open','open','','sitasjaure-hukejaurestugan','','','2008-08-13 08:42:40','2008-08-13 06:42:40','',0,'http://falleri.no/2008/08/19/sitasjaure-hukejaurestugan/',0,'post','',0),(190,2,'2008-08-14 08:43:22','2008-08-14 06:43:22','Vinden har snudd og kommer fra nordvest. Været er halvskyet med en nokså kald vind, men med vinden i ryggen vil det bare bli godt å gå. Bettina starter i motsatt retning og etter en god klem vinker hun farvel. Selv starter jeg ikke før rundt kl. 1200 og har omtrent 22-24 km å gå fram til Sälka. Turen fram går nå i et litt mere kuppert terreng enn tidligere. Også noe mer ur, men fortsatt lettgått. På det høyeste er jeg nå oppe på en 950 moh. Jeg går også langsetter et flere kilometer langt naturlig dreneringssystem. Også nå er jeg helt alene med Najax langs denne delen av Nordklalottleden. Etter en par timer ser jeg en annen person komme ned fra en annen kant av fjellet. Jeg stopper opp og venter til han kommer ned til meg. Det viser seg å være en landsmann, født på Storforshei like ved Mo i Rana der jeg bor. Han hadde vært oppe på Kebnekaise og var på vei hjem til Sjomen. Jeg må villig innrømme at det er ekstra stas å treffe på en landsmann slik ute i ødemarken.\n\nVidere framover mot Kungsleden, eller rettere sagt Dag Hammarskjöldleden treffer jeg ikke på noen flere. Landskapet er  vidt med fjell og breer. Nå er det bare å gå på mot den første meditasjonsplassen til äre for Dag Hammarskjöld. Her kommer jeg samtidig inn på Dag Hammarskjöldleden. Det er som å komme inn på en hovedvei. Helt utrolig oppgått og det er nærmest å ligne med slepene på Hardangervidda. Fra meditasjonsplassen/krysset er det ca. 4 km igjen til Sälka. I og med det er litt sent på kvelden treffer jeg ikke på mange andre. Derimot er det ganske mange telt å se langs disse fire kilometerne. Framme på Sälka tar jeg hus for kvelden. Det skal svenskene ha til ære, der er aldri problemer med hund på de svenske fjellstuene. Det eneste måtte være at jeg og Najax alltid har vært alene på til dels store rom. Sju timer tok turen fra Hukejaure til Sälka. Noe som burde fortelle om hvor lettgått det svenske fjellandskapet er.','Hukejaure - Sälkastugorna','','publish','open','open','','hukejaure-salkastugorna','','','2008-08-14 08:43:22','2008-08-14 06:43:22','',0,'http://falleri.no/2008/08/14/hukejaure-salkastugorna/',0,'post','',1),(191,2,'2008-08-16 08:47:09','2008-08-16 06:47:09','Denne dagen har jeg store planer for. Jeg tenker rett og slett å gå helt fram til Abisko. Det er en strekning på ca. 42 km. Et lettgått terreng og for det meste nedoverbakke så det burde være mulig. I alle fall våkner jeg tidlig til en fin, men kald morgen. Nysnø har falt på toppene rundt meg der jeg teltet på ca. 850 moh. og temperaturen kan ikke være på mange plussgrader. Dog, solen skinner og i ly av teltet har jeg det helt prima. Halv ni er jeg på beina, pakket og klar og jeg starter på den lange marsjen. Jeg tar den rett og slett som en måte å bli ferdig med de svenske fjellene på. Det går enkelt og greit nedover fram til Alesjaurestugorna. Der blir det en litt lengre rast med kaffe, reinkjøtt og litt til. Deretter er det bare å traske i vei forbi flere store vann og inn i Abisko nationalpark. Fjellstua her ved inngangen til parken er jeg ikke innom da den ligger noe tilside for min rute. Med hund er jeg nødt til å følge Kungsleden og derfor ser jeg ingen grunn til å telte her inne i nasjonalparken. Det blir bare å traske på og etter 12,5 timer når jeg fram til turiststasjonen i Abisko. Den siste mila var nokså steinete og egentlig litt for hard for Najax etter en så vidt lang marsj. Det er tydelig at jeg nok må ta en ekstra hvildag for hans del her i Abisko. Min nye Mjölner ryggsekk har fungert perfekt. Ingen såre eller verkende skuldre. Vel, vel på denne delen av turen hadde jeg nok ikke mer enn en 18-19 kg på ryggen. Det ble telting første kvelden da de ikke hadde noen ledige rom der jeg kunne ta med hund inn.','Aliseatnu - Abisko turiststasjon','','publish','open','open','','aliseatnu-abisko-turiststasjon','','','2008-08-16 08:47:09','2008-08-16 06:47:09','',0,'http://falleri.no/2008/08/16/aliseatnu-abisko-turiststasjon/',0,'post','',1),(192,2,'2008-08-19 10:58:26','2008-08-19 08:58:26','Etter tre netter og to hele dagers hvile er både jeg og Najax klar for ny innsats. Det jeg derimot ikke vil utsette Najax for er en 15-16 km tur på vei og asfalt. Derfor blir det buss fra Abisko til stien ved Njuoraeatnu. En annen sak er at Najax plent nekter å gå på vei og asfaltdekke med kløv. Han må både trues og lokkes. Inspirasjonen er lik null. Vi entrer nå den rene Nordkalottruta med kurs i første omgang til Lappjordhytta. Den ligger rett over grensen og er drevet av Troms Turlag. På denne siste delen av Sverige føles det nærmest som vi er hjemme igjen. Stien går i et ganske kuppert terreng med til dels bratte stigninger. Vegetasjonen er også mye likt den vi har på hjemmlige trakter.  Forskjellen ligger mest i at det er mye mer stein her i marka en hos oss.\n\nPå turen inn til Lappjordhytta treffer på alt i alt ni mennesker. Et tyskt par og et fra Estland. 4 svensker ved Pålnostugan og til sist en enslig tysk vander ved Lappjordhytta. De to parene hadde begge startet fra Kilpisjávri. Den enslige tyskeren fra Helligskogen. 11 dagers tur for alle fem.\n\nPå Lappjordhytta ble det ingen overnatting for meg. Troms Turlag ønsker tydeligvis ikke folk med hund velkommen på sine hytter. Selv med hovedhytte og en mindre sikringshytte var hunder uønsket på begge to. Det er et paradoks at i Sverige var det ikke problem med hund noen steder. På alle hytter og turiststasjoner var vi velkommen. Det ble å vinke farvell til Lappjordhytta og gå noen kilometre videre inn i fjellet for å slå opp teltet. Teltet kom opp ved en bekk like ved Ganesbákti. En kraftig kald østlig vind kom etterhvert med regn. Jeg var iskald og våt  før teltet var oppe og tørre klær kunne komme på. Krøp derfor ned i soveposen for å bli varm igjen. Mat både for meg og Najax fikk komme senere.','Abisko - Telt v/Ganesbákti','','publish','open','open','','abisko-telt-vganesbakti','','','2008-08-19 10:58:26','2008-08-19 08:58:26','',0,'http://falleri.no/2008/08/19/abisko-telt-vganesbakti/',0,'post','',2),(193,2,'2008-08-20 11:01:50','2008-08-20 09:01:50','Som fortalt i går krøp jeg ned i soveposen for å få varme i kroppen. Klokken var da 1900. I dag kl. 0500 våknet jeg. Det gikk med andre ord 12-13 timer før det ble mat både på meg og Najax. Vel, selv hadde jeg spist både noe sjokolade, nøtter og litt reinkjøtt før jeg sovnet av.\n\nDen tidlige morgenen var rå og kald med tåke. Jeg teltet da også i et nakent fjellandskap på 950 moh. Det ble derfor til en kald frokost med knekkebrød, ost, fet salami og reinkjøtt. Rent kaldt fjellvann til drikke. Kun pellets på Najax, men nok med vann i bekken 2 meter fra teltet. Teltet vått, soveposen fuktig og tøyet fra i går like vått som da jeg skiftet.  Heldigvis, nede ved Altevatnet på <a href=\"http://huskyfarm.de/\">Huskyfarm Innset</a> vil alt bli tørket opp.\n\nTuren nedover går greit. Som vanlig i fjellet er det både motbakker og kneiker, men tross alt fra 950 moh til 480 blir det for det meste nedover. Etter vannskillet oppe på Láirevággi har jeg foran meg en bred u-dal, slakt fallende framover til Altevatnet. Underveis ser jeg kun noen få rein og litt fugler. Det er nok høsten som så smått har begynt å gjøre sitt inntog i høyfjellet. På dette strekket anslått til ca 18 km treffer jeg bare på to andre mennesker. Selvsagt også det tyskere på vei fra Kilpisjávri til Abisko. Hva i all verden er feil på oss nordmenn? Tyskerne skryter i alle fall vår fjellverden opp i skyene. Dette siste paret måtte forresten ta seg tilbake til Kilpisjávri. Der hadde de nemmelig bilen stående.\n\nPå Huskeyfarmen må Najax ha fått sitt livs opplevelse og sjokk. 70 hunder i bur. Najax i fullt sprang fram mot disse, men bråstopper så han sklir på sida framover gresset. Opp og tilbake igjen. Det ble uten tvil for sterkt for han det hele. Dagen går  videre med til oppdatering av bloggen.  Deretter håper jeg at husets mobile bredbånd er operativt i morgen. I så fall venter 27 nye bilder på overføring.','Ganesbákti - Huskyfarm Innset','','publish','open','open','','ganesbakti-huskyfarm-innset','','','2008-08-20 11:01:50','2008-08-20 09:01:50','',0,'http://falleri.no/2008/08/20/ganesbakti-huskyfarm-innset/',0,'post','',0),(194,1,'2008-08-22 22:28:23','2008-08-22 20:28:23','','Klättermusen logo','','inherit','closed','open','','klattermusen-logo','','','2008-08-22 22:28:23','2008-08-22 20:28:23','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2008/08/klattermusen.png',0,'attachment','image/png',0),(195,1,'2008-08-22 22:42:08','2008-08-22 20:42:08','','Klättermusen logo skrå','','inherit','closed','open','','klattermusen-logo-skra','','','2008-08-22 22:42:08','2008-08-22 20:42:08','',19,'http://www.aas-sw.no/npl3/wp-content/uploads/2008/08/klattermusen128.png',0,'attachment','image/png',0),(196,2,'2008-08-22 23:03:04','2008-08-22 21:03:04','Etter to gode dager på Huskeyfarmen er planen for denne dagen å ta seg inn til Gaskashytta.  Dagene her på Huskyfarmen har vært ganske spesielle og meget avslappende. Mest spesielt ble det nok for Najax. Det ble utebur for han sammen med 70 andre hunder. Menge seg med huskyene fikk han nok ikke, men buret til Najax var kloss inn på resten av hundene. To ulekor i døgnet der samtlige 70 huskys deltok må da ha virket inn selv på en Golden Retrivier.\n\n15. desember starter sledesesongen opp for Huskyfarmen. Mitt største ønske for livet er nå å få anledning til et par uker her oppe. Aller helst fra siste del av februar til midten av mars.  Ta en titt på hjemmesiden til  <a href=\"http://huskyfarm.de/\">Huskeyfarm Innset</a> og se hva de har å by på. Til min kone og mine barn og barnebarn: Her fikk dere ideen til gaven for min 72 års fødseldag.\n\nFra Altevatnet rett opp av Innset tuslet jeg i behagelig tempo inn til Gaskashytta. Det var mye og passe moden blåbær underveis. Det ble derfor til flere pauser, av med sekken for så å gasse seg med masser av deilige bær. Vitamintilskudd, oksydanter og jern. Bare rene skjære sunnheten og i tillegg skikkelig godt.\n\nAkkurat som tidligere traff jeg på andre fjellvandrere på tur fra Kilpisjávre til Abisko. Denne gangen et sveitiskt par og et ungt finsk/tyskt par. Sveitserene kom fra Gaskashtta. Tyskerne hadde teltet litt lengere oppe i Gaskkasvággi. På Gaskas er det to hytter, en ny og en gammel. Begge merket med \"Hund uønsket\". Jeg velger å overse dette og tar inn på gammelhytta. Najax kommer selvsagt ikke inn på soveromsdelen.\n\nElva som renner like forbi har navnet Fiskløyselva på norsk. Uansett tar jeg i kveld en tur nedover mot Altevatnet og prøver med stang og spinner. Er lykken god blir det ørret eller røyr sammen med nyplukket sopp til kvelds. Det blåser nå en frisk bris fra nordvest og temperaturen kl 1830 er 10°C.','Huskyfarm Innset - Gaskashytta','','publish','open','open','','huskyfarm-innset-gaskashytta','','','2008-08-22 23:03:04','2008-08-22 21:03:04','',0,'http://falleri.no/2008/08/22/huskeyfarm-innset-gaskashytta/',0,'post','',1),(197,2,'2008-08-23 17:27:50','2008-08-23 15:27:50','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\"> Ikke ble det napp og fisk i går og dermed ble det med stekt sopp, potetpurre og reinkjøtt til gårsdagens middag. Jeg er som vanlig oppe i sekstida, men tar det for det meste med ro. Drikker min morgenkaffe, pakker, spiser og steller etter meg. Denne dagen er jeg iveg allerede kl. 1000. Det er tidlig til meg å være og jeg går oppover Gaskkasvággi i retning nordover mot Vuomahytta. Skiltet sier 17 km, men jeg regner med den reelle gangavstanden til rundt 19 km. Oppover mot Gaskkashjokka stiger det jevnt og trutt oppover. Jeg startet på 557 moh og skal opp til 980 moh før det igjen går nedover mot Vuomajãvri. Etter et par kilometere kjenner jeg at jeg begynner å bli våt i baken. Det er da det går opp for meg hvorfor jeg «svettet» så forbannet fra livet og ned dagen før. Det var ikke svette men min «kamelpose» som var og er lekk. En liten dråpe med jevne mellomrom og buksebaken blir våt. Heldigvis har jeg bl.a. Lynlim i mitt reparasjonssett. Lekkasjen blir lukket, vann fylt på på nytt og jeg kan fortsette uten «svetting» fra nå av.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Opp mot toppen av Gaskkasvãggi treffer jeg først på to østerrikere, deretter et tysk og til slutt far og sønn fra Israel. Jeg tar en pause sammen med de to siste. Vi prater om løst og fast, men mest naturopplevelser. Jeg blir spandert på en varm kopp te og israelerne får smake reinkjøtt fra Arctic Rein og Vilt.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Passerer vannskillet ved Jierdni. Opp hit og nedover mot Nuolimus har det vært og er mye stein og ur over flere kilometere. Det første glimtet av elvesystemet nedover mot Vuormajãvri dukker opp og håpet for matauke stiger. Hele veien i dette området er det nå lett og fint å gå. Framme ved Vuomahyttene ser jeg en naken arm komme ut av et vindu som åpnes. Her må det være folk selv om klokken bare er skikkelig tre på ettermiddagen. Framme ved hytta kommer tre unge vakre piker ut. De går litt til sides for hytta hvor to av dem hjelper den tredje med å vaske et langt lyst hår. På hytta var det alt fem personer og jeg tenker først å ta i buk den andre hytta, men blir anbefalt å ta inn sammen med de fem. 14 nederlandske ungdommer er på vei og vil nok fylle skikkelig opp den tomme. Før kvelden var omme blervi alt i alt 11 stk. I vår hytte, 14 i den andre oget engelsk par i telt. Av disse 27 menneskene er vi kun tre nordmenn.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Vuomajãvri er et røyevann, i morgen er det søndag og derfor passer det ekstra bra og ta helligdagen her ved vatnet. Både i kveld og i morgen vil jeg prøve røyefisket. Ønsk meg lykke til.</p>','Gaskashytta – Vuomahytta','','publish','open','closed','','gaskashytta-%e2%80%93-vuomahytta','','','2008-08-23 17:27:50','2008-08-23 15:27:50','',0,'http://falleri.no/2008/08/23/gaskashytta-%e2%80%93-vuomahytta/',0,'post','',0),(198,2,'2008-08-25 17:32:57','2008-08-25 15:32:57','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\">Etter en lang og trivelig morgen og formiddag legger jeg i veg kl. 1050 mot Dividalshytta. Mine ti hyttevenner fra Tromsø, Hans og Solveig rådet meg til å ta veien over rabbene sør om Vuomajãvri. De fortalte at den merkede stien var en god del gjørmete og oppgått langsetter Anjajokha. Det ble et fornuftig valg. Lettgått og med stadige tråkk etter rein og dermed et stisystem å gå etter. Landskapet her er åpent og med et vidt utsyn. Dette rutevalget i tillegg er ca 1 km kortere enn den vanlig gåtte traseen. Framme der Vuomajohka og Anjajohka møtes kommer jeg så inn på den merkede stien. Her treffer jeg på to tyske ungdommer. De ligger i telt her nede ved Anja og skal ta seg en dagstur inn til Vuomahyttene og tilbake igjen. Ungdommene er vel utstyrt med blant annet kart og kompass og går med lett dagspakning. Jeg råder dem derfor til å ta samme ruten som jeg kom nedover.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Her far elvemøtet går jeg raskt i en blanding av gammel furu- og bjørkeskog. Det er til dels et noe vått lende, men med rimelig bra klopping. Framme mot Divielva tar jeg stien mot det gamle vadestedet i stedet for ned til brua. Valget sparer meg for noe sånt som fire kilometers veg. Med lite nedbør og et tørt år er det lite vann i elva og jeg vader over uten noe ekstra på beina. Mine fjellsko har høye nok skaft til å komme tørrskodd over. De siste tre til fire kilometerne opp til Dividalshytta blir tunge. Først ganske flatt, men deretter opp 240 høydemetere de siste 2 kilometerne. Det merkes med trøtte bein opp kneikene.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Her på gammelhytta sitter jeg nå alene og skriver dagens kladd til bloggen. På nyhytta har jeg til nabo 21 ungdomsskoleelever sammen ned 4 voksne. Disse var i dag kommet opp fra Rossfjord for i morgen å gå videre til Vuomajärvi og hyttene der. Jeg ble nokså fort klar over at en av de voksne var godt kjent videre innover Dividalen.og grensestrøkene. Jeg tok derfor mine kart med og gikk på besøk for å få kjentmanns råd. Mitt opprinnelige rutevalg ble lagt om, men i tillegg ville alle ha meg til å fortelle om min tur så langt. Dette ble til en flott opplevelse for meg. Bare det å ah 21 ungdommer på 15 år sittende musestille og lydhøre og ta inn det den gamle mannen fortalte var en opplevelse i seg selv. Spørsmål om hva jeg spiste, hva slags telt jeg hadde, om gnagsår og om hvordan det var å gå alene bare forsterket den gode følelsen. Og så til slutt en skikkelig spontan applaus. Takk til dere alle jeg fikk opplevelsen av å treffe på Dividalshytta. Najax hilser også.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>','Vuomajävri – Dividalshytta','','publish','closed','closed','','vuomajavri-%e2%80%93-dividalshytta','','','2008-08-25 17:32:57','2008-08-25 15:32:57','',0,'http://falleri.no/2008/08/25/vuomajavri-%e2%80%93-dividalshytta/',0,'post','',1),(199,2,'2008-08-26 17:44:23','2008-08-26 15:44:23','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\">Etter at ungdommene dratt gjør også jeg meg klar til avgang. Nå med Dærtahyttene som mål. Skiltet sier 24 km, jeg har beregnet avstanden til 26 km. Uansett det ene eller det andre, som en «travel mann» tar jeg av allerede kl. 1220. Lia opp fra hytta og til høyden oppunder Litle Jerta er kort og brutal. 420 høydemetere på 3 km er bratt og da er det godt med utkvilte bein. Like etter runding av fjellet treffer jeg på den eneste andre vandrer på dagens tur til Dærta. Det er en ensom svenske på tur fra Kilpisjãvri til Abisko. Selvsagt nok en utlending, men dog en skandinav denne gangen. Bortsett fra at oppstigningen var bratt var underlaget godt å gå på. Lite stein, et fint mose og lyngdekke og så godt som fritt for ris og kjerr. Vel oppe i høyden var det bare å lage ut. Noen rein, ingen ryper, et par ravn og en våk. Bortsett fra det, bare stillhet og fred. Det siste stykket fram  til Skáktarjohka bar det bratt nedover. Vadestedet over elva over elva her var det mest steinete på hele turen. Men med lite vann i elva ble det lett og uproblematisk å ta seg over. Halvveis til Dærta forbereder jeg nå en liten lunchrast med hvil av lett trette muskler. Jeg stoppet mellom vannene oppunder Bumannsberget. Rasten ble kort, med en frisk sørøstlig bris og neppe mer enn 6-7 grader innbød det ikke til bål og kaffekoking. Videre framover, det samme lette, fine terrenget bringer meg til Dærtahytta allerede klokken åtte.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Til oppsummering: Denne delen av landet vårt, Dividalen er noe av det flotteste så langt på hele turen. Vann og elver, små tjørner, store vann, brede vannveier, stilt strømmende med gode fiskemuligheter, kulper og korte stryk. Divi betyr forresten hauk på samisk så riktig oversatt skulle navnet vært Haugdalen eller dalen med de mange hauger. I tillegg til alt vannet stemmer dette på en prikk. Hit til Dividalen kommer jeg til å lengte tilbake. En gang til må jeg komme meg til Dividalen. Da med telt, sovepose, stang og med minst 14 dager til virkelig å få oppleve  livet her inne. Juli/august må bli den rette tiden.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">På gamle Dærta var det godt og varmt. To ungdommer fra Tromsø tok i mot meg og Najax. Fint å ha selskap av landsmenn og få prate på nordmenns vis. Kvelden tok vi først over midnatt. Er det ikke slik det er når nordlendinger møtes.</p>','Dividalen – Dærtahyttene','','publish','closed','closed','','dividalen-%e2%80%93-d%c3%a6rtahyttene','','','2008-08-26 17:44:23','2008-08-26 15:44:23','',0,'http://falleri.no/2008/08/26/dividalen-%e2%80%93-d%c3%a6rtahyttene/',0,'post','',0),(200,2,'2008-08-27 17:48:07','2008-08-27 15:48:07','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\">Her fra Dærta tar jeg en helt ny rute i forhold til min opprinnelige plan over Rosta, Gappo og til Golda. Nå blir det i stedet Statskoghytta ved Rostojävri, Steinbua ved Rostaelva, Pältsastugan på svensk side, gjennom Varratvuoopmi og Gahpuruovddas og inn på Nordkalottruta ved Valljihat, om Treriksrøysa og til Goldahytta. Beregnet distanse 65 km. Dette er noe lengre enn den opprinnelige planen, men skal være mye bedre og lettere å gå.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Jeg er nå på vei etter min nye rute. Fra Dærta til Statskoghytta er det bare 12 km. Slakt terreng, rett fram østover til under neset ved Mallanjärga og deretter går det rett nordover langs Rostojävri fram til hytta. Selv med rolig gange er dette hva jeg vil nevne som et 4-4,5 km i timen terreng. Godt med stier, ikke menneskegåtte, men etter rein. Et nydelig vær i dag, stillhet og ro. Det er slik at jeg blir i det filosofiske hjørnet og går og undres på hvordan de engang må ha hatt det her. Da tenker jeg på de menneskene som levde med og av rein. Av jakt og fiske, samling av bær og urter og vel også i fred sammen med de store rovdyra. Uvegerlig kommer tanken, hva gjør vår moderne teknologi og vårt livsmønster med naturen? Tåler naturen vårt teknologiske liv enten det være seg samer eller alle oss andre. Er det i det hele og store rett å sette et skille mellom reindriftsutøvere og oss andre, reindrifta er jo i dag helt motorisert. Nok om disse tanker. Jeg bruker da noe ekstra tid på å komme fram til dagens mål. Det som tok noe tid var masser av molter. Endelig, av med sekk og neve på neve med akkurat modne molter. Du herlighet, jeg må ha satt til livs flere liter rett fra myra.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">På Statskoghytta var det alt kommet to andre. De hadde kommet reisende fra Bergen  for noen dagers ferie her inne. Dame og mann, han opprinnelig fra Bardu og godt kjent her inne i fjellene. Tida bare fløt avsted med trivelig prat, god turmat og en aldri så liten cognac til kaffen. I dag, dagen etter sitter jeg alene her på hytta og tar snart stanga med for å prøve ørreten nedover elva. Det blir en natt til her  og forhåpentligvis ørret til kvelds.  Et lite sidesprang. Rostojävri ligger slik til på vannskillet at det har utløp i begge ender. I nord renner Rostaelva/Rostoatnu til Norge og i sør Råstätnu til Sverige. Den blir lengre øst til Lainiälv.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Jeg tar ettermiddagen fatt og går fem kilometere inn til nordenden av Nihkolasjävri for å starte fisket der. Vannet viser seg å være et røyevann og fisken er absolutt ikke i bit  denne dagen. Går derfor rett ned til Rostaelva for å prøve ørreten der på veien oppover til Statskoghytta. Spinner og sluk, ikke et tilslag. Jeg vil derfor prøve med noen våtfluer jeg har med. Men før jeg kommer så langt sklir jeg på en stein ved elvebredden. Tar meg for med handa og får et ganske dypt kutt ved venstre tommelfeste og jeg blør som en stukken gris. Plaster ligger igjen på hytta så det ble med det fisket. Nå er det bare å komme seg tilbake og få stelt såret. Derpå blir det kokt ris og reinkjøtt til middag denne dagen.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Det mørkner mot kvelden. Najax begynner å gjø iltert. Da ser jeg to personer og en hund komme ned mot hytta. To Senjaværinger, far og sønn hadde fisket seg oppover elva. På pinner og sluk ikke et tilslag. Derimot på makk beit småørreten og disse to berget kvelden for meg. Jeg ble traktert med stekt ørret, hjemmebakt brød og en liten cognac. Du snakker om å ha flaks med sine hyttekamerater.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>','Dærta – Rostojävri','','publish','closed','closed','','d%c3%a6rta-%e2%80%93-rostojavri','','','2008-08-27 17:48:07','2008-08-27 15:48:07','',0,'http://falleri.no/2008/08/27/d%c3%a6rta-%e2%80%93-rostojavri/',0,'post','',1),(201,2,'2008-08-29 17:56:39','2008-08-29 15:56:39','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\">En stille oppvåkning kl. 0730 etter en god natts søvn. Både mine hyttevenner og jeg selv tar oss god tid samtidig som vi skal gå i hver vår retning. Senjaværingene til Dærta og jeg til Pältsa. Først kl. 1230 er jeg klar til å dra. Det er nå blitt så pass langt ut på dagen at jeg dropper turen fram om Steinbua og tar en mer direkte linje fram til Pältsastugan. Den første delen går etter et tidligere delvis merket tråkk fram til 2 km sør om Nihkolasjävri og går rett nordover mot Rr 288 A. Runder høyden Salmi på østsida og passerer Bulkujärvi. 2 km lengre framme kommer jeg til en merkevarde som egentlig etter min navigering ikke burde vært der. Jeg skal befinne meg ca. 1 km øst om den merkede stien. For sikkerhets skyld tar jeg posisjonen med GPSen og alt stemmer. Jeg er der jeg skal være, rundt 1 km øst om stien. Så en liten kuriositet. Ved denne varden finner jeg en liten reklamebrikke på 28x50 mm med et lite intakt termometer. Denne reklamedingsen fra et amerikansk selskap «Field &amp; Trek» med termometer og nesten uten vekt er akkurat hva jeg har ønsket meg og det ligger nå i min sekk. 1,5 km før Pältsa entrer jeg den merkede stien. Min direktekurs var nok 3-4 km kortere enn den merkede og jeg kommer fram ganske tidlig på ettermiddagen.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Her på Pältsa ble jeg møtt av stugverten, Per Allan Granfors og invitert på middag. Lammegryte med nyplukket sopp og kokte poteter. Bortsett fra soppen, norsk mat fløyet inn med helikopter. Tre rypejegere fra Tromsø hadde forlatt Pältsa tidligere enn beregnet og overlatt til verten det de hadde til overs av mat. Særlig må jeg si at det smakte godt med kokte poter og brun saus.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Ruta jeg gikk i går kan trykt anbefales. Et lett og fint terreng, småkuppert, så godt som fritt for ur og myr og samme kompasskurs nesten helt fram. Kvelden på Pältsa gikk med til å lese litt norsk krigshistorie. Her hadde kaptein Håkon Kyllingmark bygd opp en av fire etterretnings- og sabotasjestasjoner. Bemanningen på Pältsa varierte fra 10-16 personer. Sitat: «Organisasjonen hadde flera patrullvägar och kontaktnätt både på finsk og norsk område. Bemanningen i Pältsastugan varierade mellom tio och sexton personer. Ibland var de ute i upp til fem dygn i det tyskockuperade området  för att undersöka vissa terrängavsnitt innan de återvände för avrapportering med kortvågsradio».</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>','Rostojävri – Pältsastugan','','publish','closed','closed','','rostojavri-%e2%80%93-paltsastugan','','','2008-08-29 17:56:39','2008-08-29 15:56:39','',0,'http://falleri.no/2008/08/29/rostojavri-%e2%80%93-paltsastugan/',0,'post','',0),(202,2,'2008-08-30 17:57:37','2008-08-30 15:57:37','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\">Tidlig oppe til hvite fjell. Spesielt fjellet Pältsan på 1444 m varsler den kommende vinteren. Det blåser ganske kraftig fra nord og her nede ved hytta er det et iskaldt regn. Regnet går senere over til sludd og det hvite kryper lengre nedover fjellsidene. Hytteverten hadde hørt værmelding og det skulle lette noe utover dagen. Jeg tok derfor livet med ro også her på Pältsa og startet først klokken ett mot Golda. Fortsatt sludd, vinden imot, rett og slett et «jævla drittvær». Det ble nok å gå i ett og heller spise litt mens jeg gikk. Vann i «kamelbagen» og biter av tørka kjøtt i lomma passer godt til slikt vær. Stigningen opp fra Pältsa til ca. 1000 moh gikk i et fint og greit lende. God sti, først gjennom en del bjørkeskog, senere noe vier til det ble en ren fjellvidde. Det er lite med stein og ur der stien er lagt. Med andre ord, lettgått. Den først 1,5 timen var våt med til dels tett sludd og bra med vind imot. Deretter lettet det og himmelen ble stadig klarere. Etter hvert ble det noe mindre vind og sola kom fram. Fortsatt var det kaldt og det var bare å beholde vindvotter og balaklavaen på. Her inne i fjellet var det vist bare jeg, Najax og en del rein. Det var så langt ikke noe annet liv å se.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Godt inne på fjellplatået Duoibal tar jeg av mot Treriksrøysa og nede mot den i skogsbandet kan jeg endelig lette på antrekket. Vindvotter og balaklavaen forsvinner ned i sekken, men resten av klærne må nok beholdes på. Fram mot Treriksrøysa ser jeg to personer forlate denne. Dette ble det nærmeste jeg kom andre mennesker denne dagen. Nå er jeg innskrevet i besøksprotokollen og et bra bilde ble tatt ved røysa. Tre kilometere igjen til Goldahytta. Der skal det bli godt å komme i hus, få varme i ovnen og et skikkelig måltid. Beregn 4-5 timer fra Pältsa til Golda.</p>','Pältsa – Goldahytta','','publish','closed','closed','','paltsa-%e2%80%93-goldahytta','','','2008-08-30 17:57:37','2008-08-30 15:57:37','',0,'http://falleri.no/2008/08/30/paltsa-%e2%80%93-goldahytta/',0,'post','',0),(203,2,'2008-08-31 18:04:30','2008-08-31 16:04:30','<meta http-equiv=\"CONTENT-TYPE\" content=\"text/html; charset=utf-8\" /> 	<title></title> 	<meta name=\"GENERATOR\" content=\"OpenOffice.org 2.4  (Unix)\" />\n\n<style type=\"text/css\"> 	<!-- 		@page { size: 21cm 29.7cm; margin: 2cm } 		P { margin-bottom: 0.21cm } 	--> 	</style>\n<p style=\"margin-bottom: 0cm\">Rim på bakken med klarvær og sol. Termometeret vist en minimumstemperatur i løpet av natten på 3,4 minusgrader. Men i dag er det stille og som sagt sola skinner. Her på Golda har forresten Troms Turlag lagt alt til rette også for oss hundeeiere. Det står i boka at det er en forsøksordning. Hvorfor forstår jeg ikke riktig og det er bare å håpe at TT tar til vett og legger til rette på alle sine hytter både for folk med og uten hund. Fra Golda planlegger jeg å og rett østlig til stien oppunder Iso-Malla for så å følge denne fram til sørenden av Siilajävri. Derfra blir det veien fram til hotellet ved Tsahkalluokta. Der blir det overnatting, oppdatering av bloggen og en bedre middag. Slik været er nå og hvor vinteren nærmer seg bryter jeg av for i år her ved Kilpisjävri. Starter opp igjen den siste uka av juli til neste år og tar de siste 25 dagene fram til Nordkapp.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Nå når jeg forlater Goldahytta råder en finsk dame meg til å følge stien ned til Treriksrøysa. Et reingjerde langs grensen kan være vanskelig å komme forbi der jeg hadde planlagt kryssing. Stien er ellers grei å gå med en slak stigningopp til toppen av skaret. Her inne er fjellet en del av nasjonalparken Mallan og jeg treffer på en god del turgåere. Men alle er finner og det blr bare så som så med praten. Mest bare en hilsen til god dag.</p>\n<p style=\"margin-bottom: 0cm\">&nbsp;</p>\n<p style=\"margin-bottom: 0cm\">Framme på hotellet blir det innsjekking og en kald øl. Deretter dusj og tid for middag. Både hotellet og ikke minst rommet mitt virker ganske nedslitt. Maten derimot er topp og over alle forventninger og tyder på en kjøkkensjef eller kokk som kan sitt fag. Jeg har fått selskap av en annen turgåer, Mika Rantala fra Helsingfors. Han er en riktig trivelig kar og vi prater sammen på engelsk både over maten og til en par øl. Mika er politi av yrke og har tydeligvis vært en del over i Norge i forbindelse med jobben. Sine norske kolleger kom han tydeligvis godt overens med. Svenske kolleger har han vist et mer reservert forhold til. Som en avslutning på middagen og praten anbefaler jeg Mika den nordnorske naturen og DNTs hyttenett i nord. I morgen kommer jeg til å ta bussen fra Kilpisjärvi til Tromsø og deretter <a href=\"http://www.hurtigruten.com/no/default.aspx?side_id=376\">Hurtigruta til Nesna</a>. Vi møtes til neste år i siste uka av juli. Da står Finnmarksvidda og deretter Nordkapp for tur.</p>','Goldahytta – Kilpisjärvi','','publish','closed','closed','','goldahytta-%e2%80%93-kilpisjarvi','','','2008-08-31 18:04:30','2008-08-31 16:04:30','',0,'http://falleri.no/2008/08/31/goldahytta-%e2%80%93-kilpisjarvi/',0,'post','',1),(205,2,'2009-07-27 14:21:21','2009-07-27 12:21:21','I dag, 27. juli skulle jeg vært i gang med den siste delen av min tur fra Lindesnes - Nordkapp. Dessverre, for en tid tilbake brakk jeg venstre håndledd og det er bare to uker siden fiksasjonsutstyret ble fjernet. I min naivitet trodde jeg at 14 dager med trening og terapi skulle holde og at jeg kunne starte som planlagt. Jeg er bedre, men hånden er uten kraft og fungerer dårlig. Fra første stund var turen ment å være \"blott til lyst og glæde\" og ikke noe slit. Et dårlig håndledd gjør at verken vandring, telting eller bruk av fiskestang fungerer optimalt. Jeg har derfor besluttet å utsette siste etappen til Nordkapp til sommeren 2010.\n\nDenne utsettelsen kom samtidig på tverke for en tur jeg skulle ta i 2010. Da skulle jeg gå pilgrimsleden fra St. Jean i Frankrike, over Pyreneene, gjennom Baskerland og til Santiago de Compostella helt nordvest i Spania. De nærmeste ukene blir avgjørende for denne turen, enten tar jeg den i november/desember i år eller april/mai til neste år. Det er bare å vente og se hvordan det hele utvikler seg.','Utsatt start siste etappe','','publish','closed','closed','','utsatt-start-siste-etappe','','','2010-07-19 10:40:52','2010-07-19 08:40:52','',0,'http://falleri.no/2009/07/27/utsatt-start-siste-etappe/',0,'post','',0),(209,1,'2008-06-08 13:13:43','2008-06-08 11:13:43','1. <a href=\"proviant\">Proviant</a>\n2. <a href=\"dette-har-jeg-pa\">Dette har jeg på</a>\n3. <a href=\"klær-i-sekken\">Klær i sekken eller i kløven</a>\n4. <a href=\"sekk-og-utstyr\">Sekk og utstyr</a>\n5. <a href=\"for-hygiene-til-medisinsk-og-personslig-bruk\">For hygiene, til medisinisk eller personlig bruk</a>\n6. <a href=\"sikkerhets-og-reparasjonsutstyr\">Sikkerhets- og reparasjonsutstyr</a>\n7. <a href=\"til-najax\">Til Najax</a>','Proviant og utstyr','','inherit','open','open','','132-revision','','','2008-06-08 13:13:43','2008-06-08 11:13:43','',132,'http://www.aas-sw.no/npl3/?p=209',0,'revision','',0),(210,1,'2007-05-15 12:42:20','2007-05-15 10:42:20','Turen er en 70-årsgave fra meg selv til meg selv. Den er å se på som en gledesytring for god helse i et godt samfunn. Jeg vil, sammen med min Golden retriever Najax ta turen over to somrer. Planlagt start er fra Lindesnes fyr 1. juni 2007.\n\nDe beregnede dagsetapper forhøyes alltid til hele dager og hver søndag tar jeg fri.  Første året avsluttes ved Umbukta ved Mo i Rana i månedsskiftet august/september. Etter planen har jeg derfor 10 – 20 dager til rådighet kun til kos. Det være seg ved et flott fiskevann, bare en lat dag ved teltet eller et spesielt trivelig vertsskap som jeg gjerne blir en dag eller to ekstra hos.\n\nEtter hvert som turen går vil erfaringer, hendelser, bilder og kommenterer bli lagt til den enkelte rute i avvikende farge. Alt som skrives er mine subjektive meninger og må brukes deretter. Har du egne synspunkter kan du gjerne blogge og legge noen ord inn i gjesteboken. Selv holder jeg kontakt med hjemmesiden og oppdaterer ved hjelp av Nokia N80 IE med tråløst tastatur SU-8W.\n\n<a href=\"http://falleri.no/ruta/etappe-1/\">Etappe 1:    Lindesnes – Ljosland</a> 4/6 – 8/6\nLengde: 121 km + 5 %    =  127 km/22 = 5+1 dager\n\n<a href=\"etappe-2\">Etappe 2:    Ljosland – Haukeliseter</a> 9/6 – 20/6\nLengde: 174 km + 10 %    =  191 km/22 =    10+2 dager\n\n<a href=\"etappe-3\">Etappe 3:    Haukeliseter – Finse</a> 21/6 – 27/6\nLengde: 118 km + 10 %    =  130 km/22 =    6+1 dager\n\n<a href=\"etappe-4\">Etappe 4:    Finse – Tyinkrysset</a>        28/6 – 3/7\nLengde: 97 km + 10 %        =  107 km/22 =    5+1 dager\n\n<a href=\"etappe-5\">Etappe 5:    Tyinkrysset – Lalm</a> 4/7 – 10/7\nLengde: 114 km + 10 %    =  125 km/22 =    6+1 dager\n\n<a href=\"etappe-6\">Etappe 6:    Lalm – Folldal</a> 11/7 – 14/7\nLengde: 63,5 km + 10 %    =  70 km/22 =    4 dager\n\n<a href=\"etappe-7\">Etappe 7:    Folldal – Røros</a>            16/7 – 21-22/7\nLengde: 118 km + 10 %    =  130 km/22 =    6+1 dager\n\n<a href=\"etappe-8\">Etappe 8:    Røros–Teveltunet</a>        23/7 – 2/8\nLengde: 127 km + 10 %    =  140 km/22 =    9+2 dager\n\n<a href=\"etappe-9\">Etappe 9:     Teveltunet – Sandv./Nordli     </a>3/8 – 15/8\nLengde: 197 km + 15 %    =  227 km/22 = 11+2 dager\n\nI fjor gikk jeg fram til Sveet ved Veressjøen øst om Stiklestad. Kom dit 31. august.\n<strong> Fortsetter min ferd videre fra Sveet til Nordkapp den 19. juni 2008.</strong>\n\n<a href=\"etappe-9b\">Etappe 9b:   Sveet – Sandv./Nordli </a>22/6 – 30/6 - 2008\nLengde: 116 km + 15 %                 =  133 km/17 = 8+1 dager\n\n<a href=\"http://falleri.no/ruta/etappe-10\">Etappe 10:     Sandvika/Nordli – Røyrvik</a> 1/7 – 2/7\nLengde: 62 km + 10 %            =  68 km/34 = 2 dager\n\n<a href=\"etappe-11\">Etappe 11:  Røyrvik – Umbukta</a> 3/7 – 16/7\nLengde: 205 km + 15 %        =  237 km/22 = 11+3 dager\nBlir to til tre dager hjemme på Ytteren. Starter fra Umbukta 19/7\n\n<a href=\"etappe-12\">Etappe 12:  Umbukta – Sulitjelma</a><a href=\"etappe-12\"> </a>19/7 – 29/7\nLengde: 170 km + 13 %        =  192 km/21 = 9+1 dager\nPS!  Det er ikke lov med hund i Padjelanta nasjonalpark, Sverige. Bryter derfor ved Stødi\npå Saltfjellet  (Umbukta - Stødi: 92 km) og går E6 til     Megården. <a href=\"etappe-13b\">Se etappe 13b</a>\n\n<a href=\"etappe-13\">Etappe 13: Sulitjelma – Abisko</a> 31/7 – 14/8\nLengde: 216 km + 10 %        =  238 km/20 = 10+1 dager\nOpprinnelig planlagt trase. Tar annen vei til Abisko. <a href=\"etappe-13b\">Se etappe 13b</a>\n\n<a href=\"etappe-13b\">Etappe 13b:     E6 fra Stødi til Kobbvannet – Abisko</a>    25/7 – 10/8\nLengde: 364 km + 10 %        =  400 km/26 = 15+2\n\n<a href=\"etappe-14\">Etappe 14: Abisko – Kilpisjävre </a>11/8 – 21-23/8\nLengde: 207 km + 15 %        =  238 km/22 = 10+1+2\n\n<a href=\"etappe-15\">Etappe 15: Kilpisjävre – Maze – Skaidi</a>    25/8 – 9/9\nLengde: 268 km + 15 %        =  308 km/20 = 15+2\n\n<a href=\"etappe-16\">Etappe 16: Skaidi – Nordkapp</a> 11/9 – 16/9\nLengde: 140 km + 7,5 %        =  151 km/25 = 6 dager\n\nAvstand:  SVEET - NORDKAPP     =  1627 km\nAvstand:  LINDESNES FYR - NORDKAPP     =  2758 km','Ruta','','inherit','open','open','','7-revision','','','2007-05-15 12:42:20','2007-05-15 10:42:20','',7,'http://www.aas-sw.no/npl3/?p=210',0,'revision','',0),(211,1,'2010-07-04 22:15:48','2010-07-04 20:15:48','http://www.aas-sw.no/npl3/wp-content/uploads/2010/07/cropped-header.jpg','cropped-header.jpg','','inherit','closed','open','','cropped-header-jpg','','','2010-07-04 22:15:48','2010-07-04 20:15:48','',0,'http://www.aas-sw.no/npl3/wp-content/uploads/2010/07/cropped-header.jpg',0,'attachment','image/jpeg',0),(212,1,'2010-07-04 22:21:20','2010-07-04 20:21:20','http://www.aas-sw.no/npl3/wp-content/uploads/2010/07/cropped-2712698849_6f779b6be8_b.jpg','cropped-2712698849_6f779b6be8_b.jpg','','inherit','closed','open','','cropped-2712698849_6f779b6be8_b-jpg','','','2010-07-04 22:21:20','2010-07-04 20:21:20','',0,'http://www.aas-sw.no/npl3/wp-content/uploads/2010/07/cropped-2712698849_6f779b6be8_b.jpg',0,'attachment','image/jpeg',0),(213,2,'2010-07-05 18:00:46','0000-00-00 00:00:00','','Automatisk kladd','','auto-draft','open','open','','','','','2010-07-05 18:00:46','0000-00-00 00:00:00','',0,'http://falleri.no/?p=213',0,'post','',0),(215,2,'2010-07-05 18:14:03','2010-07-05 16:14:03','Jeg vil gjerne takke mine sponsorer som har vært så vennlig å utstyre meg for turen.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/hilleberg-150x150.jpg\" alt=\"Hilleberg\" />\nHvorfor valgte jeg teltet Nallo 2 GT fra <a href=\"http://www.hilleberg.se/EUROPE.HTM\">Hilleberg</a>?  Jeg falt rett å slett for løsningen med forteltet. Når dertil Hilleberg er kjent for gode produkter var valget enkelt. Jeg ser fram til å få brukt teltet på min tur \"Norge på langs\".\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/logo_mbt-150x150.jpg\" alt=\"MTB Logo\" />\n\"Norge på langs\" går til dels på vei og asfalt. Er det noe som røyner på må det være mil etter mil med asfalt. Her var det at MBT-skoen fra <a href=\"http://www.swissmasai.no/\">Masai Norge AS</a> kom som dalende fra himmelen. Aldri har jeg hatt noe bedre på på hardt og slett underlag. Vent på min rapport og vurdering.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/08/klattermusen.png\" alt=\"Klättermusen\" />\nPå min vei mot Nordkapp bruker jeg i år en del utstyr fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a> - Blant annet:  Mjölner, en helt nyutvikklede ryggsekk  - Tivar dunsovepose  - Liv dunjakke  - Mithril bukse. Testen av dette utstyret vil bli en interresant erfaring å ta med seg.\n\n<img src=\"http://falleri.no/wp-content/uploads/2007/05/baltologosekundar.thumbnail.jpg\" alt=\"Balto Logo\" />\nFor Najax, min Golden Retrivier er \"Norge på langs\" en stor utfording. Deler av turen går med opptil 14 kg i kløven og protein- og fettbehoved blir stort. Spesialforet <a href=\"http://www.baltoshop.no/\">Balto Competition</a> sammen med annet høyverdig tørrfor blir hovedføden. I tillegg blir det vel også spedd på med en del fisk.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/wwwarctic-circleno-150x150.jpg\" alt=\"Arctic Circle Logo\" height=\"115\" width=\"115\" />\nVårt lokale turistkontor, <a href=\"http://www.arctic-circle.no/\">Turistkontoret Mo i Rana</a> og Polarsirkelen Reiseliv er unikt interresert i friluftsliv og har skaffet meg alle kartene jeg trenger til en meget gunstig pris.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2007/05/rana-jakt-og-villmark-150x150.png\" alt=\"Rana Jakt og Villmark Logo\" height=\"104\" width=\"121\" />\nDet er mangt som skal til for en vellykket \"Norge på langs\". Listen med utstyr, både av  smått og stort ble lang. Hadde ikke <a href=\"http://www.jaktogvillmark.no/\">Rana Jakt og Villmark AS</a> det jeg trengte, skaffet de det eller fant et godt alternativ. Servicen var topp og i tillegg ga de meg ekstra gode priser.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/stensland.png\" alt=\"Specsavers Stensland\" height=\"72\" width=\"130\" />\nVed et tilfeldig besøk hos <a href=\"http://old.monet.no/viggo/interoptik/index2.php?link=visjon\">Specsavers Stensland</a> ble jeg anbefalt å bruke månedslinser på min videre ferd mot Nordkapp. Jeg syntes ideen var god og etter en en nitidig og grundig tilpassing er jeg nå på vei med linsene på og brillene i sekken. Ingen problemer videre med nedduggede briller.\n\n<img src=\"http://www.aas-sw.no/npl3/wp-content/uploads/2008/07/rss.gif\" alt=\"Rana Spesialsport\" height=\"90\" width=\"133\" />\nPå min vei mot Nordkapp bruker jeg i år en del utstyr fra <a href=\"http://www.klattermusen.se/\">Klättermusen</a> levert av <a href=\"http://www.spesialsport.no/\">Rana Spesialsport</a>.','Takk til sponsorer','','inherit','open','open','','19-revision-2','','','2010-07-05 18:14:03','2010-07-05 16:14:03','',19,'http://falleri.no/2010/07/05/19-revision-2/',0,'revision','',0),(216,2,'2010-07-19 10:24:36','2010-07-19 08:24