DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE latin1_danish_ci NOT NULL,
  `post_title` text COLLATE latin1_danish_ci NOT NULL,
  `post_category` int(4) NOT NULL DEFAULT '0',
  `post_excerpt` text COLLATE latin1_danish_ci NOT NULL,
  `post_status` enum('publish','draft','private','static','object','attachment','inherit','future') COLLATE latin1_danish_ci NOT NULL DEFAULT 'publish',
  `comment_status` enum('open','closed','registered_only') COLLATE latin1_danish_ci NOT NULL DEFAULT 'open',
  `ping_status` enum('open','closed') COLLATE latin1_danish_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(20) COLLATE latin1_danish_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE latin1_danish_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE latin1_danish_ci NOT NULL,
  `pinged` text COLLATE latin1_danish_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` text COLLATE latin1_danish_ci NOT NULL,
  `post_parent` bigint(20) NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE latin1_danish_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE latin1_danish_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE latin1_danish_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`)
) ENGINE=MyISAM AUTO_INCREMENT=206 DEFAULT CHARSET=latin1 COLLATE=latin1_danish_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

